UPDATE verses_content SET c2text= '(60-61) ನಾವು ನಿಮ್ಮ ಮಧ್ಯೆ ಮರಣವನ್ನು ನಿರ್ಣಯಿಸಿರುವೆವು. ನಿಮ್ಮ ಬದಲಿಗೆ ನಿಮ್ಮಂತಿರುವವರನ್ನು ತರುವೆವು[[‘ಅನ್ ನುಬದ್ದಿಲ ಅಮ್ಸಾಲಕುಮ್’ಎಂದರೆ ‘ನಿಮ್ಮ ಸ್ವರೂಪವನ್ನು ಬದಲಾಯಿಸಲು’ಎಂದು ಕೆಲವರು ಅರ್ಥ ನೀಡಿದ್ದಾರೆ.]] ಮತ್ತು ನಿಮಗೆ ಅರಿವಿಲ್ಲದ ವಿಧದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಪುನಃ ಸೃಷ್ಟಿಸುವೆವು ಎಂಬ ವಿಷಯದಲ್ಲಿ ನಾವು ಎಂದಿಗೂ ಸೋಲಿಸಲ್ಪಡಲಾರೆವು.' WHERE docid = 5040; 
UPDATE verses_content SET c2text= 'ಮೊದಲ ಬಾರಿ ಸೃಷ್ಟಿಸಲಾಗಿರುವ ಬಗ್ಗೆ ಖಂಡಿತವಾಗಿಯೂ ನೀವು ಅರಿತುಕೊಂಡಿರುವಿರಿ. ಆದರೂ ನೀವೇಕೆ ಚಿಂತಿಸಿ ನೋಡುವುದಿಲ್ಲ.[[ಮೊದಲ ಬಾರಿ ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹುವಿಗೆ ನಿಮ್ಮನ್ನು ಪುನಃ ಸೃಷ್ಟಿಸುವುದು ಕಷ್ಟಕರವಲ್ಲವೆಂದು ನೀವೇಕೆ ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳುವುದಿಲ್ಲ ಎಂದರ್ಥ.]]' WHERE docid = 5041; 
UPDATE verses_content SET c2text= 'ನೀವು ಮಾಡುತ್ತಿರುವ ಕೃಷಿಯ ಬಗ್ಗೆ ನೀವು ಚಿಂತಿಸಿ ನೋಡಿರುವಿರಾ?' WHERE docid = 5042; 
UPDATE verses_content SET c2text= 'ಅದನ್ನು ಬೆಳೆಸುವವರು ನೀವೋ? ಅಥವಾ  ಅದನ್ನು ಬೆಳೆಸುವವರು ನಾವೋ?[[ಬೀಜ ಮೊಳಕೆಯೊಡೆದು ಬೆಳೆದು ಉತ್ಪಾದನಾ ಶಕ್ತಿಯನ್ನು ಪಡೆಯುವುದರಲ್ಲಿ ಕೃಷಿಕನ ಮೂಲಭೂತವಾದ ಯಾವುದೇ ಪಾತ್ರವಿಲ್ಲ. ಸಸ್ಯ ಬೆಳವಣಿಗೆಯು ನಡೆಯುವುದು ಬೀಜದ ಕೋಶಗಳಲ್ಲಿರುವ ಜೀನ್‍ಗಳಲ್ಲಿ ದಾಖಲಿಸಲಾಗಿರುವ ಅನುವಂಶೀಯ ನಿಯಮವ್ಯವಸ್ಥೆಯ ಪ್ರಕಾರವಾಗಿರುತ್ತದೆ. ಈ ವ್ಯವಸ್ಥೆಯು ಸಂಪೂರ್ಣವಾಗಿಯೂ ಅಲ್ಲಾಹುವಿನದ್ದಾಗಿದೆ.]]' WHERE docid = 5043; 
