UPDATE verses_content SET c2text= 'ಆ ಸಮಯದಲ್ಲಿ ನೀವು ನೋಡುತ್ತಲೇ ಇರುವಿರಿ!' WHERE docid = 5063; 
UPDATE verses_content SET c2text= 'ಆ ವ್ಯಕ್ತಿಯ ಪಾಲಿಗೆ ನಿಮಗಿಂತಲೂ ಹೆಚ್ಚು ಹತ್ತಿರದಲ್ಲಿರುವವರು ನಾವಾಗಿರುವೆವು. ಆದರೆ ನೀವು ಕಂಡು ತಿಳಿಯುವುದಿಲ್ಲ.' WHERE docid = 5064; 
UPDATE verses_content SET c2text= '' WHERE docid = 5065; 
UPDATE verses_content SET c2text= '(86-87) ನೀವು (ಅಲ್ಲಾಹುವಿನ ನಿಯಮಕ್ಕೆ) ವಿಧೇಯರಲ್ಲ ದವರಾದರೆ ಅದನ್ನು (ಪ್ರಾಣವನ್ನು) ಮರಳಿ ಪಡೆಯಲು ನಿಮಗೇಕೆ ಸಾಧ್ಯವಾಗುತ್ತಿಲ್ಲ; ನೀವು ಸತ್ಯಸಂಧರಾಗಿದ್ದರೆ.' WHERE docid = 5066; 
UPDATE verses_content SET c2text= 'ಅವನು (ಮೃತಪಟ್ಟವನು) ಸಾಮೀಪ್ಯ ಗಳಿಸಿದವ ರಲ್ಲಿ ಸೇರಿದವನಾಗಿದ್ದರೆ.' WHERE docid = 5067; 
UPDATE verses_content SET c2text= '(ಅವನಿಗೆ) ಸಾಂತ್ವನವೂ, ವಿಶಿಷ್ಟವಾದ ಅನ್ನಾ ಧಾರವೂ[[ರೈಹಾನ್ ಎಂದರೆ ಪರಿಮಳ ಎಂದೂ ಅರ್ಥವಿದೆ.]] ಸುಖಾನುಭೂತಿಯ ಸ್ವರ್ಗೋದ್ಯಾನವೂ ಇರುವುದು.' WHERE docid = 5068; 
UPDATE verses_content SET c2text= 'ಅವನು ಬಲಭಾಗದವರಲ್ಲಿ ಸೇರಿದವನಾಗಿದ್ದರೆ.' WHERE docid = 5069; 
UPDATE verses_content SET c2text= '(ಅವನಿಗಿರುವ ಅಭಿವಂದನೆಯು) ‘ಬಲಭಾಗದ ವರಲ್ಲಿ ಸೇರಿದ ನಿನಗೆ ಶಾಂತಿಯಿರಲಿ’ ಎಂದಾಗಿರುವುದು.' WHERE docid = 5070; 
UPDATE verses_content SET c2text= 'ಆದರೆ ಅವನು ದುರ್ಮಾರ್ಗಿಗಳಾದ ಸತ್ಯನಿಷೇಧಿಗಳಲ್ಲಿ ಸೇರಿದವನಾಗಿದ್ದರೆ.' WHERE docid = 5071; 
UPDATE verses_content SET c2text= 'ಕುದಿಯುವ ನೀರಿನ ಆತಿಥ್ಯವಿರುವುದು.' WHERE docid = 5072; 
UPDATE verses_content SET c2text= 'ನರಕಾಗ್ನಿಯಲ್ಲಿ ಉರಿಯುವಿಕೆಯೂ ಇರುವುದು.' WHERE docid = 5073; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ಇದು ದೃಢವಾದ ಸತ್ಯವಾಗಿದೆ.' WHERE docid = 5074; 
UPDATE verses_content SET c2text= 'ಆದ್ದರಿಂದ ಮಹೋನ್ನತನಾಗಿರುವ ತಮ್ಮ ಪ್ರಭುವಿನ ನಾಮವನ್ನು ತಾವು ಕೊಂಡಾಡಿರಿ.' WHERE docid = 5075; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವು ದೆಲ್ಲವೂ ಅಲ್ಲಾಹುವಿನ ಕೀರ್ತನೆ ಮಾಡುತ್ತಿವೆ. ಅವನು ಪ್ರತಾಪಶಾಲಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5076; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಆಧಿಪತ್ಯವು ಅವನದ್ದಾಗಿದೆ. ಅವನು ಜೀವವನ್ನು ನೀಡುವನು ಮತ್ತು ಮರಣವನ್ನು ನೀಡುವನು. ಅವನು ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ಸಾಮರ್ಥ್ಯವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5077; 
UPDATE verses_content SET c2text= 'ಅವನು ಆದಿಯೂ, ಅಂತ್ಯನೂ, ಪ್ರತ್ಯಕ್ಷನೂ, ಪರೋಕ್ಷನೂ ಆಗಿರುವನು. ಅವನು ಸರ್ವ ವಿಷಯಗಳ ಬಗ್ಗೆಯೂ ಅರಿವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5078; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳನ್ನು ಮತ್ತು ಭೂಮಿಯನ್ನು ಆರು ದಿನಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿದವನು ಅವನಾಗಿರುವನು. ತರುವಾಯ ಅವನು ಸಿಂಹಾಸನಾರೂಢನಾದನು. ಭೂಮಿಯನ್ನು ಪ್ರವೇಶಿಸುವ, ಅದರಿಂದ ಹೊರಹೋಗುವ, ಆಕಾಶದಿಂದ ಇಳಿಯುವ ಮತ್ತು ಅದರೆಡೆಗೆ ಏರಿಹೋಗುವ ಎಲ್ಲವನ್ನೂ ಅವನು ಅರಿಯುವನು. ನೀವೆಲ್ಲೇ ಇದ್ದರೂ ಅವನು ನಿಮ್ಮ ಜೊತೆಗಿರುವನು. ಅಲ್ಲಾಹು ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ವೀಕ್ಷಿಸುವವನಾಗಿರುವನು.' WHERE docid = 5079; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಆಧಿಪತ್ಯವಿರು ವುದು ಅವನಿಗಾಗಿದೆ. ವಿಷಯಗಳೆಲ್ಲವೂ ಮರಳುವುದು ಅಲ್ಲಾಹುವಿನೆಡೆಗಾಗಿದೆ.' WHERE docid = 5080; 
UPDATE verses_content SET c2text= 'ಅವನು ರಾತ್ರಿಯನ್ನು ಹಗಲಿನಲ್ಲಿ ನುಸುಳಿಸುವನು ಮತ್ತು ಹಗಲನ್ನು ರಾತ್ರಿಯಲ್ಲಿ ನುಸುಳಿಸುವನು. ಅವನು ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಅರಿಯುವವನಾಗಿರುವನು.' WHERE docid = 5081; 
UPDATE verses_content SET c2text= 'ನೀವು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮತ್ತು ಅವನ ಸಂದೇಶ ವಾಹಕರಲ್ಲಿ ವಿಶ್ವಾಸವಿಡಿರಿ. ಅವನು ನಿಮ್ಮನ್ನು ಯಾವ ಸಂಪತ್ತಿಗೆ ಉತ್ತರಾಧಿಕಾರಿಯನ್ನಾಗಿ ಮಾಡಿರುವನೋ ಆ ಸಂಪತ್ತಿನಿಂದ ವ್ಯಯಿಸಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ವಿಶ್ವಾಸವಿಟ್ಟವರು ಮತ್ತು ವ್ಯಯಿಸಿದವರು ಯಾರೋ ಅವರಿಗೆ ಮಹಾ ಪ್ರತಿಫಲವಿದೆ.' WHERE docid = 5082; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಡದಿರಲು ನಿಮಗಿರುವ ಸಮರ್ಥನೆಯಾದರೂ ಏನು? ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುವ ಸಲುವಾಗಿ ಸಂದೇಶವಾಹಕರು ನಿಮ್ಮನ್ನು ಆಹ್ವಾನಿಸುತ್ತಿರುವರು. ಅಲ್ಲಾಹು ನಿಮ್ಮಿಂದ ಖಾತ್ರಿಯನ್ನು ಪಡೆದಿರುವನು.[[ಮನುಷ್ಯ ಪ್ರಕೃತಿಯು ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹುವಿನ ಅಸ್ತಿತ್ವದ ಬಗ್ಗೆ ಖಾತ್ರಿಯನ್ನು ನೀಡುತ್ತದೆ ಮತ್ತು ಇಲ್ಲಿ ಉದ್ದೇಶಿಸಲಾಗಿರುವುದು ಆ ಖಾತ್ರಿಯನ್ನಾಗಿದೆಯೆಂದು ಕೆಲವು ವ್ಯಾಖ್ಯಾನಕಾರರು ಹೇಳಿದ್ದಾರೆ. ಅಲ್ಲಾಹು ಆಧ್ಯಾತ್ಮಿಕ ಲೋಕದಲ್ಲಿ ಮನುಷ್ಯರೆಲ್ಲರಿಂದ ಏಕದೇವವಿಶ್ವಾಸದ ಬಗ್ಗೆ ಕರಾರು ಪಡೆದಿರುವುದರ ಬಗ್ಗೆ ಇಲ್ಲಿ ಸೂಚಿಸಲಾಗಿದೆಯೆಂದು ಬೇರೆ ಕೆಲವು ವ್ಯಾಖ್ಯಾನಕಾರರು ಅಭಿಪ್ರಾಯಪಟ್ಟಿದ್ದಾರೆ.]] ನೀವು ವಿಶ್ವಾಸವಿಡುವವರಾಗಿದ್ದರೆ.' WHERE docid = 5083; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮನ್ನು ಅಂಧಕಾರಗಳಿಂದ ಪ್ರಕಾಶದೆಡೆಗೆ ಹೊರತರುವ ಸಲುವಾಗಿ ತನ್ನ ದಾಸನ ಮೇಲೆ ಸ್ಪಷ್ಟವಾದ ದೃಷ್ಟಾಂತಗಳನ್ನು ಇಳಿಸಿಕೊಡುವವನು ಅವನಾಗಿರುವನು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ನಿಮ್ಮೊಂದಿಗೆ ಅತ್ಯಂತ ದಯೆಯುಳ್ಳವನೂ ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರು ವನು.' WHERE docid = 5084; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಉತ್ತರಾಧಿಕಾರವು ಅಲ್ಲಾಹುವಿನದ್ದಾಗಿರುವಾಗ ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ ವ್ಯಯಿಸದಿರಲು ನಿಮಗಿರುವ ಸಮರ್ಥನೆಯಾದರೂ ಏನು? ನಿಮ್ಮ ಪೈಕಿ (ಮಕ್ಕಾ) ವಿಜಯಕ್ಕೆ ಮುಂಚಿತವಾಗಿ ವ್ಯಯಿಸಿದವರು ಮತ್ತು ಯುದ್ಧದಲ್ಲಿ ಪಾಲ್ಗೊಂಡವರು (ಮತ್ತು ಅವರಲ್ಲದವರು) ಸಮಾನರಾಗಲಾರರು. ಅವರು ನಂತರದ ಕಾಲದಲ್ಲಿ ವ್ಯಯಿಸಿದವರಿಗಿಂತಲೂ ಮತ್ತು ಯುದ್ಧದಲ್ಲಿ ಪಾಲ್ಗೊಂಡವರಿಗಿಂತಲೂ ಉನ್ನತ ಪದವಿಗಳಲ್ಲಿರುವರು. ಅಲ್ಲಾಹು ಎಲ್ಲರಿಗೂ ಸ್ವರ್ಗವನ್ನು ವಾಗ್ದಾನ ಮಾಡಿರುವನು.[[ಇಸ್ಲಾಮ್ ತೀವ್ರವಾಗಿ ವಿರೋಧಿಸಲ್ಪಟ್ಟು, ಮುಸ್ಲಿಮರು ಕಠಿಣ ಹಿಂಸೆಗೊಳಗಾಗಿದ್ದ ಆರಂಭಕಾಲದಲ್ಲಿ ವಿಶ್ವಾಸವನ್ನು ಘೋಷಿಸಿ ಆಕ್ರಮಣಕಾರಿಗಳೊಂದಿಗೆ ಹೋರಾಡಿದವರ ಪದವಿ ಅತ್ಯುನ್ನತವಾಗಿದೆ. ತರುವಾಯ ವಿಶ್ವಾಸವಿಟ್ಟು ತ್ಯಾಗದೊಂದಿಗೆ ಯುದ್ಧದಲ್ಲಿ ಪಾಲ್ಗೊಂಡವರಿಗೂ ಅಲ್ಲಾಹು ವಿಶೇಷ ಪ್ರತಿಫಲದ ವಾಗ್ದಾನವಿತ್ತಿದ್ದಾನೆ.]] ಅಲ್ಲಾಹು ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯಾಗಿರುವನು.' WHERE docid = 5085; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹುವಿಗೆ ಒಂದು ಉತ್ತಮ ಸಾಲವನ್ನು ನೀಡುವವನು ಯಾರಿರುವನು? ಹಾಗಾದರೆ ಅವನು ಅದನ್ನು ಅವನಿಗೆ ಇಮ್ಮಡಿಗೊಳಿಸಿ ಕೊಡುವನು. ಅವನಿಗೆ ಗೌರವಾನ್ವಿತ ಪ್ರತಿಫಲವೂ ಇರುವುದು.' WHERE docid = 5086; 
UPDATE verses_content SET c2text= 'ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರ ಪ್ರಕಾಶವು ಅವರ ಮುಂಭಾಗದಿಂದಲೂ, ಬಲಭಾಗ ದಿಂದಲೂ ಚಲಿಸುತ್ತಿರುವುದಾಗಿ ತಾವು ಕಾಣುವ ದಿನ! (ಅಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲಾಗುವುದು): ‘ಇಂದು ನಿಮಗಿರುವ ಶುಭವಾರ್ತೆಯು ಸ್ವರ್ಗೋದ್ಯಾನಗಳಾಗಿವೆ. ಅವುಗಳ ತಳಭಾಗದಿಂದ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವುವು. ನೀವು ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವಿರಿ. ಅದು ಮಹಾ ವಿಜಯವಾಗಿದೆ’.' WHERE docid = 5087; 
UPDATE verses_content SET c2text= 'ಕಪಟವಿಶ್ವಾಸಿಗಳು ಮತ್ತು ಕಪಟವಿಶ್ವಾಸಿನಿಯರು ಸತ್ಯವಿಶ್ವಾಸಿಗಳೊಂದಿಗೆ ಹೇಳುವ ದಿನ: ‘ನಮ್ಮನ್ನು ನೋಡಿರಿ! ನಿಮ್ಮ ಪ್ರಕಾಶದಿಂದ ನಾವೂ ಸ್ವಲ್ಪ ಎರವಲು ಪಡೆ ಯುವೆವು’. (ಆಗ ಅವರೊಂದಿಗೆ) ಹೇಳಲಾಗುವುದು: ‘ನೀವು ನಿಮ್ಮ ಹಿಂಭಾಗಕ್ಕೆ ಮರಳಿ ಹೋಗಿರಿ. ತರು ವಾಯ ಪ್ರಕಾಶವನ್ನು ಅರಸಿರಿ’. ಆಗ ಅವರ ಮಧ್ಯೆ ಒಂದು ಗೋಡೆಯ ಮೂಲಕ ಮರೆಹಾಕಲಾಗುವುದು. ಅದಕ್ಕೊ ಂದು ಬಾಗಿಲಿರುವುದು. ಅದರ ಒಳಭಾಗದಲ್ಲಿ ಕಾರುಣ್ಯ ವಿರುವುದು[[ಯಾಕೆಂದರೆ ಅದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಹತ್ತಿರವಿರುವ ಭಾಗವಾಗಿದೆ.]] ಮತ್ತು ಹೊರಭಾಗದಲ್ಲಿ ಶಿಕ್ಷೆಯಿರುವುದು.' WHERE docid = 5088; 
UPDATE verses_content SET c2text= 'ಅವರು (ಕಪಟವಿಶ್ವಾಸಿಗಳು) ಅವರನ್ನು (ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳನ್ನು) ಕರೆದು ಹೇಳುವರು: ‘ನಾವು ನಿಮ್ಮೊಂ ದಿಗಿರಲಿಲ್ಲವೇ?’ ಅವರು (ಸತ್ಯವಿಶ್ವಾಸಿಗಳು) ಹೇಳು ವರು: ‘ಹೌದು! ಆದರೆ ನೀವು ನಿಮ್ಮನ್ನೇ ಕ್ಷೋಭೆಗೆ ದೂಡಿದಿರಿ. (ಇತರರಿಗೆ ಕೇಡುಂಟಾಗುವುದನ್ನು) ಕಾಯು ವವರಾಗಿದ್ದಿರಿ. (ಧರ್ಮದಲ್ಲಿ) ಸಂದೇಹಪಟ್ಟಿರಿ. ಅಲ್ಲಾಹು ವಿನ ಆಜ್ಞೆ ಬರುವವರೆಗೆ ವ್ಯರ್ಥ ನಿರೀಕ್ಷೆಗಳು ನಿಮ್ಮನ್ನು ವಂಚಿಸಿದವು. ಮಹಾವಂಚಕನಾದ ಸೈತಾನನು ಅಲ್ಲಾಹು ವಿನ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮನ್ನು ವಂಚಿಸಿದನು.' WHERE docid = 5089; 
UPDATE verses_content SET c2text= 'ಆದ್ದರಿಂದ ಇಂದು ನಿಮ್ಮಿಂದ ಅಥವಾ ಸತ್ಯನಿಷೇಧಿಗಳಿಂದ ಯಾವುದೇ ಪ್ರಾಯಶ್ಚಿತ್ತವನ್ನೂ ಸ್ವೀಕರಿಸಲಾಗದು. ನಿಮ್ಮ ವಾಸಸ್ಥಳವು ನರಕಾಗ್ನಿಯಾಗಿದೆ. ಅದೇ ನಿಮ್ಮ ಸಂಬಂಧಿಯಾಗಿದೆ.[[‘ಮೌಲಾಕುಂ’ಎಂದರೆ ನಿಮಗೆ ಅತ್ಯಂತ ಯೋಗ್ಯವಾದ ಅಭಯಸ್ಥಾನ ಎಂದು ಕೆಲವರು ಅರ್ಥ ನೀಡಿದ್ದಾರೆ.]] ತಲುಪಲಿರುವ ಆ ಸ್ಥಳವು ನಿಕೃಷ್ಟವೇ ಆಗಿದೆ!' WHERE docid = 5090; 
UPDATE verses_content SET c2text= 'ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ, ಅವರ ಹೃದಯಗಳು ಅಲ್ಲಾಹು ವಿನ ಸ್ಮರಣೆಗೆ ಮತ್ತು ಅವತೀರ್ಣಗೊಂಡಿರುವ ಸತ್ಯಕ್ಕೆ ಶರಣಾಗಲು ಹಾಗೂ ಮುಂಚೆ ಗ್ರಂಥ ನೀಡಲಾದವರಂತೆ ಆಗದಿರಲು ಸಮಯವಾಗಲಿಲ್ಲವೇ?[[ಓರ್ವ ಪ್ರವಾದಿಯ ಮರಣಾನಂತರ ಕೆಲವು ವರ್ಷಗಳು ಕಳೆದಾಗ ಆ ಪ್ರವಾದಿಯ ಶಿಕ್ಷಣಗಳು ವಿಸ್ಮರಿಸಲಾಗಿ ಧರ್ಮೋಪದೇಶಗಳನ್ನು ಸ್ವೀಕರಿಸಲಾಗದಷ್ಟು ಕಠಿಣ ಮನಸ್ಕರಾಗಿ ಬಿಡುವುದು ಪೂರ್ವಸಮುದಾಯದವರ ರೂಢಿಯಾಗಿತ್ತು. ಮುಸ್ಲಿಮರು ಹಾಗಾಗಕೂಡದೆಂದು ಇಲ್ಲಿ ಉಪದೇಶ ನೀಡಲಾಗಿದೆ.]] ಆ ಗ್ರಂಥದ ವರಿಗೆ ಕಾಲವು ದೀರ್ಘವಾಯಿತು ಮತ್ತು ತನ್ಮೂಲಕ ಅವರ ಹೃದಯಗಳು ಕಠೋರವಾದವು. ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರೂ ಧಿಕ್ಕಾರಿಗಳಾಗಿದ್ದರು.' WHERE docid = 5091; 
UPDATE verses_content SET c2text= 'ಅರಿಯಿರಿ! ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಭೂಮಿಗೆ ಅದು ನಿರ್ಜೀವವಾದ ಬಳಿಕ ಜೀವ ನೀಡುವನು. ನೀವು ಚಿಂತಿಸುವ ಸಲುವಾಗಿ ಖಂಡಿತವಾಗಿಯೂ ನಾವು ನಿಮಗೆ ದೃಷ್ಟಾಂತಗಳನ್ನು ವಿವರಿಸಿಕೊಟ್ಟಿರುವೆವು.' WHERE docid = 5092; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ದಾನಧರ್ಮ ಮಾಡಿದ ಪುರುಷರು ಮತ್ತು ಸ್ತ್ರೀಯರು ಹಾಗೂ ಅಲ್ಲಾಹುವಿಗೆ ಉತ್ತಮ ಸಾಲ ನೀಡಿದವರು ಯಾರೋ ಅವರಿಗೆ ಅದನ್ನು ಇಮ್ಮಡಿಯಾಗಿ ನೀಡಲಾಗುವುದು. ಅವರಿಗೆ ಗೌರವಾನ್ವಿತ ಪ್ರತಿಫಲವಿರುವುದು.' WHERE docid = 5093; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕ ರಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟವರು ಯಾರೋ ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಸತ್ಯಸಂಧರೂ ಸತ್ಯಸಾಕ್ಷಿಗಳೂ ಆಗಿರುವರು. ಅವರಿಗೆ ಅವರ ಪ್ರತಿಫಲ ಮತ್ತು ಅವರ ಪ್ರಕಾಶವಿರುವುದು. ಅವಿಶ್ವಾಸವಿಟ್ಟವರು ಮತ್ತು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸಿದವರಾರೋ ಅವರು ನರಕವಾಸಿಗಳಾಗಿರುವರು.' WHERE docid = 5094; 
UPDATE verses_content SET c2text= 'ಅರಿಯಿರಿ! ಐಹಿಕ ಜೀವನವು ಆಟ, ಮನೋರಂಜನೆ, ಶೃಂಗಾರ, ನೀವು ಪರಸ್ಪರ ಅಹಂಭಾವ ಪಡುವುದು, ಸಂಪತ್ತಿನಲ್ಲಿ ಮತ್ತು ಸಂತತಿಯಲ್ಲಿ ಹಿರಿಮೆ ತೋರಿಸುವುದು ಮಾತ್ರವಾಗಿವೆ. ಒಂದು ಮಳೆಯಂತೆ. ಅದರಿಂದ ಬೆಳೆಯುವ ಸಸ್ಯಗಳು ಕೃಷಿಕರನ್ನು ಅಚ್ಚರಿಪಡಿಸಿದವು. ತರುವಾಯ ಅದು ಒಣಗಿದಾಗ ಅದು ಹಳದಿ ಬಣ್ಣವಾಗಿ ಮಾರ್ಪಡುವುದನ್ನು ತಾವು ಕಾಣುವಿರಿ. ತರುವಾಯ ಅದು ಕಿಲುಬುಗಳಾಗಿ ಬಿಡುತ್ತವೆ. ಆದರೆ ಪರಲೋಕದಲ್ಲಿ (ದುರ್ಜನರಿಗೆ) ಕಠಿಣ ಶಿಕ್ಷೆಯಿದೆ ಮತ್ತು (ಸಜ್ಜನರಿಗೆ) ಅಲ್ಲಾಹುವಿನ ವತಿಯ ಪಾಪಮುಕ್ತಿ ಮತ್ತು ಸಂತೃಪ್ತಿಯಿದೆ. ಐಹಿಕ ಜೀವನವು ವಂಚನಾತ್ಮಕ ಅನುಭೋಗವಲ್ಲದೆ ಬೇರೇನೂ ಅಲ್ಲ.' WHERE docid = 5095; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಪ್ರಭುವಿನ ವತಿಯ ಪಾಪಮುಕ್ತಿಯೆಡೆಗೆ ಮತ್ತು ಸ್ವರ್ಗದೆಡೆಗೆ ಮುಂಚೂಣಿಯಲ್ಲಿ ಬನ್ನಿರಿ. ಅದರ ವಿಸ್ತಾರವು ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ವಿಸ್ತಾರದಷ್ಟಿದೆ. ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟವರಿಗಾಗಿ ಅದನ್ನು ಸಜ್ಜೀಕರಿಸಲಾಗಿದೆ. ಅದು ಅಲ್ಲಾಹುವಿನ ಅನುಗ್ರಹವಾಗಿದೆ. ಅವನಿಚ್ಛಿಸುವವರಿಗೆ ಅವನು ಅದನ್ನು ದಯಪಾಲಿಸುವನು. ಅಲ್ಲಾಹು ಮಹಾ ಅನುಗ್ರಹದ ಒಡೆಯನಾಗಿರುವನು.' WHERE docid = 5096; 
UPDATE verses_content SET c2text= 'ಭೂಮಿಯಲ್ಲಾಗಲಿ ನಿಮ್ಮ ಶರೀರಗಳಲ್ಲಾಗಲಿ ಯಾವುದೇ ವಿಪತ್ತು ಕೂಡ ಬಾಧಿಸಲಾರದು; ಅದನ್ನು ನಾವು ಸೃಷ್ಟಿಸುವುದಕ್ಕೆ ಮುಂಚೆಯೇ ಒಂದು ದಾಖಲೆಯಲ್ಲಿ ಸೇರಿಸಿಕೊಂಡಿರುವ ಹೊರತು. ಖಂಡಿತವಾಗಿಯೂ ಅದು ಅಲ್ಲಾಹುವಿನ ಮಟ್ಟಿಗೆ ಸುಲಭವಾದುದಾಗಿದೆ.' WHERE docid = 5097; 
UPDATE verses_content SET c2text= '(ನಾವು ಹೀಗೆ ಮಾಡಿರುವುದೇಕೆಂದರೆ) ನೀವು ಏನನ್ನು ಕಳಕೊಂಡಿರುವಿರೋ ಅದಕ್ಕಾಗಿ ನೀವು ದುಃಖಿಸದಿರುವ ಸಲುವಾಗಿ ಮತ್ತು ಅವನು ನಿಮಗೆ ದಯಪಾಲಿಸಿರುವುದರಲ್ಲಿ ನೀವು ಅತಿಯಾಗಿ ಹಿಗ್ಗದಿರುವ ಸಲುವಾಗಿ.[[ಅಲ್ಲಾಹುವಿನ ಯುಕ್ತಿ ಮತ್ತು ನಿರ್ಧಾರಕ್ಕೆ ಅನುಸಾರವಾಗಿ ನಷ್ಟ ಮತ್ತು ಹಾನಿ ಉಂಟಾಗುವುದೆಂದು ವಿಶ್ವಾಸವಿಡುವ ವ್ಯಕ್ತಿಗೆ ಸಂಕಷ್ಟದ ಸಮಯದಲ್ಲಿ ತಾಳ್ಮೆ ವಹಿಸಲು ಸಾಧ್ಯವಾಗುತ್ತದೆ. ಯಾವುದೇ ಸಾಧನೆಯೂ ಅಲ್ಲಾಹುವಿನ ದಾನವೇ ಆಗಿದೆಯೆಂದು ವಿಶ್ವಾಸವಿಡುವವನು ತನ್ನ ವ್ಯಕ್ತಿ ಸಾಧನೆಯಿಂದಾಗಿ ಮೈಮರೆತು ಹಿಗ್ಗುವುದಾಗಲಿ ಅಹಂಕಾರ ಪಡುವುದಾಗಲಿ ಮಾಡಲಾರನು.]] ಅಹಂಕಾರಿಯೂ, ದುರಾಭಿಮಾನಿಯೂ ಆಗಿರುವ ಯಾರನ್ನೂ ಅಲ್ಲಾಹು ಇಷ್ಟಪಡಲಾರನು.' WHERE docid = 5098; 
UPDATE verses_content SET c2text= 'ಅಂದರೆ ಜಿಪುಣತನ ತೋರಿಸುವವರನ್ನು ಮತ್ತು ಜಿಪುಣತನ ತೋರಿಸುವಂತೆ ಜನರಿಗೆ ಆದೇಶಿಸುವವರನ್ನು. ಯಾರಾದರೂ ವಿಮುಖರಾಗುವುದಾದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುವನು.' WHERE docid = 5099; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ನಾವು ನಮ್ಮ ಸಂದೇಶವಾಹಕರನ್ನು ಸ್ಪಷ್ಟವಾದ ದೃಷ್ಟಾಂತಗಳೊಂದಿಗೆ ಕಳುಹಿಸಿರುವೆವು. ಜನರು ನ್ಯಾಯಬದ್ಧವಾಗಿ ನೆಲೆಗೊಳ್ಳುವ ಸಲುವಾಗಿ ನಾವು ಅವರೊಂದಿಗೆ ಗ್ರಂಥವನ್ನೂ ತಕ್ಕಡಿಯನ್ನೂ[[ತಕ್ಕಡಿ ಎಂಬ ಪದದ ತಾತ್ಪರ್ಯ ಅಲ್ಲಾಹುವಿನ ಸಮತೋಲನ ನಿಯಮವಾಗಿರಬಹುದು....]] ಇಳಿಸಿಕೊಟ್ಟಿರುವೆವು. ನಾವು ಕಬ್ಬಿಣವನ್ನೂ ಇಳಿಸಿಕೊಟ್ಟಿರುವೆವು.[[ಅಲ್ಲಾಹು ತನ್ನ ಮಹಾ ಸಾಮರ್ಥ್ಯದಿಂದ ಮನುಷ್ಯರಿಗೆ ಸಜ್ಜೀಕರಿಸಿಕೊಟ್ಟ ಅನೇಕ ವಿಷಯಗಳನ್ನು ಸೂಚಿಸುವ ಕಡೆ ಕುರ್‍ಆನ್ ‘ಇಳಿಸಿಕೊಟ್ಟನು’ ಎಂಬ ಪದವನ್ನು ಬಳಸಿದ್ದಾಗಿ ಕಾಣಬಹುದು. ಕಬ್ಬಿಣವು ಮನುಷ್ಯರಿಗೆ ಬಹಳ ಪ್ರಯೋಜನವನ್ನು ನೀಡಿದ ಒಂದು ಲೋಹವಾಗಿದೆ. ಅಸಂಖ್ಯಾತ ಜನರ ಮರಣಕ್ಕೆ ಕಾರಣವಾದುದೂ ಕೂಡ ಕಬ್ಬಿಣದಿಂದ ನಿರ್ಮಿಸಲಾದ ಆಯುಧಗಳಾಗಿವೆ. ಲೋಹಗಳು ಮತ್ತು ಧಾತುಗಳನ್ನು ಹೊಂದಿರುವ ಪದಾರ್ಥವು ಅಲ್ಲಾಹುವಿನ ದಾನವಾಗಿದೆ. ಅದನ್ನು ಮನುಷ್ಯರು ಹೇಗೆ ಬಳಸುತ್ತಾರೆ ಎಂಬುದರಲ್ಲಿ ಅದರ ಲಾಭ ಮತ್ತು ಹಾನಿಗಳು ಅಡಗಿವೆ.]] ಅದರಲ್ಲಿ ಕಠಿಣವಾದ ಹೋರಾಟ ಶಕ್ತಿಯಿದೆ ಮತ್ತು ಜನರಿಗೆ ಪ್ರಯೋಜನಗಳಿವೆ. ಇವೆಲ್ಲವೂ ಅಲ್ಲಾಹುವಿಗೆ ಮತ್ತು ಅವನ ಸಂದೇಶ ವಾಹಕರಿಗೆ ಅಗೋಚರವಾಗಿ ಸಹಾಯ ಮಾಡುವವರು ಯಾರೆಂದು ಅವನು ತಿಳಿಯುವ ಸಲುವಾಗಿದೆ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಬಲಿಷ್ಠನೂ ಪ್ರತಾಪಶಾಲಿಯೂ ಆಗಿರುವನು.' WHERE docid = 5100; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ನಾವು ನೂಹ್ ರನ್ನು ಮತ್ತು ಇಬ್ರಾಹೀಮ್ ರನ್ನು (ಸಂದೇಶವಾಹಕರಾಗಿ) ಕಳುಹಿಸಿದೆವು. ಅವರಿಬ್ಬರ ಸಂತತಿಗಳಲ್ಲಿ ಪ್ರವಾದಿತ್ವವನ್ನು ಮತ್ತು ಗ್ರಂಥವನ್ನು ನಾವು ಮಾಡಿದೆವು. ಅವರ ಪೈಕಿ ಸನ್ಮಾರ್ಗ ಪಡೆದವರಿರುವರು. ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರೂ ದುರ್ಮಾರ್ಗಿಗಳಾಗಿರುವರು.' WHERE docid = 5101; 
UPDATE verses_content SET c2text= 'ತರುವಾಯ ಅವರ ಹಿಂದೆಯೇ ನಾವು ನಮ್ಮ ಸಂದೇಶವಾಹಕರನ್ನು ನಿರಂತರವಾಗಿ ಕಳುಹಿಸಿದೆವು. ಮರ್ಯಮ್ ರ ಮಗನಾದ ಈಸಾರನ್ನೂ ಕಳುಹಿಸಿದೆವು.[[ಈಸಾ (عليه السلام) ರಿಗಿಂತ ಮುಂಚೆ ಕಳುಹಿಸಲಾದ ಪ್ರವಾದಿಗಳನ್ನು ಕ್ರೈಸ್ತರು ಪ್ರವಾದಿಗಳೆಂದೇ ಪರಿಗಣಿಸುತ್ತಾರೆ. ಆದರೆ ಈಸಾ (عليه السلام) ರವರು ಪ್ರವಾದಿಯಲ್ಲ, ದೇವಪುತ್ರರೆಂದು ಅವರು ವಾದಿಸುತ್ತಾರೆ. ಅದನ್ನು ಖಂಡಿಸಿ ಈಸಾ (عليه السلام) ರವರು ಪ್ರವಾದಿ ಪರಂಪರೆಯ ಒಂದು ಕೊಂಡಿಯಾಗಿದ್ದಾರೆಂದು ಈ ಸೂಕ್ತಿಯು ಒತ್ತಿಹೇಳುತ್ತದೆ....]] ನಾವು ಅವರಿಗೆ ಇಂಜೀಲನ್ನು ನೀಡಿದೆವು. ಅವರನ್ನು ಅನುಸರಿಸಿದವರ ಹೃದಯಗಳಲ್ಲಿ ನಾವು ಕೃಪೆ ಮತ್ತು ಕರುಣೆಯನ್ನು ಇಟ್ಟೆವು. ಸನ್ಯಾಸತ್ವವನ್ನು ಸ್ವತಃ ಅವರೇ ಹೊಸದಾಗಿ ನಿರ್ಮಿಸಿದರು. ಅಲ್ಲಾಹುವಿನ ಸಂತೃಪ್ತಿಯನ್ನು ಗಳಿಸಲು ಅವರು (ಅವರು ಹೀಗೆ ಮಾಡಿದರೇ) ವಿನಾ ನಾವು ಅದನ್ನು ಅವರಿಗೆ ನಿಯಮವನ್ನಾಗಿ ಮಾಡಿರಲಿಲ್ಲ.[[ಬ್ರಹ್ಮಚರ್ಯೆ, ವನವಾಸ ಇತ್ಯಾದಿಗಳನ್ನು ಅನೇಕ ಮಂದಿ ಧಾರ್ಮಿಕ ಜೀವನದ ಪರಿಪೂರ್ಣತೆಯನ್ನಾಗಿ ಪರಿಗಣಿಸುತ್ತಾರೆ. ಇಸ್ಲಾಮ್ ಇದನ್ನು ಪ್ರೋತ್ಸಾಹಿಸುವುದಿಲ್ಲ. ಸಾಮಾಜಿಕ ಜೀವಿಯಾಗಿ ಬದುಕಿಕೊಂಡೇ ಸತ್ಯಕ್ಕೆ ಸಾಕ್ಷಿಯಾಗುವುದರಲ್ಲಿ ಮತ್ತು ಪ್ರತಿಕೂಲ ಸನ್ನಿವೇಶಗಳನ್ನು ಹಿಮ್ಮೆಟ್ಟಿಸುವುದರಲ್ಲಿ ಇಸ್ಲಾಮ್ ಮಹತ್ವವನ್ನು ಕಾಣುತ್ತದೆ.]] ತರುವಾಯ ಅವರು ಅದನ್ನು ಪಾಲಿಸಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ಪಾಲಿಸಲೂ ಇಲ್ಲ. ಆಗ ಅವರ ಪೈಕಿ ವಿಶ್ವಾಸವಿಟ್ಟವರಿಗೆ ನಾವು ಅವರ ಪ್ರತಿಫಲವನ್ನು ನೀಡಿದೆವು. ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರೂ ದುರ್ಮಾರ್ಗಿಗಳಾಗಿದ್ದರು.' WHERE docid = 5102; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರಲ್ಲಿ ವಿಶ್ವಾಸವಿಡಿರಿ. ಹಾಗಾದರೆ ಅವನು ತನ್ನ ಕಾರುಣ್ಯದಿಂದ ನಿಮಗೆ ಎರಡು ಪಾಲನ್ನು ದಯಪಾಲಿಸುವನು.[[ಪೂರ್ವ ಗ್ರಂಥಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದ ಯಹೂದರು ಮತ್ತು ಕ್ರೈಸ್ತರ ಪೈಕಿ ಇಸ್ಲಾಮ್ ಸ್ವೀಕರಿಸಿದವರ ಬಗ್ಗೆ ಈ ಸೂಕ್ತಿಯಲ್ಲಿ ಪ್ರಸ್ತಾಪಿಸಲಾಗಿದೆ ಮತ್ತು ಇಸ್ಲಾಮಿಗಿಂತ ಮುಂಚೆ ತೌರಾತ್ ಅಥವಾ ಇಂಜೀಲ್‍ಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟ ಕಾರಣ ಅಲ್ಲಾಹುವಿನ ಕಾರುಣ್ಯದ ಒಂದು ಪಾಲು ಮತ್ತು ತರುವಾಯ ಕುರ್‍ಆನಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟ ಕಾರಣ ಎರಡನೇ ಪಾಲು ಅವರಿಗಿದೆ ಎಂದು ವ್ಯಾಖ್ಯಾನಕಾರರು ಹೇಳಿದ್ದಾರೆ.]] ಅವನು ನಿಮಗೆ ಒಂದು ಪ್ರಕಾಶವನ್ನು ಮಾಡಿಕೊಡುವನು. ಅದರ ಮೂಲಕ ನಿಮಗೆ (ಸರಿಯಾದ ಮಾರ್ಗದಲ್ಲಿ) ನಡೆಯಬಹುದು. ಅವನು ನಿಮಗೆ ಕ್ಷಮಿಸುವನು. ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವನು.' WHERE docid = 5103; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹುವಿನ ಅನುಗ್ರಹದಿಂದ ಏನನ್ನೂ ಸ್ವಾಧೀನದಲ್ಲಿಟ್ಟುಕೊಳ್ಳಲು ತಮಗೆ ಸಾಧ್ಯವಾಗದು ಹಾಗೂ ಖಂಡಿತವಾಗಿಯೂ ಅನುಗ್ರಹವಿರುವುದು ಅಲ್ಲಾಹುವಿನ ಕೈಯಲ್ಲಾಗಿದೆ ಮತ್ತು ಅವನು ಇಚ್ಛಿಸುವವರಿಗೆ ಅವನು ಅದನ್ನು ನೀಡುವನು ಎಂದು ಗ್ರಂಥದವರು ಅರಿತುಕೊಳ್ಳುವ ಸಲುವಾಗಿ. ಅಲ್ಲಾಹು ಮಹಾ ಅನುಗ್ರಹದ ಒಡೆಯನಾಗಿ ರುವನು.' WHERE docid = 5104; 
UPDATE verses_content SET c2text= '(ಓ ಪ್ರವಾದಿಯವರೇ!) ತನ್ನ ಪತಿಯ ವಿಷಯದಲ್ಲಿ ತಮ್ಮೊಂದಿಗೆ ತರ್ಕಿಸುತ್ತಿರುವ ಮತ್ತು ಅಲ್ಲಾಹು ವಿನೊಂದಿಗೆ ದುಃಖವನ್ನು ತೋಡಿಕೊಳ್ಳುತ್ತಿರುವ ಒಬ್ಬಾಕೆಯ ಮಾತನ್ನು ಅಲ್ಲಾಹು ಆಲಿಸಿರುವನು. ಅಲ್ಲಾಹು ನಿಮ್ಮಿಬ್ಬರ ಸಂಭಾಷಣೆಯನ್ನು ಆಲಿಸುತ್ತಿರುವನು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಎಲ್ಲವನ್ನು ಆಲಿಸುವವನೂ ವೀಕ್ಷಿಸುವವನೂ ಆಗಿರುವನು.' WHERE docid = 5105; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಪೈಕಿ ತಮ್ಮ ಪತ್ನಿಯರನ್ನು ತಾಯಂದಿರಿಗೆ ಸಮಾನರೆಂದು ಘೋಷಿಸುವವರು[[‘ಝಿಹಾರ್’ಎಂಬುದು ಪ್ರವಾದಿರವರ ಕಾಲದಲ್ಲಿ ಅರಬರ ಮಧ್ಯೆ ರೂಢಿಯಲ್ಲಿದ್ದ ಒಂದು ಕೆಟ್ಟ ಸಂಪ್ರದಾಯವಾಗಿತ್ತು. ‘ಇನ್ನು ಮೇಲೆ ನಿನ್ನೊಂದಿಗಿರುವ ಲೈಂಗಿಕ ಸಂಬಂಧವು ನನ್ನ ತಾಯಿಯೊಂದಿಗಿರುವ ಲೈಂಗಿಕ ಸಂಬಂಧದಂತೆ ನನಗೆ ನಿಷಿದ್ಧವಾಗಿದೆ’ ಎಂದು ಘೋಷಿಸಿ ಪತ್ನಿಯೊಂದಿಗಿನ ಲೈಂಗಿಕ ಸಂಬಂಧವನ್ನು ಕಡಿಯುವ ಸಂಪ್ರದಾಯಕ್ಕೆ ‘ಝಿಹಾರ್’ ಎನ್ನಲಾಗುತ್ತದೆ.]] (ತಪ್ಪು ಮಾಡುತ್ತಿ ರುವರು). ಅವರು (ಪತ್ನಿಯರು) ಅವರ ತಾಯಂದಿ ರಾಗಲಾರರು. ಅವರ ತಾಯಂದಿರು ಅವರನ್ನು ಹೆತ್ತವರೇ ವಿನಾ ಇನ್ನಾರೂ ಅಲ್ಲ. ಖಂಡಿತವಾಗಿಯೂ ಅವರು ನಿಷಿದ್ಧ ಮಾತನ್ನು ಮತ್ತು ಅಸತ್ಯವನ್ನು ಹೇಳುತ್ತಿರುವರು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಪಾಪಮುಕ್ತಿ ನೀಡುವವನೂ ಆಗಿರುವನು.' WHERE docid = 5106; 
UPDATE verses_content SET c2text= 'ತಮ್ಮ ಪತ್ನಿಯರನ್ನು ತಾಯಂದಿರಿಗೆ ಸಮಾನ ರೆಂದು ಘೋಷಿಸಿ, ತರುವಾಯ ತಾವು ಹೇಳಿರುವ ಮಾತಿನಿಂದ ಹಿಂದೆ ಸರಿಯುವವರು ಯಾರೋ ಅವರು ಪರಸ್ಪರ ಸ್ಪರ್ಶಿಸುವ ಮುಂಚೆ ಒಬ್ಬ ಗುಲಾಮನನ್ನು ವಿಮೋಚನೆಗೊಳಿಸಬೇಕಾಗಿದೆ.[[ಇದು ‘ಝಿಹಾರ್’ನ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗಿದೆ.]] ಅದು ನಿಮಗೆ ನೀಡಲಾ ಗುವ ಉಪದೇಶವಾಗಿದೆ. ಅಲ್ಲಾಹು ನೀವು ಮಾಡುತ್ತಿರು ವುದರ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮಜ್ಞಾನವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5107; 
UPDATE verses_content SET c2text= 'ಇನ್ನು ಯಾರಿಗಾದರೂ (ಗುಲಾಮನು) ಸಿಗದಿ ದ್ದರೆ, ಅವರು ಪರಸ್ಪರ ಸ್ಪರ್ಶಿಸುವುದಕ್ಕೆ ಮುಂಚೆ ನಿರಂತರವಾಗಿ ಎರಡು ತಿಂಗಳ ಕಾಲ ಉಪವಾಸ ಆಚರಿ ಸಬೇಕಾಗಿದೆ. ಯಾರಿಗಾದರೂ (ಅದೂ) ಸಾಧ್ಯವಿಲ್ಲ ವೆಂದಾದರೆ ಅವರು ಅರುವತ್ತು ಬಡವರಿಗೆ ಆಹಾರ ನೀಡಬೇಕಾಗಿದೆ. ಅದು ನೀವು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುವ ಸಲುವಾಗಿದೆ. ಇವು ಅಲ್ಲಾಹುವಿನ ವ್ಯಾಪ್ತಿಗಳಾಗಿವೆ. ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ಯಾತನಾಮಯವಾದ ಶಿಕ್ಷೆಯಿದೆ.' WHERE docid = 5108; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹುವನ್ನು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರನ್ನು ವಿರೋಧಿಸುತ್ತಿರುವವರು ಅವರ ಪೂರ್ವಿಕರು ಹೀನಾಯಿಸಲಾದಂತೆ ಹೀನಾಯಿಸ ಲಾಗುವರು. ನಾವು ಸುಸ್ಪಷ್ಟವಾದ ಅನೇಕ ಪುರಾವೆಗಳನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿರುವೆವು. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅಪಮಾನಕರವಾದ ಶಿಕ್ಷೆಯಿದೆ.' WHERE docid = 5109; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ಅವರೆಲ್ಲರನ್ನೂ ಪುನರುತ್ಥಾನಗೊಳಿಸಿ, ತರುವಾಯ ಅವರು ಮಾಡಿದ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಅವರಿಗೆ ತಿಳಿಸಿಕೊಡುವ ದಿನ! ಅಲ್ಲಾಹು ಅದನ್ನು ಎಣಿಸಿರುವನು. ಆದರೆ ಅವರು ಅದನ್ನು ಮರೆತುಬಿಟ್ಟಿರುವರು. ಅಲ್ಲಾಹು ಎಲ್ಲ ವಿಷಯಗಳ ಮೇಲೂ ಸಾಕ್ಷಿಯಾಗಿರುವನು.' WHERE docid = 5110; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವುದನ್ನು ಅಲ್ಲಾಹು ಅರಿಯುತ್ತಿರುವನೆಂಬುದನ್ನು ತಾವು ಕಾಣುವು ದಿಲ್ಲವೇ? ಮೂವರು ಪರಸ್ಪರ ನಡೆಸುವ ಯಾವುದೇ ಗುಪ್ತ ಸಂಭಾಷಣೆಯು ನಾಲ್ಕನೆಯವನಾಗಿ ಅವನು (ಅಲ್ಲಾಹು) ಅವರೊಂದಿಗೆ ಇರದ ಹೊರತು ಜರುಗಲಾರದು. ಐವರು ನಡೆಸುವ ಸಂಭಾಷಣೆಯಾಗಿದ್ದಲ್ಲಿ ಆರನೆಯವನಾಗಿ ಅವನು ಅವರೊಂದಿಗೆ ಇರದ ಹೊರತು. ಅದಕ್ಕಿಂತ ಕಡಿಮೆ ಜನರಾಗಲಿ, ಅಧಿಕ ಜನರಾಗಲಿ ನಡೆಸುವ (ಸಂಭಾಷಣೆ)ಯಾಗಿದ್ದಲ್ಲಿ ಅವರು ಎಲ್ಲೇ ಇದ್ದರೂ ಅವನು ಅವರೊಂದಿಗೆ ಇರದ ಹೊರತು ಜರುಗಲಾರದು. ತರುವಾಯ ಪುನರುತ್ಥಾನ ದಿನದಂದು ಅವರು ಮಾಡಿರು ವುದರ ಬಗ್ಗೆ ಅವನು ಅವರಿಗೆ ತಿಳಿಸಿಕೊಡುವನು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಎಲ್ಲ ವಿಷಯಗಳ ಬಗ್ಗೆಯೂ ಅರಿವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5111; 
UPDATE verses_content SET c2text= 'ಗುಪ್ತ ಸಂಭಾಷಣೆಯಲ್ಲಿ ತೊಡಗುವುದರಿಂದ ವಿರೋಧಿಸಲಾಗಿರುವವರನ್ನು ತಾವು ಕಂಡಿಲ್ಲವೇ? ತರುವಾಯ ಅವರನ್ನು ವಿರೋಧಿಸಲಾದೆಡೆಗೆ ಅವರು ಮರಳುವರು.[[ಮುಸ್ಲಿಮರೊಂದಿಗೆ ಬೆರೆತು ಜೀವಿಸುತ್ತಿದ್ದ ಕಪಟವಿಶ್ವಾಸಿಗಳು ಮುಸ್ಲಿಮರ ವಿರುದ್ಧ ಹಲವು ರೀತಿಯ ಪಿತೂರಿಗಳನ್ನು ಹೆಣೆಯತೊಡಗಿದರು. ಈ ಮಾಹಿತಿ ಸಿಕ್ಕಿದಾಗ ಪ್ರತ್ಯೇಕ ಗುಂಪುಗಳಾಗಿ ಗುಪ್ತ ಸಂಭಾಷಣೆಯಲ್ಲಿ ತೊಡಗುವುದನ್ನು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ನಿಷೇಧಿಸಿದರು. ಆದರೆ ಕಪಟವಿಶ್ವಾಸಿಗಳು ಈ ನಿಷೇಧವನ್ನು ಉಲ್ಲಂಘಿಸಿ ಪುನಃ ಗುಪ್ತ ಸಂಭಾಷಣೆಗಳಲ್ಲಿ ನಿರತರಾಗ ತೊಡಗಿದರು. ಅದರ ಬಗ್ಗೆ ಇಲ್ಲಿ ಸೂಚಿಸಲಾಗಿದೆ....]] ಪಾಪಕ್ಕೂ, ಅತಿಕ್ರಮಗಳಿಗೂ, ಸಂದೇಶ ವಾಹಕರನ್ನು ಧಿಕ್ಕರಿಸುವುದಕ್ಕೂ ಅವರು ಪರಸ್ಪರ ಗುಪ್ತ ವಾಗಿ ಉಪದೇಶ ಮಾಡುತ್ತಿರುವರು. ಅವರು ತಮ್ಮ ಬಳಿ ಬಂದರೆ ಅಲ್ಲಾಹು ತಮ್ಮನ್ನು ಅಭಿವಂದಿಸದ ರೀತಿಯಲ್ಲಿ ಅವರು ತಮ್ಮನ್ನು ಅಭಿವಂದಿಸುವರು.[[‘ಅಸ್ಸಲಾಮು ಅಲೈಕುಮ್’ (ನಿಮ್ಮ ಮೇಲೆ ಶಾಂತಿಯಿರಲಿ) ಎಂಬ ಅಭಿವಂದನೆಯನ್ನು ಅಲ್ಲಾಹು ಅಂಗೀಕರಿಸಿದ್ದಾನೆ. ಆದರೆ ಸಲಾಮ್ ಎಂಬ ಪದಕ್ಕೆ ಬದಲಾಗಿ ‘ಅಸ್ಸಾಮು ಅಲೈಕುಮ್’ (ನಿಮ್ಮ ಮೇಲೆ ಮರಣವುಂಟಾಗಲಿ) ಎಂದು ಯಹೂದರು ಹೇಳುತ್ತಿದ್ದರು.]] ‘ನಾವು ಹೀಗೆ ಹೇಳುವ ನಿಮಿತ್ತ ಅಲ್ಲಾಹು ನಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುವುದಿಲ್ಲ ವೇಕೆ?’ ಎಂದು ಅವರು ಪರಸ್ಪರ ಕೇಳುವರು. ಅವರಿಗೆ ನರಕಾಗ್ನಿಯೇ ಸಾಕು. ಅವರು ಅದರಲ್ಲಿ ಉರಿಯುವರು. ಆ ಪರ್ಯವಸಾನವು ನಿಕೃಷ್ಟವೇ ಆಗಿದೆ.' WHERE docid = 5112; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಗುಪ್ತ ಸಂಭಾಷಣೆ ಯಲ್ಲಿ ತೊಡಗುವುದಾದರೆ ಪಾಪಕ್ಕೂ, ಅತಿಕ್ರಮಕ್ಕೂ ಹಾಗೂ ಸಂದೇಶವಾಹಕರನ್ನು ಧಿಕ್ಕರಿಸಲಿಕ್ಕೂ ಗುಪ್ತ ಸಂಭಾಷಣೆಯನ್ನು ನಡೆಸದಿರಿ. ಪುಣ್ಯ ಮತ್ತು ಭಯಭಕ್ತಿಯ ವಿಷಯದಲ್ಲಿ ನೀವು ಗುಪ್ತ ಉಪದೇಶ ಮಾಡಿರಿ. ಯಾವ ಅಲ್ಲಾಹುವಿನೆಡೆಗೆ ನಿಮ್ಮನ್ನು ಒಟ್ಟುಗೂಡಿಸಲಾಗುವುದೋ ಅವನನ್ನು ನೀವು ಭಯಪಡಿರಿ.' WHERE docid = 5113; 
UPDATE verses_content SET c2text= 'ಆ ಗುಪ್ತ ಸಂಭಾಷಣೆಯು ಕೇವಲ ಸೈತಾನ ನಿಂದಿರುವುದಾಗಿದೆ. ಅದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ದುಃಖಕ್ಕೀಡು ಮಾಡುವ ಸಲುವಾಗಿದೆ. ಆದರೆ ಅಲ್ಲಾಹು ವಿನ ಅನುಮತಿಯ ವಿನಾ ಅದು ಅವರಿಗೆ ಯಾವುದೇ ಹಾನಿಯನ್ನೂ ಮಾಡದು. ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹುವಿನ ಮೇಲೆ ಭರವಸೆಯಿಡಲಿ.' WHERE docid = 5114; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ‘ಸಭೆಗಳಲ್ಲಿ ಎಡೆ ಮಾಡಿಕೊಡಿರಿ’ ಎಂದು ನಿಮ್ಮೊಂದಿಗೆ ಹೇಳಲಾದರೆ ಎಡೆ ಮಾಡಿಕೊಡಿರಿ. ಹಾಗಾದರೆ ಅಲ್ಲಾಹು ನಿಮಗೆ ಎಡೆ ಮಾಡಿಕೊಡುವನು. ‘ಎದ್ದು ಹೋಗಿರಿ’ ಎಂದು ನಿಮ್ಮೊಂದಿಗೆ ಹೇಳಲಾದರೆ ಎದ್ದು ಹೋಗಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ವಿಶ್ವಾಸವಿಟ್ಟವರನ್ನು ಮತ್ತು ಜ್ಞಾನ ನೀಡಲಾದವರನ್ನು ಅಲ್ಲಾಹು ಹಲವು ಪದವಿಗಳಿಗೆ ಏರಿಸುವನು. ಅಲ್ಲಾಹು ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿಯುವ ವನಾಗಿರುವನು.' WHERE docid = 5115; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಸಂದೇಶವಾಹಕ ರೊಂದಿಗೆ ಗುಪ್ತ ಸಂಭಾಷಣೆಯಲ್ಲಿ ತೊಡಗುವುದಾದರೆ ನಿಮ್ಮ ಗುಪ್ತ ಸಂಭಾಷಣೆಗೆ ಮುಂಚಿತವಾಗಿ ನೀವು ಏನಾದರೂ ದಾನಧರ್ಮ ಮಾಡಿರಿ.[[ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರ ಸಭೆಯು ಬಹಿರಂಗ ಸಭೆಯಾಗಿರುತ್ತಿತ್ತು. ಸಹಾಬಿಗಳ ಪೈಕಿ ಯಾರಿಗೂ ಅಲ್ಲಿ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರೊಂದಿಗೆ ವಿಷಯಗಳನ್ನು ಕೇಳಿ ಮನದಟ್ಟು ಮಾಡಿಕೊಳ್ಳಬಹುದಿತ್ತು. ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ನೀಡುವ ಉತ್ತರವನ್ನು ಆಲಿಸಿ ಗ್ರಹಿಸಲು ಎಲ್ಲರಿಗೂ ಸಾಧ್ಯವಾಗುವುದರಿಂದ ಈ ಬಹಿರಂಗ ಸಭೆಯು ಧರ್ಮ ಬೋಧನೆಗೆ ಉತ್ತಮ ವೇದಿಕೆಯಾಗಿತ್ತು. ಆದರೆ ಇದಕ್ಕೆ ವ್ಯತಿರಿಕ್ತವಾಗಿ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯರೊಂದಿಗೆ ವಿವಿಧ ವಿಷಯಗಳ ಬಗ್ಗೆ ಗುಪ್ತ ಸಂಭಾಷಣೆ ನಡೆಸುವುದು ಅನಿವಾರ್ಯವಾಗಿರುವ ಕೆಲವರಿದ್ದರು. ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರೊಂದಿಗೆ ಗುಪ್ತ ಸಂಭಾಷಣೆ ನಡೆಸಲು ಅವಕಾಶ ದೊರೆಯುವುದು ಹೆಮ್ಮೆಯ ವಿಷಯವಾಗಿ ಪರಿಗಣಿಸುವ ಧನಿಕರಾಗಿದ್ದರು. ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರ ಸಹಚರರಲ್ಲಿ ಹೆಚ್ಚಿನವರೂ ಬಡವರಾಗಿದ್ದರು. ಅವರೊಂದಿಗೆ ಸಮಯ ಕಳೆಯಲು ಅಥವಾ ಅವರಿಗೆ ಮಾರ್ಗನಿರ್ದೇಶನ ನೀಡಲು ಸಾಧ್ಯವಾಗದ ವಿಧದಲ್ಲಿ ಖಾಸಗಿ ಸಂಭಾಷಣೆಗಳು ಅಧಿಕವಾಗ ತೊಡಗಿದಾಗ ಅಂತಹ ಸಂಭಾಷಣೆಗಳನ್ನು ನಿಯಂತ್ರಿಸುವ ನಿಟ್ಟಿನಲ್ಲಿ ಮತ್ತು ಖಾಸಗಿ ಸಂಭಾಷಣೆ ಮಾಡುವ ಅವಕಾಶವನ್ನು ಅರಸುತ್ತಾ ಬರುವವರ ಪ್ರಾಮಾಣಿಕತೆಯನ್ನು ಪರೀಕ್ಷಿಸುವುದಕ್ಕಾಗಿ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರೊಂದಿಗೆ ಗುಪ್ತ ಸಂಭಾ ಷಣೆಯಲ್ಲಿ ತೊಡಗಲು ಇಚ್ಛಿಸುವವರು ಅದಕ್ಕಿಂತ ಮುಂಚೆ ಇಸ್ಲಾಮ್ ಮತ್ತು ಮುಸ್ಲಿಮರಿಗೆ ಏನಾದರೂ ದಾನ ಮಾಡಬೇಕೆಂದು ಶಾಸನ ಮಾಡಲಾಯಿತು. ಇದು ಧನಿಕರಿಗೆ ಮಾತ್ರವಾಗಿದ್ದ ಶಾಸನವಾಗಿತ್ತೆಂದು ಹೆಚ್ಚಿನ ವ್ಯಾಖ್ಯಾನಕಾರರೂ ಹೇಳಿದ್ದಾರೆ.]] ಅದು ನಿಮಗೆ ಉತ್ತಮವಾಗಿದೆ ಮತ್ತು ಹೆಚ್ಚು ಪರಿಶುದ್ಧವಾಗಿದೆ. ಇನ್ನು ನಿಮಗೆ (ದಾನಧರ್ಮ ಮಾಡಲು) ಏನೂ ಸಿಗದಿದ್ದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವನು.' WHERE docid = 5116; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಗುಪ್ತ ಸಂಭಾಷಣೆಗೆ ಮುಂಚಿತವಾಗಿ ನೀವು ದಾನಧರ್ಮ ಮಾಡುವುದರ ಬಗ್ಗೆ ನೀವು ಭಯಪಟ್ಟಿರು ವಿರಾ?[[ಜಿಪುಣರಾದ ಹಲವರು ತರುವಾಯ ಖಾಸಗಿ ಸಂಭಾಷಣೆಗಾಗಿ ಬರುವುದನ್ನು ನಿಲ್ಲಿಸಿದರು....]] ಆದರೆ ನೀವು ಅದನ್ನು ಮಾಡದಿರುವುದರಿಂದ ಮತ್ತು ಅಲ್ಲಾಹು ನಿಮಗೆ ಕ್ಷಮಿಸಿರುವುದರಿಂದ ನೀವು ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ, ಝಕಾತ್ ನೀಡಿರಿ ಹಾಗೂ ಅಲ್ಲಾಹುವನ್ನೂ ಅವನ ಸಂದೇಶವಾಹಕರನ್ನೂ ಅನುಸರಿಸಿರಿ.[[ಮಹತ್ವವಿರುವುದು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯರೊಂದಿಗೆ ಗುಪ್ತ ಸಂಭಾಷಣೆಯನ್ನು ನಡೆಸಲು ಅವಕಾಶ ಸಿಗುವುದರಲ್ಲಿ ಅಲ್ಲ; ಬದಲಾಗಿ ಮಹತ್ವವಿರುವುದು ಇಸ್ಲಾಮಿನ ವಿಧಿನಿಷೇಧಗಳನ್ನು ಪಾಲಿಸುವುದರಲ್ಲಾಗಿದೆ.]] ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿಯುವವನಾಗಿರುವನು.' WHERE docid = 5117; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ಕೋಪಿಸಿಕೊಂಡಿರುವ ಒಂದು ಜನತೆ ಯೊಂದಿಗೆ (ಯಹೂದರೊಂದಿಗೆ) ಮೈತ್ರಿ ಮಾಡಿಕೊಂಡ ವರನ್ನು (ಕಪಟವಿಶ್ವಾಸಿಗಳನ್ನು) ತಾವು ಕಂಡಿಲ್ಲವೇ? ಅವರು ನಿಮ್ಮ ಪೈಕಿ ಸೇರಿದವರಲ್ಲ. ಅವರ (ಯಹೂದರ) ಪೈಕಿ ಸೇರಿದವರೂ ಅಲ್ಲ. ಅವರು ಅರಿತಿರುತ್ತಲೇ ಸುಳ್ಳು ಆಣೆ ಹಾಕುತ್ತಿರುವರು.' WHERE docid = 5118; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ಅವರಿಗೆ ಕಠೋರ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧ ಗೊಳಿಸಿರುವನು. ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಮಾಡುತ್ತಿ ರುವುದು ಅತಿ ನಿಕೃಷ್ಟವಾದುದಾಗಿದೆ!' WHERE docid = 5119; 
UPDATE verses_content SET c2text= 'ಅವರು ತಮ್ಮ ಶಪಥಗಳನ್ನು ಒಂದು ಗುರಾಣಿ ಯನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿರುವರು. ತರುವಾಯ ಅವರು ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಿಂದ (ಜನರನ್ನು) ತಡೆದರು. ಆದ್ದರಿಂದ ಅವರಿಗೆ ಅಪಮಾನಕರವಾದ ಶಿಕ್ಷೆಯಿದೆ.' WHERE docid = 5120; 
UPDATE verses_content SET c2text= 'ಅವರ ಸಂಪತ್ತುಗಳಾಗಲಿ, ಸಂತತಿಗಳಾಗಲಿ ಅಲ್ಲಾಹುವಿನ ಬಳಿ ಅವರಿಗೆ ಯಾವುದೇ ಪ್ರಯೋಜನವನ್ನೂ ಮಾಡದು. ಅವರು ನರಕವಾಸಿಗಳಾಗಿರುವರು. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವರು.' WHERE docid = 5121; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ಅವರೆಲ್ಲರನ್ನೂ ಪುನರುತ್ಥಾನಗೊಳಿ ಸುವ ದಿನ! ನಿಮ್ಮೊಂದಿಗೆ ಅವರು ಶಪಥ ಮಾಡುವಂತೆ ಅವನೊಂದಿಗೂ ಅವರು ಶಪಥ ಮಾಡುವರು. ತಾವು (ಈ ಸುಳ್ಳು ಆಣೆಯ ಮೂಲಕ) ಏನನ್ನೋ ಗಳಿಸಿರುವುದಾಗಿ ಅವರು ಭಾವಿಸುವರು. ಅರಿಯಿರಿ! ಖಂಡಿತವಾಗಿಯೂ ಸುಳ್ಳು ಹೇಳುವವರು ಅವರೇ ಆಗಿರುವರು.' WHERE docid = 5122; 
UPDATE verses_content SET c2text= 'ಸೈತಾನನು ಅವರನ್ನು ಸ್ವಾಧೀನಪಡಿಸಿರುವನು. ಆದ್ದರಿಂದ ಅವರು ಅಲ್ಲಾಹುವಿನ ಸ್ಮರಣೆಯನ್ನು ಮರೆಯು ವಂತೆ ಮಾಡಿದನು. ಅವರು ಸೈತಾನನ ಪಕ್ಷದವರಾಗಿ ರುವರು. ಅರಿಯಿರಿ! ಖಂಡಿತವಾಗಿಯೂ ಸೈತಾನನ ಪಕ್ಷದವರು ನಷ್ಟ ಹೊಂದಿದವರಾಗಿರುವರು.' WHERE docid = 5123; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹುವನ್ನು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರನ್ನು ವಿರೋಧಿಸುವವರು ಯಾರೋ ಅವರು ಅತ್ಯಂತ ಅಪಮಾನಿತ ಸ್ಥಿತಿಯಲ್ಲಿರುವರು.' WHERE docid = 5124; 
UPDATE verses_content SET c2text= '‘ಖಂಡಿತವಾಗಿಯೂ ನಾನು ಮತ್ತು ನನ್ನ ಸಂದೇಶವಾಹಕರು ವಿಜಯ ಗಳಿಸುವವರಾಗಿರುವೆವು’ ಎಂದು ಅಲ್ಲಾಹು ದಾಖಲಿಸಿರುವನು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಬಲಿಷ್ಠನೂ ಪ್ರತಾಪಶಾಲಿಯೂ ಆಗಿರುವನು.' WHERE docid = 5125; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮತ್ತು ಅಂತ್ಯದಿನದಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುವ ಒಂದು ಜನತೆಯು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರನ್ನು ವಿರೋಧಿಸುವವರೊಂದಿಗೆ ಮೈತ್ರಿ ಸ್ಥಾಪಿಸುವುದನ್ನು ತಾವು ಕಾಣಲಾರಿರಿ. ಅವರು (ವಿರೋಧಿಗಳು) ಅವರ ತಂದೆಯಂದಿರೋ, ಪುತ್ರರೋ, ಸಹೋದರರೋ, ಸಂಬಂಧಿಕರೋ ಆಗಿದ್ದರೂ ಸಹ. ಅಂತಹವರ[[ಅಂದರೆ ಅಲ್ಲಾಹು ಮತ್ತು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರ ಶತ್ರುಗಳೊಂದಿಗೆ ಮೈತ್ರಿ ಮಾಡಿಕೊಳ್ಳದ ನೈಜ ವಿಶ್ವಾಸಿಗಳು.]] ಹೃದಯಗಳಲ್ಲಿ ಅವನು ವಿಶ್ವಾಸವನ್ನು ದಾಖಲಿಸಿಟ್ಟಿರುವನು ಮತ್ತು ತನ್ನ ವತಿಯ ಆತ್ಮ ಚೈತನ್ಯದಿಂದ ಅವನು ಅವರಿಗೆ ಬೆಂಬಲವನ್ನು ನೀಡಿರುವನು. ಅವನು ಅವರನ್ನು ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿಗೆ ಪ್ರವೇಶ ಮಾಡಿಸುವನು. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವರು. ಅಲ್ಲಾಹು ಅವರ ಬಗ್ಗೆ ಸಂತೃಪ್ತನಾಗಿರುವನು ಮತ್ತು ಅವರು ಅವನ ಬಗ್ಗೆ ಸಂತೃಪ್ತರಾಗಿರುವರು. ಅವರು ಅಲ್ಲಾಹುವಿನ ಪಕ್ಷದ ವರಾಗಿರುವರು. ಅರಿಯಿರಿ! ಖಂಡಿತವಾಗಿಯೂ ವಿಜಯ ಗಳಿಸುವವರು ಅಲ್ಲಾಹುವಿನ ಪಕ್ಷದವರೇ ಆಗಿರುವರು.' WHERE docid = 5126; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವುದೆಲ್ಲವೂ ಅಲ್ಲಾಹುವಿನ ಕೀರ್ತನೆ ಮಾಡಿವೆ. ಅವನು ಪ್ರತಾಪ ಶಾಲಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5127; 
UPDATE verses_content SET c2text= 'ಗ್ರಂಥದವರಲ್ಲಿ ಸೇರಿದ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಪ್ರಥಮ ಗಡಿಪಾರಿನಲ್ಲಿ ಅವರ ಮನೆಗಳಿಂದ ಹೊರಗಟ್ಟಿ ದವನು ಅವನಾಗಿರುವನು.[[ಮದೀನಕ್ಕೆ ತೆರಳಿದೊಡನೆ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಅಲ್ಲಿನ ಯಹೂದರೊಂದಿಗೆ ಶಾಂತಿ ಒಪ್ಪಂದವನ್ನು ಮಾಡಿಕೊಂಡಿದ್ದರು. ಆ ಒಪ್ಪಂದದ ಪ್ರಕಾರ ಮುಸ್ಲಿಮರು ಮತ್ತು ಯಹೂದರು ಪರಸ್ಪರ ದಾಳಿ ಮಾಡುವುದಾಗಲಿ, ದಾಳಿ ಮಾಡಲು ಸಹಾಯ ಮಾಡುವುದಾಗಲಿ ಕೂಡದು. ಆದರೆ ಯಹೂದರು ಹಲವಾರು ಬಾರಿ ಈ ಒಪ್ಪಂದಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಇಸ್ಲಾಮಿನ ಶತ್ರುಗಳೊಂದಿಗೆ ಗೂಡಾಲೋಚನೆ ಮಾಡಿಕೊಂಡಿದ್ದರು. ತನ್ನಿಮಿತ್ತ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಮತ್ತು ಸಹಾಬಿಗಳು ಅಲ್ಲಾಹುವಿನ ಅಪ್ಪಣೆ ಪ್ರಕಾರ ಯಹೂದ ಜನಾಂಗವೊಂದನ್ನು ಮದೀನದಿಂದ ಗಡಿಪಾರು ಮಾಡಿದರು. ಮದೀನ ತೊರೆದ ಯಹೂದರು ಖೈಬರ್‍ನಲ್ಲಿ ಆಶ್ರಯಪಡೆದರು. ಆದರೆ ಖೈಬರ್‍ನಿಂದಲೂ ಅವರು ಕಿರುಕುಳ ನೀಡಲಾರಂಭಿಸಿದಾಗ ಅವರನ್ನು ಅಲ್ಲಿಂದಲೂ ಗಡಿಪಾರು ಮಾಡುವುದು ಮುಸ್ಲಿಮರಿಗೆ ಅನಿವಾರ್ಯವಾಯಿತು....]] ಅವರು ಹೊರಬರುವರೆಂದು ನೀವು ಭಾವಿಸಿರಲಿಲ್ಲ. ತಮ್ಮ ಕೋಟೆಗಳು ಅಲ್ಲಾಹುವಿನಿಂದ  ತಮ್ಮನ್ನು ಪ್ರತಿರೋಧಿಸಬಹುದೆಂದು ಅವರು ಭಾವಿಸಿದ್ದರು. ಆದರೆ ಅವರು ಊಹಿಸಿರದ ರೀತಿಯಲ್ಲಿ ಅಲ್ಲಾಹು ಅವರ ಬಳಿಗೆ ಬಂದನು ಮತ್ತು ಅವರ ಹೃದಯಗಳಲ್ಲಿ ಭೀತಿಯನ್ನು ಬಿತ್ತಿದನು. ಅವರು ಸ್ವತಃ ತಮ್ಮದೇ ಕೈಗಳಿಂದಲೂ, ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಕೈಗಳಿಂದಲೂ ತಮ್ಮ ಮನೆಗಳನ್ನು ದ್ವಂಸ ಮಾಡಿದರು.[[ಯಹೂದರು ಮದೀನವನ್ನು ಬಿಟ್ಟು ಹೋಗುವಾಗ ಅವರ ಸರಕು ಸಾಮಗ್ರಿಗಳನ್ನು ಜೊತೆಗೊಯ್ಯಲು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಅನುಮತಿ ನೀಡಿದ್ದರು. ಆದ್ದರಿಂದ ಅವರು ತಮ್ಮ ಮನೆಗಳನ್ನು ಒಡೆದು ತೆಗೆಯಬಹುದಾದ ಮರದ ತುಂಡುಗಳನ್ನು ತೆಗೆದೊಯ್ದರು. ಆದರೆ ಒಯ್ಯಲು ಸಾಧ್ಯವಾಗದ್ದನ್ನು ಮುಸ್ಲಿಮರು ಬಳಸಬಾರದೆಂದು ಅವುಗಳನ್ನು ನಾಶ ಮಾಡಿದರು. ಮನೆಗಳ ಉಳಿದ ಭಾಗಗಳನ್ನು ವಿಶ್ವಾಸಿಗಳು ನೆಲಸಮಗೊಳಿಸಿದರು.]] ಆದ್ದರಿಂದ ಓ ಕಣ್ಣುಳ್ಳವರೇ! ನೀವು ನೀತಿಪಾಠವನ್ನು ಕಲಿಯಿರಿ!' WHERE docid = 5128; 
UPDATE verses_content SET c2text= 'ಊರು ಬಿಟ್ಟು ಹೋಗುವುದನ್ನು ಅಲ್ಲಾಹು ಅವರ ಮೇಲೆ ವಿಧಿಸದಿರುತ್ತಿದ್ದರೆ ಅವನು ಅವರನ್ನು ಇಹಲೋಕದಲ್ಲಿ ಶಿಕ್ಷಿಸುತ್ತಿದ್ದನು.[[ಮದೀನದ ಮುಸ್ಲಿಮರನ್ನು ನಿರ್ನಾಮ ಮಾಡಲು ನಿರಂತರ ಪಿತೂರಿಗಳನ್ನು ರೂಪಿಸುತ್ತಿದ್ದ ಯಹೂದ ಜನಾಂಗವನ್ನು ನಿರ್ನಾಮ ಮಾಡಲು ನಿರ್ಧರಿಸಿದರೂ ಅದರಲ್ಲಿ ಅತಿಶಯೋಕ್ತಿಯಿಲ್ಲ. ಆದರೆ ಅಲ್ಲಾಹುವಿನ ತೀರ್ಮಾನ ಪ್ರಕಾರ ಅವರನ್ನು ಕೇವಲ ಗಡಿಪಾರು ಮಾಡಲಾಯಿತು.]] ಪರಲೋಕದಲ್ಲಿ ಅವರಿಗೆ ನರಕಾಗ್ನಿಯ ಶಿಕ್ಷೆಯಿದೆ.' WHERE docid = 5129; 
UPDATE verses_content SET c2text= 'ಅದೇಕೆಂದರೆ ಅವರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರನ್ನು ವಿರೋಧಿಸಿದರು ಎಂಬುದರಿಂದಾ ಗಿದೆ. ಯಾರಾದರೂ ಅಲ್ಲಾಹುವನ್ನು ವಿರೋಧಿಸಿದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸುವವ ನಾಗಿರುವನು.' WHERE docid = 5130; 
UPDATE verses_content SET c2text= 'ನೀವು ಯಾವುದಾದರೂ ಖರ್ಜೂರದ ಮರವನ್ನು ಕಡಿಯುವುದಾಗಲಿ ಅಥವಾ ಅವುಗಳನ್ನು ಅವುಗಳ ಬುಡ ಗಳ ಮೇಲೆ ನಿಲ್ಲಲು ಬಿಟ್ಟುಬಿಡುವುದಾಗಲಿ ಮಾಡುವು ದಾದರೆ ಅದು ಅಲ್ಲಾಹುವಿನ ಅಪ್ಪಣೆ ಪ್ರಕಾರವಾಗಿದೆ ಮತ್ತು ಧಿಕ್ಕಾರಿಗಳನ್ನು ಅಪಮಾನಗೊಳಿಸುವ ಸಲುವಾಗಿದೆ.' WHERE docid = 5131; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ಅವರಿಂದ (ಯಹೂದರಿಂದ) ಅವನ ಸಂದೇಶವಾಹಕರಿಗೆ ಏನನ್ನು ವಶಪಡಿಸಿಕೊಟ್ಟನೋ ಅದಕ್ಕಾಗಿ ನೀವು ಕುದುರೆಗಳನ್ನೋ ಒಂಟೆಗಳನ್ನೋ ಓಡಿಸಬೇಕಾಗಿ ಬರಲಿಲ್ಲ.[[ಅಂದರೆ ಯಾವುದೇ ಹೋರಾಟವನ್ನೂ ನಡೆಸದೆ ಯಹೂದರ ವಾಸಸ್ಥಳಗಳು ಮುಸ್ಲಿಮರ ಪಾಲಾದವು...]] ಆದರೆ ಅಲ್ಲಾಹು ಅವನಿಚ್ಛಿ ಸುವವರ ಮೇಲೆ ಅವನ ಸಂದೇಶವಾಹಕರಿಗೆ ಅಧಿಕಾರ ವನ್ನು ಕೊಡುವನು.[[ಯುದ್ಧವಿಲ್ಲದೆ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯರಿಗೆ ಮತ್ತು ಅನುಯಾಯಿಗಳಿಗೆ ಅಧೀನವಾಗುವ ಸಂಪತ್ತು ಸಮರಾರ್ಜಿತ ಸೊತ್ತಿನಂತೆ ಪಾಲು ಮಾಡಬೇಕಾದುದಲ್ಲ. ಬದಲಾಗಿ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ತಮ್ಮ ವಿವೇಚನೆಗೆ ಅನುಸಾರವಾಗಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಪೈಕಿ ಅರ್ಹರಿಗೆ ಅದನ್ನು ಪಾಲು ಮಾಡಬೇಕಾಗಿದೆ. ಈ ಆಯತ್ತಿನ ತಳಹದಿಯಲ್ಲಿ ಹಲವಾರು ವ್ಯಾಖ್ಯಾನಕಾರರು ಈ ಅಭಿಪ್ರಾಯ ಹೊಂದಿದ್ದಾರೆ.]] ಅಲ್ಲಾಹು ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5132; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ಅವನ ಸಂದೇಶವಾಹಕರಿಗೆ ವಿವಿಧ ಪ್ರದೇಶದವರಿಂದ ಏನನ್ನು ವಶಪಡಿಸಿಕೊಟ್ಟಿರುವನೋ ಅದು ಅಲ್ಲಾಹುವಿಗೂ, ಸಂದೇಶವಾಹಕರಿಗೂ, ನಿಕಟ ಸಂಬಂಧಿಕರಿಗೂ, ಅನಾಥರಿಗೂ, ನಿರ್ಗತಿಕರಿಗೂ, ದಾರಿಹೋಕರಿಗೂ ಇರುವುದಾಗಿದೆ. ಅದು (ಸಂಪತ್ತು) ನಿಮ್ಮ ಪೈಕಿ ಧನಿಕರ ಮಧ್ಯೆ ಮಾತ್ರ ವಿನಿಮಯವಾಗುವ ವಸ್ತುವಾಗದಿರುವ ಸಲುವಾಗಿ. ಸಂದೇಶವಾಹಕರು ನಿಮಗೆ ಏನನ್ನು ನೀಡುವರೋ ಅದನ್ನು ಸ್ವೀಕರಿಸಿರಿ ಮತ್ತು ಅವರು ನಿಮ್ಮನ್ನು ಯಾವುದರಿಂದ ವಿರೋಧಿಸುವರೋ ಅದರಿಂದ ದೂರಸರಿಯಿರಿ. ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸುವವ ನಾಗಿರುವನು.' WHERE docid = 5133; 
UPDATE verses_content SET c2text= '(ಆ ಸಂಪತ್ತು) ತಮ್ಮ ಮನೆಗಳಿಂದ ಮತ್ತು ಸಂಪತ್ತುಗಳಿಂದ ಹೊರಗಟ್ಟಲಾದ ಮುಹಾಜಿರ್ ಗಳಾದ ಬಡವರಿಗೆ (ಇರುವುದಾಗಿದೆ).  ಅವರು ಅಲ್ಲಾಹುವಿನ ವತಿಯ ಅನುಗ್ರಹವನ್ನು ಮತ್ತು ಸಂತೃಪ್ತಿಯನ್ನು ಅರಸುತ್ತಿ ರುವರು ಮತ್ತು ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ಸಂದೇಶ ವಾಹಕರಿಗೆ ಸಹಾಯ ಮಾಡುತ್ತಿರುವರು. ಸತ್ಯಸಂಧರು ಅವರೇ ಆಗಿರುವರು.' WHERE docid = 5134; 
UPDATE verses_content SET c2text= 'ಅವರ (ಮುಹಾಜಿರ್ ಗಳ) ಆಗಮನಕ್ಕೆ ಮುಂಚಿತ ವಾಗಿ ವಾಸಸ್ಥಳವನ್ನು ಮತ್ತು ವಿಶ್ವಾಸವನ್ನು ಸಿದ್ಧಗೊಳಿಸಿ ದವರಿಗೂ (ಅನ್ಸಾರ್ ಗಳಿಗೂ). ಸ್ವಂತ ಊರನ್ನು ಬಿಟ್ಟು ತಮ್ಮ ಬಳಿ ಬಂದವರನ್ನು ಅವರು ಪ್ರೀತಿಸುತ್ತಿರುವರು. ಅವರಿಗೆ (ಮುಹಾಜಿರ್ ಗಳಿಗೆ) ನೀಡಲಾದ ಸಂಪತ್ತಿನ ಬಗ್ಗೆ ಅವರು (ಅನ್ಸಾರ್ ಗಳು) ತಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಯಾವುದೇ ಅಗತ್ಯವನ್ನೂ ಕಾಣುವುದಿಲ್ಲ. ತಮಗೆ ಬಡತನವಿದ್ದರೂ ಸಹ ಸ್ವತಃ ತಮಗಿಂತಲೂ ಹೆಚ್ಚಾಗಿ ಅವರು ಇತರರಿಗೆ ಪ್ರಾಮುಖ್ಯತೆಯನ್ನು ನೀಡುವರು.[[ಮುಹಾಜಿರ್‍ಗಳೆಂದರೆ ಮಕ್ಕಾದಿಂದ ಮದೀನಕ್ಕೆ ಆಶ್ರಯ ಕೋರಿ ಬಂದ ಮುಸ್ಲಿಮರಾಗಿದ್ದಾರೆ. ಅವರಿಗೆ ಸಂರಕ್ಷಣೆ ನೀಡಿ ಸಹಾಯ ಮಾಡಿದ ಮದೀನ ನಿವಾಸಿಗಳಾದ ಮುಸ್ಲಿಮರನ್ನು ಅನ್ಸಾರ್ ಎನ್ನಲಾಗುತ್ತದೆ. ಮದೀನದಿಂದ ಬನೂ ನಝೀರ್ ಎಂಬ ಯಹೂದ ಜನಾಂಗವನ್ನು ಹೊರಗಟ್ಟಿದಾಗ ಅವರು ಬಿಟ್ಟು ಹೋದ ಸಂಪತ್ತುಗಳಲ್ಲಿ ಹೆಚ್ಚಿನವುಗಳನ್ನೂ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಮುಹಾಜಿರ್‍ಗಳಿಗೆ ನೀಡಿದ್ದರು. ಯಾಕೆಂದರೆ ಅವರು ಹೆಚ್ಚು ಬಡವರಾಗಿದ್ದರು. ಪ್ರಸ್ತುತ ಸಂಪತ್ತು ದೊರೆಯದ ಅನ್ಸಾರ್‍ಗಳಿಗೆ ಆ ವಿಷಯದಲ್ಲಿ ಯಾವುದೇ ವಿಧದ ಅತೃಪ್ತಿಯೂ ಇರಲಿಲ್ಲವೆಂದು ಈ ಸೂಕ್ತಿಯು ಸ್ಪಷ್ಟಪಡಿಸುತ್ತದೆ.]] ತನ್ನ ಮನಸ್ಸಿನ ಜಿಪುಣತನದಿಂದ ಯಾರನ್ನು ಕಾಪಾಡಲಾಗುತ್ತದೋ ಅವರೇ ಯಶಸ್ವಿಯಾದವರು.' WHERE docid = 5135; 
UPDATE verses_content SET c2text= 'ಅವರ ಬಳಿಕ ಬಂದವರಿಗೂ. ಅವರು ಹೇಳುವರು: ‘ನಮ್ಮ ಪ್ರಭೂ! ನಮಗೂ, ವಿಶ್ವಾಸದಲ್ಲಿ ನಮಗಿಂತ ಮುಂಚೆ ಗತಿಸಿಹೋಗಿರುವ ನಮ್ಮ ಸಹೋದರರಿಗೂ ಕ್ಷಮಿಸು. ನಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೊಂದಿಗೆ ಯಾವುದೇ ವಿದ್ವೇಷವನ್ನೂ ಉಂಟುಮಾಡದಿರು. ನಮ್ಮ ಪ್ರಭೂ! ಖಂಡಿತವಾಗಿಯೂ ನೀನು ಅತ್ಯಧಿಕ ದಯೆಯುಳ್ಳ ವನೂ, ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವೆ’.' WHERE docid = 5136; 
UPDATE verses_content SET c2text= 'ಆ ಕಪಟ ವಿಶ್ವಾಸಿಗಳೆಡೆಗೆ ತಾವು ನೋಡಲಿಲ್ಲವೇ? ಗ್ರಂಥದವರಲ್ಲಿ ಸೇರಿದ ಸತ್ಯನಿಷೇಧಿಗಳಾದ ತಮ್ಮ ಸಹೋದರರೊಂದಿಗೆ ಅವರು ಹೇಳುವರು: ‘ನಿಮ್ಮನ್ನು ಹೊರಗಟ್ಟಲಾದರೆ ಖಂಡಿತವಾಗಿಯೂ ನಾವು ನಿಮ್ಮೊಂದಿಗೆ ಹೊರಹೋಗುವೆವು. ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಾವು ಯಾರನ್ನೂ ಎಂದಿಗೂ ಅನುಸರಿಸಲಾರೆವು. ನಿಮ್ಮ ವಿರುದ್ಧ ಯುದ್ಧ ನಡೆದರೆ ಖಂಡಿತವಾಗಿಯೂ ನಾವು ನಿಮಗೆ ಸಹಾಯ ಮಾಡುವೆವು’. ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಸುಳ್ಳು ನುಡಿಯುವವರಾಗಿರುವರು ಎಂಬುದಕ್ಕೆ ಅಲ್ಲಾಹು ಸಾಕ್ಷ್ಯವಹಿಸುವನು.' WHERE docid = 5137; 
UPDATE verses_content SET c2text= 'ಅವರನ್ನು (ಯಹೂದರನ್ನು) ಹೊರಗಟ್ಟಲಾದರೆ ಇವರು (ಕಪಟವಿಶ್ವಾಸಿಗಳು) ಅವರೊಂದಿಗೆ ಖಂಡಿತವಾಗಿಯೂ ಹೊರಹೋಗಲಾರರು. ಅವರು ಒಂದು ಯುದ್ಧವನ್ನು ಎದುರಿಸಬೇಕಾಗಿ ಬಂದರೆ ಇವರು ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಲಾರರು. ಇವರು ಅವರಿಗೆ ಸಹಾಯ ಮಾಡುವುದಾದರೂ ಇವರು ಖಂಡಿತವಾಗಿಯೂ ವಿಮುಖರಾಗಿ ಓಡುವರು. ತರುವಾಯ ಅವರಿಗೆ ಯಾವುದೇ ಸಹಾಯವೂ ಲಭ್ಯವಾಗದು.' WHERE docid = 5138; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ಅವರ ಹೃದಯಗಳಲ್ಲಿ ಅಲ್ಲಾಹುವಿಗಿಂತಲೂ ಹೆಚ್ಚು ನಿಮ್ಮ ಬಗ್ಗೆ ಭಯವಿದೆ. ಇದೇಕೆಂದರೆ ಅವರು ವಾಸ್ತವತೆಯನ್ನು ಗ್ರಹಿಸದ ಒಂದು ಜನತೆಯಾಗಿರುವರು ಎಂಬುದರಿಂದಾಗಿದೆ.' WHERE docid = 5139; 
UPDATE verses_content SET c2text= 'ಕೋಟೆಗಳಿಂದಾವೃತವಾದ ಪಟ್ಟಣಗಳಿಂದ ಅಥವಾ ಗೋಡೆಗಳ ಹಿಂಭಾಗದಿಂದಲೇ ಹೊರತು ಅವರು ಒಟ್ಟಾಗಿ ನಿಮ್ಮೊಂದಿಗೆ ಯುದ್ಧ ಮಾಡಲಾರರು. ಅವರಲ್ಲಿರುವ ಒಳಜಗಳವು ಅತ್ಯುಗ್ರವಾಗಿದೆ. ಅವರು ಒಗ್ಗಟ್ಟಾಗಿರುವರೆಂದು ತಾವು ಭಾವಿಸುವಿರಿ. ಆದರೆ ಅವರ ಹೃದಯಗಳು ಭಿನ್ನಮತ ಹೊಂದಿವೆ. ಅದು ಅವರು ಚಿಂತಿಸಿ ಗ್ರಹಿಸದ ಒಂದು ಜನತೆಯಾಗಿರುವರು ಎಂಬುದರಿಂದಾಗಿದೆ.' WHERE docid = 5140; 
UPDATE verses_content SET c2text= 'ಇತ್ತೀಚೆಗಷ್ಟೇ ಗತಿಸಿಹೋದ ಅವರಿಗಿಂತ ಮುಂಚಿನವರ ಸ್ಥಿತಿಯಂತೆ! ಅವರು ಮಾಡಿರುವುದರ ದುಷ್ಫಲವನ್ನು ಅವರು ಆಸ್ವಾದಿಸಿದರು. ಅವರಿಗೆ ಯಾತನಾಮಯವಾದ ಶಿಕ್ಷೆಯೂ ಇದೆ.' WHERE docid = 5141; 
UPDATE verses_content SET c2text= 'ಸೈತಾನನ ಸ್ಥಿತಿಯಂತೆ! ‘ಅವಿಶ್ವಾಸವಿಡು’ ಎಂದು ಅವನು ಮನುಷ್ಯನೊಂದಿಗೆ ಹೇಳಿದ ಸಂದರ್ಭ. ಅವನು (ಮನುಷ್ಯನು) ಅವಿಶ್ವಾಸಿಯಾದಾಗ ಅವನು (ಸೈತಾನನು) ಹೇಳಿದನು: ‘ಖಂಡಿತವಾಗಿಯೂ ನಾನು ನಿನ್ನಿಂದ ವಿಮುಕ್ತನಾಗಿರುವೆನು. ಖಂಡಿತವಾಗಿಯೂ ನಾನು ಸರ್ವಲೋಕಗಳ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡುತ್ತಿರುವೆನು’.' WHERE docid = 5142; 
UPDATE verses_content SET c2text= 'ಹಾಗೆ ಅವರಿಬ್ಬರ ಪರ್ಯವಸಾನವು ಅವರಿಬ್ಬರೂ ನರಕಾಗ್ನಿಯಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವರು ಎಂಬುದಾಗಿದೆ. ಅದು ಅಕ್ರಮಿಗಳಿಗಿರುವ ಪ್ರತಿಫಲವಾಗಿದೆ.' WHERE docid = 5143; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ. ಪ್ರತಿಯೊಬ್ಬ ವ್ಯಕ್ತಿಯೂ ತಾನು ನಾಳೆಗಾಗಿ ಏನನ್ನು ಪೂರ್ವಭಾವಿಯಾಗಿ ಮಾಡಿಟ್ಟಿರುವೆನು ಎಂಬುದನ್ನು ನೋಡಲಿ. ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮ ಜ್ಞಾನವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5144; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹುವನ್ನು ಮರೆತುಬಿಟ್ಟ ಒಂದು ಜನತೆ ಯಂತೆ ನೀವಾಗದಿರಿ. ಆದ್ದರಿಂದ ಅವರು ಸ್ವತಃ ಅವರನ್ನೇ ಮರೆಯುವಂತೆ ಅಲ್ಲಾಹು ಮಾಡಿದನು. ಧಿಕ್ಕಾರಿಗಳು ಅವರೇ ಆಗಿರುವರು.' WHERE docid = 5145; 
UPDATE verses_content SET c2text= 'ನರಕವಾಸಿಗಳು ಮತ್ತು ಸ್ವರ್ಗವಾಸಿಗಳು ಸಮಾನರಾಗಲಾರರು. ಸ್ವರ್ಗವಾಸಿಗಳು ವಿಜಯಗಳಿಸಿ ದವರಾಗಿರುವರು.' WHERE docid = 5146; 
UPDATE verses_content SET c2text= 'ನಾವು ಈ ಕುರ್ ಆನ್ ನನ್ನು ಒಂದು ಪರ್ವತದ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸಿರುತ್ತಿದ್ದರೆ ಅದು (ಪರ್ವತ) ವಿನಮ್ರಗೊಳ್ಳುವುದನ್ನು ಮತ್ತು ಅಲ್ಲಾಹುವಿನ ಭಯದಿಂದ ಒಡೆದು ಹೋಳಾಗುವುದನ್ನು ತಾವು ಕಾಣುತ್ತಿದ್ದಿರಿ.[[ಅಲ್ಲಾಹುವಿನ ವಚನವು ನೇರವಾಗಿ (ಯಾವ ಮಾಧ್ಯಮವೂ ಇಲ್ಲದೆ) ಅವತೀರ್ಣಗೊಳ್ಳುವುದು ನಮಗೆ ಊಹಿಸಲು ಸಾಧ್ಯವಾಗದ ವಿಧ ಅತಿಭಯಾನಕವಾಗಿರುವುದೆಂದು ಇದರಿಂದ ಗ್ರಹಿಸಬಹುದು. ಅಲ್ಲಾಹು ಒಂದು ಪರ್ವತದ ಮುಂದೆ ಪ್ರತ್ಯಕ್ಷವಾದಾಗ ಅದು ನುಚ್ಚುನೂರಾದ ವಿಷಯವನ್ನು 7:143ರಲ್ಲಿ ಪ್ರಸ್ತಾಪಿಸಲಾಗಿದೆ.]] ನಾವು ಜನರಿಗಾಗಿ ಈ ಉದಾಹರಣೆಗಳನ್ನು ವಿವರಿಸಿಕೊಡುತ್ತಿರುವೆವು. ಅವರು ಚಿಂತಿಸುವ ಸಲುವಾಗಿ.' WHERE docid = 5147; 
UPDATE verses_content SET c2text= 'ಅವನಾಗಿರುವನು ಅಲ್ಲಾಹು! ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯರಿಲ್ಲ. ಅವನು ಅಗೋಚರವಾಗಿರುವುದನ್ನು ಮತ್ತು ಗೋಚರವಾಗಿರುವುದನ್ನು ಅರಿಯುವವನಾಗಿರುವನು. ಅವನು ಪರಮ ದಯಾಮಯನೂ ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುವನು.' WHERE docid = 5148; 
UPDATE verses_content SET c2text= 'ಅವನಾಗಿರುವನು ಅಲ್ಲಾಹು! ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯರಿಲ್ಲ. ಅವನು ಸಾಮ್ರಾಟನೂ, ಪರಮ ಪಾವನನೂ, ಶಾಂತಿ ನೀಡುವವನೂ, ಅಭಯ ನೀಡುವವನೂ, ಮೇಲ್ನೋಟ ವಹಿಸುವವನೂ, ಪ್ರತಾಪಶಾಲಿಯೂ, ಸರ್ವಾಧಿಕಾರಿಯೂ ಮತ್ತು ಮಹತ್ವವುಳ್ಳವನೂ ಆಗಿರುವನು. ಅವರು ಸಹಭಾಗಿತ್ವ ಮಾಡುವುದರಿಂದ ಅಲ್ಲಾಹು ಪರಮ ಪಾವನನಾಗಿರುವನು.' WHERE docid = 5149; 
UPDATE verses_content SET c2text= 'ಅವನಾಗಿರುವನು ಅಲ್ಲಾಹು! ಅವನು ಸೃಷ್ಟಿ ಕರ್ತನೂ, ನಿರ್ಮಾತೃನೂ, ರೂಪ ನೀಡುವವನೂ ಆಗಿರುವನು. ಅವನಿಗೆ ಅತ್ಯುತ್ತಮವಾದ ನಾಮಗಳಿವೆ. ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವುದೆಲ್ಲವೂ ಅವನ ಮಹಾತ್ಮೆಯನ್ನು ಕೊಂಡಾಡುತ್ತಿವೆ. ಅವನು ಪ್ರತಾಪ ಶಾಲಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5150; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನನ್ನ ಶತ್ರುವೂ, ನಿಮ್ಮ ಶತ್ರುವೂ ಆಗಿರುವ ಜನರೊಂದಿಗೆ ಮೈತ್ರಿ ಮಾಡಿಕೊಂಡು ನೀವು ಅವರನ್ನು ಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳದಿರಿ. ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿರುವ ಸತ್ಯವನ್ನು ಅವರು ನಿಷೇಧಿಸಿರುವರು. ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಾಗಿರುವ ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ವಿಶ್ವಾಸ ವಿಡುತ್ತಿರುವಿರಿ ಎಂಬುದರಿಂದಾಗಿ ಅವರು ಸಂದೇಶ ವಾಹಕರನ್ನೂ, ನಿಮ್ಮನ್ನೂ ಊರಿನಿಂದ ಹೊರಗಟ್ಟುತ್ತಿರು ವರು. ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡಲು ಮತ್ತು ನನ್ನ ಸಂತೃಪ್ತಿಯನ್ನು ಅರಸಲು ನೀವು ಹೊರಟಿರುವುದಾದರೆ (ಅವರೊಂದಿಗೆ ಮೈತ್ರಿ ಮಾಡದಿರಿ). ನೀವು ಅವರೊಂದಿಗೆ ಗುಪ್ತವಾಗಿ ಮೈತ್ರಿ ಮಾಡುತ್ತಿರುವಿರಿ. ನೀವು ಮರೆಮಾಚುವುದನ್ನೂ, ಬಹಿರಂಗ ಪಡಿಸುವುದನ್ನೂ ನಾನು ಚೆನ್ನಾಗಿ ಅರಿಯುತ್ತಿರುವೆನು. ನಿಮ್ಮ ಪೈಕಿ ಯಾರಾದರೂ ಹಾಗೆ ಮಾಡಿದರೆ ಅವನು ನೇರ ಮಾರ್ಗದಿಂದ ಪಥಭ್ರಷ್ಟಗೊಂಡಿರುವನು.' WHERE docid = 5151; 
UPDATE verses_content SET c2text= 'ಅವರು ನಿಮ್ಮನ್ನು ಸಂಧಿಸುವುದಾದರೆ ಅವರು ನಿಮ್ಮ ಶತ್ರುಗಳಾಗಿರುವರು. ಅವರು ಕೆಟ್ಟ ಉದ್ದೇಶದೊಂದಿಗೆ ನಿಮ್ಮ ವಿರುದ್ಧ ತಮ್ಮ ಕೈಗಳನ್ನು ಮತ್ತು ನಾಲಗೆಗಳನ್ನು ಚಾಚುತ್ತಿರುವರು ಮತ್ತು ‘ನೀವು ಅವಿಶ್ವಾಸಿಗಳಾಗುತ್ತಿದ್ದರೆ’ ಎಂದು ಅವರು ಆಶಿಸುವರು.' WHERE docid = 5152; 
UPDATE verses_content SET c2text= 'ಪುನರುತ್ಥಾನ ದಿನದಂದು ನಿಮ್ಮ ರಕ್ತ ಸಂಬಂಧ ಗಳಾಗಲಿ, ನಿಮ್ಮ ಸಂತತಿಗಳಾಗಲಿ ನಿಮ್ಮ ಪ್ರಯೋಜನಕ್ಕೆ ಬರದು. ಅವನು ನಿಮ್ಮನ್ನು ಪರಸ್ಪರ ಬೇರ್ಪಡಿಸು ವನು.[[ನಿಮ್ಮ ರಕ್ತ ಸಂಬಂಧಗಳಾಗಲಿ, ನಿಮ್ಮ ಸಂತತಿಗಳಾಗಲಿ ನಿಮಗೆ ಪ್ರಯೋಜನ ನೀಡದು. ಪುನರುತ್ಥಾನ ದಿನದಂದು ಅಲ್ಲಾಹು ನಿಮ್ಮ ಮಧ್ಯೆ ತೀರ್ಪು ನೀಡುವನು -ಹೀಗೂ ಈ ಸೂಕ್ತಿಯನ್ನು ಅರ್ಥೈಸಿಕೊಳ್ಳಬಹುದು.]] ಅಲ್ಲಾಹು ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ವೀಕ್ಷಿಸುವವನಾಗಿರುವನು.' WHERE docid = 5153; 
UPDATE verses_content SET c2text= 'ನಿಮಗೆ ಇಬ್ರಾಹೀಮ್ ರಲ್ಲಿ ಮತ್ತು ಅವರ ಜೊತೆಗಿದ್ದವರಲ್ಲಿ ಒಂದು ಉತ್ತಮ ಮಾದರಿಯಿದೆ. ಅವರು ತಮ್ಮ ಜನರೊಂದಿಗೆ ಹೇಳಿದ ಸಂದರ್ಭ: ‘ಖಂಡಿತವಾಗಿಯೂ ನಾವು ನಿಮ್ಮಿಂದ ಮತ್ತು ಅಲ್ಲಾಹುವಿನ ಹೊರತು ನೀವು ಆರಾಧಿಸುತ್ತಿರುವವುಗಳಿಂದ ಸಂಪೂರ್ಣ ವಿಮುಕ್ತರಾಗಿರುವೆವು. ನಾವು ನಿಮ್ಮಲ್ಲಿ ಅವಿಶ್ವಾಸವಿಟ್ಟಿರುವೆವು. ನೀವು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮಾತ್ರ ವಿಶ್ವಾಸವಿಡುವ ತನಕ ಎಂದೆಂದಿಗೂ ನಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಶತ್ರುತ್ವ ಹಾಗೂ ವಿದ್ವೇಷವು ಬಹಿರಂಗವಾಗಿಬಿಟ್ಟಿದೆ’.[[ಸತ್ಯವಿಶ್ವಾಸಿಗಳೊಂದಿಗೆ ಶತ್ರುಗಳಂತೆ ವರ್ತಿಸುವವರೊಂದಿಗೆ ಮಾತ್ರ ಶತ್ರುತ್ವ ತೋರಿಸಬೇಕು, ಮುಸ್ಲಿಮರೊಂದಿಗೆ ಶಾಂತಿಯುತವಾಗಿ ಸಹಬಾಳ್ವೆ ನಡೆಸಲು ಆಶಿಸುವವರೊಂದಿಗೆ ಮುಸ್ಲಿಮರು ಶಾಂತಿಯುತವಾಗಿ ವರ್ತಿಸಬೇಕೆಂದು ಕುರ್‍ಆನಿನ 8:60-61 ಸೂಕ್ತಿಗಳಲ್ಲಿ ಹೇಳಲಾಗಿದೆ. ಈ ಅಧ್ಯಾಯದ 8, 9 ಸೂಕ್ತಿಗಳನ್ನೂ ನೋಡಿರಿ....]] ಖಂಡಿತವಾಗಿಯೂ ನಾನು ತಮಗೋಸ್ಕರ ಪಾಪಮುಕ್ತಿ ಬೇಡುವೆನು ಮತ್ತು ತಮಗಾಗಿ ಅಲ್ಲಾಹುವಿನಿಂದ ಏನನ್ನೂ ಸ್ವಾಧೀನಪಡಿಸಲು ನನಗೆ ಸಾಧ್ಯವಿಲ್ಲ ಎಂದು ಇಬ್ರಾಹೀಮ್ ತಮ್ಮ ತಂದೆಯೊಂದಿಗೆ ಹೇಳಿದ ಮಾತಿನ ಹೊರತು.[[ಇಬ್ರಾಹೀಮ್ ತನ್ನ ತಂದೆಗೆ ಮಾತು ಕೊಟ್ಟ ಕಾರಣದಿಂದಾಗಿ ಅವರಿಗೆ ಪಾಪಮುಕ್ತಿಯನ್ನು ಬೇಡಿದ್ದರು. ಆದರೆ ಮುಸ್ಲಿಮರು ಈ ಮಾದರಿಯನ್ನು ಸ್ವೀಕರಿಸಿ ಅವಿಶ್ವಾಸಿಗಳಾದ ಹೆತ್ತವರಿಗೋಸ್ಕರ ಪ್ರಾರ್ಥಿಸಕೂಡದೆಂದು ಇದರಿಂದ ಗ್ರಹಿಸಬಹುದು.]] (ಅವರು ಪ್ರಾರ್ಥಿಸಿದರು): ‘ನಮ್ಮ ಪ್ರಭೂ! ನಾವು ನಿನ್ನ ಮೇಲೆ ಭರವಸೆಯಿಟ್ಟಿರುವೆವು ಮತ್ತು ನಾವು ನಿನ್ನೆಡೆಗೆ ಮರಳಿರುವೆವು. ಮರಳುವಿಕೆಯು ನಿನ್ನೆಡೆಗೇ ಆಗಿದೆ.' WHERE docid = 5154; 
UPDATE verses_content SET c2text= 'ನಮ್ಮ ಪ್ರಭೂ! ನಮ್ಮನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳ ಪರೀಕ್ಷೆಗೆ ಬಲಿಯಾಗಿಸದಿರು. ನಮ್ಮ ಪ್ರಭೂ! ನಮ್ಮನ್ನು ಕ್ಷಮಿಸು. ಖಂಡಿತವಾಗಿಯೂ ನೀನು ಪ್ರತಾಪಶಾಲಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವೆ’.' WHERE docid = 5155; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ನಿಮಗೆ -ಅಲ್ಲಾಹುವನ್ನು ಮತ್ತು ಅಂತ್ಯದಿನವನ್ನು ಆಶಿಸುವವರಿಗೆ- ಅವರಲ್ಲಿ ಉತ್ತಮ ಮಾದರಿಯಿದೆ. ಯಾರಾದರೂ ವಿಮುಖನಾಗುವು ದಾದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುವನು.' WHERE docid = 5156; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಮತ್ತು ಅವರ ಪೈಕಿ ನೀವು ಶತ್ರುತ್ವ ಹೊಂದಿರುವವರ ನಡುವೆ ಅಲ್ಲಾಹು ಮೈತ್ರಿಯನ್ನು ಸ್ಥಾಪಿಸಲೂ ಬಹುದು. ಅಲ್ಲಾಹು ಸಾಮಥ್ರ್ಯವುಳ್ಳವನಾಗಿರು ವನು. ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವನು.' WHERE docid = 5157; 
UPDATE verses_content SET c2text= 'ಧರ್ಮದ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮೊಂದಿಗೆ ಯುದ್ಧ ಮಾಡದವರು ಮತ್ತು ನಿಮ್ಮ ಮನೆಗಳಿಂದ ನಿಮ್ಮನ್ನು ಹೊರಗಟ್ಟದವರು ಯಾರೋ ಅವರಿಗೆ ನೀವು ಒಳಿತು ಮಾಡುವುದನ್ನು ಮತ್ತು ಅವರೊಂದಿಗೆ ನ್ಯಾಯಪಾಲಿಸು ವುದನ್ನು ಅಲ್ಲಾಹು ನಿಮಗೆ ವಿರೋಧಿಸುವುದಿಲ್ಲ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ನ್ಯಾಯ ಪಾಲಿಸುವವರನ್ನು ಇಷ್ಟಪಡುವನು.' WHERE docid = 5158; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ವಿರೋಧಿಸುವುದು ಧರ್ಮದ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮೊಂದಿಗೆ ಯುದ್ಧ ಮಾಡಿದವರು, ನಿಮ್ಮ ಮನೆಗಳಿಂದ ನಿಮ್ಮನ್ನು ಹೊರಗಟ್ಟಿದವರು ಮತ್ತು ನಿಮ್ಮನ್ನು ಹೊರಗಟ್ಟುವುದರಲ್ಲಿ ಪರಸ್ಪರ ಸಹಕರಿಸಿದವರು ಯಾರೋ ಅವರೊಂದಿಗೆ ಮೈತ್ರಿ ಮಾಡುವುದನ್ನು ಮಾತ್ರವಾಗಿದೆ. ಯಾರಾದರೂ ಅವರೊಂದಿಗೆ ಮೈತ್ರಿ ಮಾಡಿಕೊಂಡರೆ ಅವರೇ ಅಕ್ರಮಿಗಳಾಗಿರುವರು.' WHERE docid = 5159; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ವಿಶ್ವಾಸಿನಿಯರಾದ ಸ್ತ್ರೀಯರು ಅಭಯಾರ್ಥಿಗಳಾಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ಅವರನ್ನು ಪರೀಕ್ಷಿಸಿರಿ. ಅವರ ವಿಶ್ವಾಸದ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಅರಿತಿರುವನು. ತರುವಾಯ ಅವರು ವಿಶ್ವಾಸಿನಿಯರೆಂದು ನಿಮಗೆ ಮನದಟ್ಟಾದರೆ ಅವರನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳ ಬಳಿಗೆ ಮರಳಿ ಕಳುಹಿಸದಿರಿ. ಆ ಸ್ತ್ರೀಯರು ಅವರಿಗೆ ಧರ್ಮಸಮ್ಮತರಲ್ಲ. ಅವರು ಆ ಸ್ತ್ರೀಯರಿಗೂ ಧರ್ಮಸಮ್ಮತರಲ್ಲ. ಅವರಿಗೆ[[ವಿವಾಹಿತೆಯಾದ ಓರ್ವ ಮುಸ್ಲಿಮೇತರ ಸ್ತ್ರೀ ಇಸ್ಲಾಮ್ ಸ್ವೀಕರಿಸಿ ಅವಿಶ್ವಾಸಿ ಸಮುದಾಯದಿಂದ ಮುಸ್ಲಿಮ್ ಸಮುದಾಯಕ್ಕೆ ಬಂದರೆ ಏನು ಮಾಡಬೇಕೆಂದು ಇಲ್ಲಿ ಹೇಳಲಾಗಿದೆ. ಅವಳನ್ನು ಅವಿಶ್ವಾಸಿಗಳ ಕಡೆಗೆ ಮರಳಿ ಕಳುಹಿಸಬಾರದು. ಇಸ್ಲಾಮ್ ಸ್ವೀಕರಿಸಿದ ಓರ್ವ ಸ್ತ್ರೀ ಓರ್ವ ಸತ್ಯನಿಷೇಧಿಯ ಪತ್ನಿಯಾಗಿ ಮುಂದುವರಿಯಕೂಡದು. ಆದರೆ ಅವಿಶ್ವಾಸಿಯಾದ ಪತಿ ಆಕೆಗೆ ನೀಡಿದ ವಧುದಕ್ಷಿಣೆಯನ್ನು (ಮಹ್ರ್) ಮುಸ್ಲಿಮರು ಅವನಿಗೆ ಹಿಂದಿರುಗಿಸಬೇಕು. ಇದ್ದಃ ಕಳೆದ ನಂತರ ನ್ಯಾಯಬದ್ಧವಾದ ಮಹ್ರ್ ನೀಡಿ ಯಾವುದೇ ಮುಸ್ಲಿಮನಿಗೂ ಆಕೆಯನ್ನು ವರಿಸಬಹುದಾಗಿದೆ....]] ಅವರು ವ್ಯಯಿಸಿರುವುದನ್ನು ನೀಡಿರಿ. ನೀವು ಆ ಸ್ತ್ರೀಯರಿಗೆ ಅವರ ಪ್ರತಿಫಲವನ್ನು ನೀಡಿದರೆ ಅವರನ್ನು ವರಿಸುವುದರಲ್ಲಿ ನಿಮ್ಮ ಮೇಲೆ ದೋಷವಿಲ್ಲ. ಅವಿಶ್ವಾಸಿನಿಯರೊಂದಿಗಿನ ವಿವಾಹ ಸಂಬಂಧದಲ್ಲಿ ನೀವು ಗಟ್ಟಿಯಾಗಿ ನಿಲ್ಲದಿರಿ.[[ಇಸ್ಲಾಮ್ ಸ್ವೀಕರಿಸಿದ ಓರ್ವ ವ್ಯಕ್ತಿ ಅವಿಶ್ವಾಸಿಯಾದ ಸ್ತ್ರೀಯೊಂದಿಗೆ ದಾಂಪತ್ಯ ಸಂಬಂಧವನ್ನು ಮುಂದುವರಿಸಕೂಡದು....]] ನೀವೇನನ್ನು ವ್ಯಯಿಸಿರುವಿರೋ ಅದನ್ನು ಕೇಳಿ ಪಡೆಯಿರಿ. ಅವರು ಏನನ್ನು ವ್ಯಯಿಸಿರುವರೋ ಅದನ್ನು ಅವರೂ ಕೇಳಿ ಪಡೆಯಲಿ. ಅದು ಅಲ್ಲಾಹುವಿನ ನಿಯಮವಾಗಿದೆ. ಅವನು ನಿಮ್ಮ ಮಧ್ಯೆ ತೀರ್ಪು ನೀಡುವನು. ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5160; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಪತ್ನಿಯರ ಪೈಕಿ ಯಾರಾದರೂ ಸತ್ಯನಿಷೇಧಿಗಳ ಕಡೆಗೆ ಹೋಗಿ ಅವರು ನಿಮಗೆ ನಷ್ಟವಾದರೆ, ತರುವಾಯ ನೀವು ಕ್ರಮಕೈಗೊಳ್ಳುವುದಾದರೆ, ಯಾರ ಪತ್ನಿಯರು ನಷ್ಟವಾಗಿರುವರೋ, ಅವರಿಗೆ ಅವರು ವ್ಯಯಿಸಿದ ಮೊತ್ತ (ಮಹ್ರ್)ದಂತಿರುವುದನ್ನು ನೀಡಿರಿ. ಯಾವ ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ನೀವು ವಿಶ್ವಾಸವಿಡುತ್ತಿರುವಿರೋ ಅವನನ್ನು ಭಯಪಡಿರಿ.' WHERE docid = 5161; 
UPDATE verses_content SET c2text= 'ಓ ಪ್ರವಾದಿಯವರೇ! ‘ಅಲ್ಲಾಹುವಿನೊಂದಿಗೆ ಯಾವುದನ್ನೂ ಸಹಭಾಗಿತ್ವ ಮಾಡಲಾರೆವು, ಕಳ್ಳತನ ಮಾಡಲಾರೆವು, ವ್ಯಭಿಚಾರ ಮಾಡಲಾರೆವು, ತಮ್ಮ ಮಕ್ಕಳನ್ನು ವಧಿಸಲಾರೆವು, ತಮ್ಮ ಕೈಕಾಲುಗಳ ಮಧ್ಯೆ ಸುಳ್ಳಾರೋಪವನ್ನು ಹೊರಿಸಲಾರೆವು[[ವ್ಯಭಿಚಾರದ ಮೂಲಕ ಗರ್ಭಿಣಿಯಾಗಿ ಅದನ್ನು ಪತಿಯ ಮೇಲೆ ಆರೋಪಿಸುವ ಸ್ತ್ರೀಯರ ಕಡೆಗಿರುವ ಸೂಚನೆ.]] ಮತ್ತು ಯಾವುದೇ ಸತ್ಕರ್ಮದಲ್ಲೂ ತಮ್ಮೊಂದಿಗೆ ಅವಿಧೇಯತೆ ತೋರಲಾರೆವು’ ಎಂದು ಪ್ರತಿಜ್ಞೆ ಮಾಡುತ್ತಾ ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರು ತಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ತಾವು ಅವರ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಸ್ವೀಕರಿಸಿರಿ ಮತ್ತು ಅವರಿಗಾಗಿ ಅಲ್ಲಾಹುವಿನೊಂದಿಗೆ ಪಾಪಮುಕ್ತಿಯನ್ನು ಬೇಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವನು.' WHERE docid = 5162; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಅಲ್ಲಾಹು ಕ್ರೋಧ ಗೊಂಡಿರುವ ಒಂದು ಜನತೆಯೊಂದಿಗೆ ನೀವು ಮೈತ್ರಿ ಮಾಡದಿರಿ. ಗೋರಿಗಳಲ್ಲಿರುವವರ ಬಗ್ಗೆ ಸತ್ಯನಿಷೇಧಿಗಳು ನಿರಾಶರಾದಂತೆ ಪರಲೋಕದ ಬಗ್ಗೆ ಅವರು ನಿರಾಶ ರಾಗಿರುವರು.' WHERE docid = 5163; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವವುಗಳು ಅಲ್ಲಾಹುವಿನ ಕೀರ್ತನೆ ಮಾಡಿವೆ. ಅವನು ಪ್ರತಾಪ ಶಾಲಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5164; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಮಾಡದಿರುವುದನ್ನು ನೀವು ಏಕೆ ಹೇಳುತ್ತಿರುವಿರಿ?' WHERE docid = 5165; 
UPDATE verses_content SET c2text= 'ನೀವು ಮಾಡದಿರುವುದನ್ನು ನೀವು ಹೇಳುವುದು ಅಲ್ಲಾಹುವಿನ ಬಳಿ ಮಹಾ ಕ್ರೋಧಕ್ಕೆ ಕಾರಣವಾಗುತ್ತದೆ.' WHERE docid = 5166; 
UPDATE verses_content SET c2text= '(ಇಟ್ಟಿಗೆಗಳನ್ನು) ಸದೃಢವಾಗಿ ಜೋಡಿಸಲಾಗಿ ರುವ ಒಂದು ಗೋಡೆಯಂತೆ ಸಾಲು ಸಾಲಾಗಿ ನಿಂತು ತನ್ನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡುವವರನ್ನು ಅಲ್ಲಾಹು ಖಂಡಿತವಾಗಿಯೂ ಇಷ್ಟಪಡುವನು.' WHERE docid = 5167; 
UPDATE verses_content SET c2text= 'ಮೂಸಾ ತಮ್ಮ ಜನತೆಯೊಂದಿಗೆ ಹೇಳಿದ ಸಂದರ್ಭ: ‘ಓ ನನ್ನ ಜನರೇ! ನೀವೇಕೆ ನನಗೆ ಕಿರುಕುಳ ಕೊಡುತ್ತಿರುವಿರಿ? ನಾನು ನಿಮ್ಮೆಡೆಗೆ ಕಳುಹಿಸಲಾಗಿರುವ ಅಲ್ಲಾಹುವಿನ ಸಂದೇಶವಾಹಕನಾಗಿರುವೆನು ಎಂಬುದನ್ನು ನೀವು ಅರಿತಿರುವಿರಿ!’ ತರುವಾಯ ಅವರು ಜಾರಿ ಹೋದಾಗ ಅಲ್ಲಾಹು ಅವರ ಹೃದಯಗಳನ್ನು ಜಾರುವಂತೆ ಮಾಡಿದನು. ಅಲ್ಲಾಹು ಧಿಕ್ಕಾರಿಗಳಾದ ಜನರನ್ನು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಸೇರಿಸಲಾರನು.' WHERE docid = 5168; 
UPDATE verses_content SET c2text= 'ಮರ್ಯಮ್ ರ ಪುತ್ರ ಈಸಾ ಹೇಳಿದ ಸಂದರ್ಭ: ‘ಓ ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳೇ! ನಾನು ನನಗಿಂತ ಮುಂಚಿನ ತೌರಾತನ್ನು ದೃಢೀಕರಿಸುವವನಾಗಿ ಮತ್ತು ನನ್ನ ನಂತರ ಬರುವ ಅಹ್ಮದ್ ಎಂಬ ಹೆಸರಿನ ಒಬ್ಬ ಸಂದೇಶವಾಹಕರ ಬಗ್ಗೆ[[ಅಹ್ಮದ್ ಎಂಬುದು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರ ಇನ್ನೊಂದು ಹೆಸರಾಗಿದೆ. ಅಹ್ಮದ್, ಮುಹಮ್ಮದ್ ಎಂಬೀ ಹೆಸರುಗಳ ಅರ್ಥ ಪ್ರಶಂಸನೀಯ ಎಂದಾಗಿದೆ. ಈಸಾ (عليه السلام) ರವರ (ಯೇಸುವಿನ) ನಂತರ ಬರುವ ಸಂದೇಶವಾಹಕರ ಬಗ್ಗೆ ಯೋಹಾನನ ಸುವಾರ್ತೆಯಲ್ಲಿ ಪ್ರಸ್ತಾಪಿಸಲಾದ ಪೆರಿಕ್ಲೀಟಸ್ ಎಂಬ ಗ್ರೀಕ್ ಪದದ ಅರ್ಥ ಕೂಡ ಇದೇ ಆಗಿದೆ.]] ಶುಭವಾರ್ತೆ ತಿಳಿಸುವವನಾಗಿ ನಿಮ್ಮೆಡೆಗೆ ಕಳುಹಿಸಲಾಗಿರುವ ಅಲ್ಲಾಹುವಿನ ಸಂದೇಶ ವಾಹಕನಾಗಿರುವೆನು. ತರುವಾಯ ಅವರು ಸ್ಪಷ್ಟವಾದ ಪುರಾವೆಗಳೊಂದಿಗೆ ಅವರ ಬಳಿಗೆ ಬಂದಾಗ ಅವರು ಹೇಳಿದರು: ‘ಇದು ಸ್ಪಷ್ಟವಾದ ಮಾಂತ್ರಿಕತೆಯಾಗಿದೆ’.' WHERE docid = 5169; 
UPDATE verses_content SET c2text= 'ತನ್ನನ್ನು ಇಸ್ಲಾಮಿನೆಡೆಗೆ ಆಹ್ವಾನಿಸಲಾಗುವಾಗ ಅಲ್ಲಾಹುವಿನ ಹೆಸರಲ್ಲಿ ಸುಳ್ಳನ್ನು ಹೆಣೆದವನಿಗಿಂತಲೂ ದೊಡ್ಡ ಅಕ್ರಮಿ ಇನ್ನಾರಿರುವನು? ಅಕ್ರಮಿಗಳಾಗಿರುವ ಜನರನ್ನು ಅಲ್ಲಾಹು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಸೇರಿಸಲಾರನು.' WHERE docid = 5170; 
UPDATE verses_content SET c2text= 'ಅವರು ತಮ್ಮ ಬಾಯಿಯ ಮೂಲಕ ಅಲ್ಲಾಹು ವಿನ ಪ್ರಕಾಶವನ್ನು ಊದಿ ನಂದಿಸಲು ಇಚ್ಛಿಸುತ್ತಿರುವರು. ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳು ಅಸಹ್ಯಪಟ್ಟರೂ ಅಲ್ಲಾಹು ತನ್ನ ಪ್ರಕಾಶವನ್ನು ಪೂರ್ಣಗೊಳಿಸುವವನಾಗಿರುವನು.' WHERE docid = 5171; 
UPDATE verses_content SET c2text= 'ಸನ್ಮಾರ್ಗ ಮತ್ತು ಸತ್ಯಧರ್ಮದೊಂದಿಗೆ, ಅದನ್ನು ಸರ್ವ ಧರ್ಮಗಳ ಮೇಲೂ ಪ್ರಕಟಗೊಳಿಸಿ ತೋರಿಸುವ ಸಲುವಾಗಿ ತನ್ನ ಸಂದೇಶವಾಹಕರನ್ನು ಕಳುಹಿಸಿದವನು ಅವನಾಗಿರುವನು. ಬಹುದೇವಾರಾಧಕರಿಗೆ (ಅದು) ಅಸಹ್ಯಕರವಾದರೂ ಸರಿಯೇ!' WHERE docid = 5172; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಯಾತನಾಮಯವಾದ ಶಿಕ್ಷೆಯಿಂದ ನಿಮ್ಮನ್ನು ಪಾರು ಮಾಡುವ ಒಂದು ವ್ಯಾಪಾರದ ಬಗ್ಗೆ ನಾನು ನಿಮಗೆ ತಿಳಿಸಿಕೊಡಲೇ?' WHERE docid = 5173; 
UPDATE verses_content SET c2text= 'ನೀವು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರಲ್ಲಿ ವಿಶ್ವಾಸವಿಡಿರಿ. ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ ನಿಮ್ಮ ಸಂಪತ್ತುಗಳ ಮೂಲಕ ಮತ್ತು ಶರೀರಗಳ ಮೂಲಕ ಹೋರಾಡಿರಿ. ಅದು ನಿಮಗೆ ಅತ್ಯುತ್ತಮವಾಗಿದೆ. ನೀವು ಅರಿವುಳ್ಳವರಾಗಿದ್ದರೆ.' WHERE docid = 5174; 
UPDATE verses_content SET c2text= 'ಹಾಗಾದರೆ ಅವನು ನಿಮಗೆ ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವನು ಮತ್ತು ತಳಭಾಗದಿಂದ ನದಿಗಳು ಹರಿಯುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಮತ್ತು ಶಾಶ್ವತ ವಾಸಕ್ಕಾಗಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳ ವಿಶಿಷ್ಟ ವಸತಿಗಳಲ್ಲಿ ಅವನು ನಿಮ್ಮನ್ನು ಪ್ರವೇಶ ಮಾಡಿಸುವನು. ಮಹಾ ವಿಜಯವು ಅದೇ ಆಗಿದೆ.' WHERE docid = 5175; 
UPDATE verses_content SET c2text= 'ನೀವು ಇಷ್ಟಪಡುವ ಇನ್ನೊಂದನ್ನೂ (ಅವನು ನಿಮಗೆ ನೀಡುವನು). ಅದು ಅಲ್ಲಾಹುವಿನ ಕಡೆಯ ಸಹಾಯ ಮತ್ತು ಸನ್ನಿಹಿತವಾಗಿರುವ ವಿಜಯವಾಗಿದೆ. (ಓ ಪ್ರವಾದಿಯವರೇ!) ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಶುಭವಾರ್ತೆಯನ್ನು ತಿಳಿಸಿರಿ.' WHERE docid = 5176; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹುವಿನ ಸಹಾಯಕರಾಗಿರಿ. ಮರ್ಯಮ್ ರ ಪುತ್ರ ಈಸಾ ‘ಅಲ್ಲಾಹು ವಿನೆಡೆಗಿರುವ ಮಾರ್ಗದಲ್ಲಿ ನನ್ನ ಸಹಾಯಕರಾಗಿ ಯಾರಿರುವಿರಿ?’ ಎಂದು ಹವಾರಿಗಳೊಂದಿಗೆ ಕೇಳಿದಂತೆ. ಹವಾರಿಗಳು ಹೇಳಿದರು: ‘ನಾವು ಅಲ್ಲಾಹುವಿನ ಸಹಾಯಕರಾಗಿರುವೆವು’. ಆಗ ಇಸ್ರಾಈಲ್ ಸಂತತಿ ಗಳಲ್ಲಿ ಸೇರಿದ ಒಂದು ಪಂಗಡವು ವಿಶ್ವಾಸವಿಟ್ಟಿತು ಹಾಗೂ ಮತ್ತೊಂದು ಪಂಗಡವು ನಿಷೇಧಿಸಿತು. ತರುವಾಯ ವಿಶ್ವಾಸವಿಟ್ಟವರಿಗೆ ಅವರ ಶತ್ರುಗಳ ವಿರುದ್ಧ ನಾವು ಬೆಂಬಲವನ್ನು ನೀಡಿದೆವು. ಹಾಗೆ ಅವರು ವಿಜಯಶಾಲಿ ಗಳಾದರು.' WHERE docid = 5177; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವವುಗಳು ಸಾಮ್ರಾಟನೂ, ಪರಮ ಪವಿತ್ರನೂ, ಪ್ರತಾಪಶಾಲಿಯೂ, ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವ ಅಲ್ಲಾಹುವಿನ ಕೀರ್ತನೆ ಮಾಡುತ್ತಿವೆ.' WHERE docid = 5178; 
UPDATE verses_content SET c2text= 'ಅನಕ್ಷರಸ್ಥರ ಮಧ್ಯೆ, ತನ್ನ ದೃಷ್ಟಾಂತಗಳನ್ನು ಅವರಿಗೆ ಓದಿಕೊಡುವ ಸಲುವಾಗಿ, ಅವರನ್ನು ಸಂಸ್ಕರಿಸುವ ಸಲುವಾಗಿ ಮತ್ತು ಅವರಿಗೆ ಗ್ರಂಥ ಹಾಗೂ ತತ್ವಜ್ಞಾನವನ್ನು ಕಲಿಸಿಕೊಡುವ ಸಲುವಾಗಿ ಅವರಿಂದಲೇ ಇರುವ ಒಬ್ಬ ಸಂದೇಶವಾಹಕರನ್ನು ಕಳುಹಿಸಿದವನು ಅವನಾಗಿರುವನು. ಖಂಡಿತವಾಗಿಯೂ ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಅವರು ಸ್ಪಷ್ಟವಾದ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲಿದ್ದರು.' WHERE docid = 5179; 
UPDATE verses_content SET c2text= 'ಅವರ ಪೈಕಿ ಇನ್ನೂ ಅವರೊಂದಿಗೆ ಸೇರಿಕೊಳ್ಳದವರೂ ಆಗಿರುವ ಇತರರೆಡೆಗೂ (ಅವರನ್ನು ಕಳುಹಿಸಲಾಗಿದೆ).[[ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರ ಸಮಕಾಲೀನರೋ ದೇಶೀಯರೋ ಅಲ್ಲದವರಿಗೂ ಅವರ ಸಂದೇಶವಾಹಕತ್ವವು ಅನ್ವಯವಾಗುತ್ತದೆ.]] ಅವನು ಪ್ರತಾಪಶಾಲಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5180; 
UPDATE verses_content SET c2text= 'ಅದು ಅಲ್ಲಾಹುವಿನ ಅನುಗ್ರಹವಾಗಿದೆ. ಅವನಿಚ್ಛಿಸುವವರಿಗೆ ಅವನು ಅದನ್ನು ದಯಪಾಲಿಸುವನು. ಅಲ್ಲಾಹು ಮಹಾ ಅನುಗ್ರಹದಾತನಾಗಿರುವನು.' WHERE docid = 5181; 
UPDATE verses_content SET c2text= 'ತೌರಾತಿನ ಹೊಣೆ ವಹಿಸಿಕೊಡಲಾಗಿರುವವರು ಮತ್ತು ತರುವಾಯ ಅದನ್ನು ವಹಿಸಿಕೊಳ್ಳದಿರುವವರ (ಯಹೂದರ) ಉದಾಹರಣೆಯು ಗ್ರಂಥಗಳನ್ನು ಹೊರುವ ಕತ್ತೆಯಂತಾಗಿದೆ.[[ಗ್ರಂಥದ ವಾಹಕರೆಂದು ಅಭಿಮಾನ ಪಡುವ, ಆದರೆ ಗ್ರಂಥದ ಸಾರೋಪದೇಶಗಳನ್ನು ಬದುಕಿನಲ್ಲಿ ಅಳವಡಿಸಿಕೊಳ್ಳದ ಎಲ್ಲರಿಗೂ ಈ ಉಪಮೆಯು ಅನ್ವಯವಾಗುತ್ತದೆ.]] ಅಲ್ಲಾಹುವಿನ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸಿರುವ ಜನರ ಉದಾಹರಣೆಯು ಎಷ್ಟು ನಿಕೃಷ್ಟವಾದುದು! ಅಕ್ರಮಿಗಳಾದ ಜನರನ್ನು ಅಲ್ಲಾಹು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಸೇರಿಸಲಾರನು.' WHERE docid = 5182; 
UPDATE verses_content SET c2text= '(ಓ ಪ್ರವಾದಿಯವರೇ!) ಹೇಳಿರಿ: ‘ಓ ಯಹೂ ದರೇ! ಇತರೆಲ್ಲರ ಹೊರತು ನೀವು ಮಾತ್ರ ಅಲ್ಲಾಹುವಿನ ಮಿತ್ರರಾಗಿರುವಿರಿ ಎಂದು ನೀವು ವಾದಿಸುವುದಾದರೆ ನೀವು ಮರಣವನ್ನು ಆಶಿಸಿರಿ.[[ಮರಣದೊಂದಿಗೆ ಸ್ವರ್ಗ ಸುಖಗಳು ಲಭ್ಯವಾಗಲಿವೆ ಎಂದು ದೃಢವಾಗಿ ಹೇಳುವಷ್ಟು ದೇವಸಾಮೀಪ್ಯವನ್ನು ನಾವು ಗಳಿಸಿದ್ದೇವೆ ಎಂಬ ಯಹೂದಿಗಳ ವಾದವು ಸತ್ಯಸಂಧವಾಗಿದ್ದರೆ ಅವರು ಮರಣವನ್ನು ಆಶಿಸಲಿ. ಹಲವು ರೀತಿಯ ಕಷ್ಟಗಳನ್ನು ಅನುಭವಿಸಿ ಇಹಲೋಕದಲ್ಲಿ ಬದುಕುವುದು ಅವರ ಪಾಲಿಗೆ ಯೋಗ್ಯವಾದುದಲ್ಲ.]] ನೀವು ಸತ್ಯಸಂಧರಾಗಿದ್ದರೆ’.' WHERE docid = 5183; 
UPDATE verses_content SET c2text= 'ಆದರೆ ಅವರ ಕೈಗಳು ಪೂರ್ವಭಾವಿಯಾಗಿ ಮಾಡಿಟ್ಟಿರುವುದರ ಫಲವಾಗಿ ಅವರು ಎಂದಿಗೂ ಅದನ್ನು ಆಶಿಸಲಾರರು. ಅಲ್ಲಾಹು ಅಕ್ರಮಿಗಳ ಬಗ್ಗೆ ಅರಿವುಳ್ಳ ವನಾಗಿರುವನು.' WHERE docid = 5184; 
UPDATE verses_content SET c2text= '(ಓ ಪ್ರವಾದಿಯವರೇ!) ಹೇಳಿರಿ: ‘ಯಾವ ಮರಣದಿಂದ ನೀವು ಓಡಿ ಹೋಗುತ್ತಿರುವಿರೋ ಅದು ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮನ್ನು ಸಂಧಿಸಲಿದೆ. ತರುವಾಯ ಅಗೋಚರವನ್ನು ಮತ್ತು ಗೋಚರವನ್ನು ಅರಿಯುವವನ ಬಳಿಗೆ ನಿಮ್ಮನ್ನು ಮರಳಿಸಲಾಗುವುದು. ಆಗ ನೀವು ಮಾಡಿಕೊಂಡಿರುವವುಗಳ ಬಗ್ಗೆ ಅವನು ನಿಮಗೆ ತಿಳಿಸಿಕೊಡುವನು’.' WHERE docid = 5185; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಶುಕ್ರವಾರದಂದು ನಮಾಝ್ ಗಾಗಿ ಕರೆಯಲಾದರೆ ಅಲ್ಲಾಹುವಿನ ಸ್ಮರಣೆ ಯೆಡೆಗೆ ವೇಗವಾಗಿ ಧಾವಿಸಿರಿ ಮತ್ತು ವ್ಯಾಪಾರವನ್ನು ವರ್ಜಿಸಿರಿ. ಅದು ನಿಮಗೆ ಉತ್ತಮವಾಗಿದೆ. ನೀವು ಅರಿತವರಾಗಿದ್ದರೆ.' WHERE docid = 5186; 
UPDATE verses_content SET c2text= 'ತರುವಾಯ ನಮಾಝ್ ನಿರ್ವಹಿಸಿದ ಬಳಿಕ ನೀವು ಭೂಮಿಯಲ್ಲಿ ಚದುರಿ ಹೋಗಿರಿ ಮತ್ತು ಅಲ್ಲಾಹು ವಿನ ಅನುಗ್ರಹದಿಂದ ಅರಸಿರಿ. ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಹೇರಳವಾಗಿ ಸ್ಮರಿಸಿರಿ. ನೀವು ಯಶಸ್ವಿಯಾಗಲೂಬಹುದು.' WHERE docid = 5187; 
UPDATE verses_content SET c2text= 'ಅವರೊಂದು ವ್ಯಾಪಾರವನ್ನಾಗಲಿ, ಮನೋ ರಂಜನೆಯನ್ನಾಗಲಿ ಕಂಡರೆ ಅದರೆಡೆಗೆ ಧಾವಿಸುವರು ಮತ್ತು ತಮ್ಮನ್ನು ನಿಂತ ಸ್ಥಿತಿಯಲ್ಲೇ ಬಿಟ್ಟು ಬಿಡುವರು.[[ಒಂದು ಶುಕ್ರವಾರದಂದು ಮದೀನ ಮಸೀದಿಯ ಮಿಂಬರ್‍ನಲ್ಲಿ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಖುತ್ಬಾ ನಿರ್ವಹಿಸುತ್ತಿದ್ದಾಗ ಮದೀನದ ಮಾರುಕಟ್ಟೆಗೆ ವರ್ತಕರ ತಂಡವೊಂದು ಆಗಮಿಸಿ ಗದ್ದಲ ಮಾಡತೊಡಗಿದಾಗ ಖುತ್ಬಾ ಆಲಿಸುತ್ತಿದ್ದವರ ಪೈಕಿ ಕೆಲವರ ಹೊರತು ಉಳಿದವರೆಲ್ಲರೂ ಮಾರುಕಟ್ಟೆಗೆ ಓಡಿದರು. ಆ ಸಂದರ್ಭದಲ್ಲಿ ಈ ಸೂಕ್ತಿಯು ಅವತೀರ್ಣಗೊಂಡಿತು.]] ಹೇಳಿರಿ: ‘ಮನೋರಂಜನೆ ಮತ್ತು ವ್ಯಾಪಾರಕ್ಕಿಂತ ಅಲ್ಲಾಹುವಿನ ಬಳಿಯಿರುವುದು ಅತ್ಯುತ್ತಮವಾಗಿದೆ. ಅಲ್ಲಾಹು ಅನ್ನಾಧಾರ ನೀಡುವವರಲ್ಲಿ ಅತ್ಯುತ್ತಮನಾಗಿರು ವನು’.' WHERE docid = 5188; 
UPDATE verses_content SET c2text= 'ಕಪಟ ವಿಶ್ವಾಸಿಗಳು ತಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ಅವರು ಹೇಳುವರು: ‘ಖಂಡಿತವಾಗಿಯೂ ತಾವು ಅಲ್ಲಾಹುವಿನ ಸಂದೇಶವಾಹಕರಾಗಿರುವಿರಿ ಎಂಬುದಕ್ಕೆ ನಾವು ಸಾಕ್ಷ್ಯ ವಹಿಸುವೆವು’. ಖಂಡಿತವಾಗಿಯೂ ತಾವು ಅಲ್ಲಾಹುವಿನ ಸಂದೇಶವಾಹಕರಾಗಿರುವಿರಿ ಎಂಬುದು ಅಲ್ಲಾಹುವಿಗೆ ತಿಳಿದಿದೆ. ಖಂಡಿತವಾಗಿಯೂ ಕಪಟವಿಶ್ವಾಸಿಗಳು ಸುಳ್ಳು ನುಡಿಯುವವರಾಗಿರುವರು ಎಂಬುದಕ್ಕೆ ಅಲ್ಲಾಹು ಸಾಕ್ಷ್ಯ ವಹಿಸುವನು.' WHERE docid = 5189; 
UPDATE verses_content SET c2text= 'ಅವರು ತಮ್ಮ ಪ್ರತಿಜ್ಞೆಗಳನ್ನು ಒಂದು ಗುರಾಣಿ ಯನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿರುವರು.[[ಇಸ್ಲಾಮ್ ಮತ್ತು ಮುಸ್ಲಿಮರ ವಿರುದ್ಧ ಅನೇಕ ಪಿತೂರಿಗಳನ್ನು ರೂಪಿಸುತ್ತಿದ್ದ ಕಪಟವಿಶ್ವಾಸಿಗಳು ಆತ್ಮರಕ್ಷಣೆಗಾಗಿ ತಾವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಎಂದು ಆಣೆಯಿಟ್ಟು ಹೇಳುವುದು ಒಂದು ತಂತ್ರ ಮಾತ್ರವಾಗಿತ್ತು.]] ಹೀಗೆ ಅವರು ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಿಂದ (ಜನರನ್ನು) ತಡೆದಿರುವರು. ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಮಾಡುತ್ತಿರುವುದು ಅತಿ ನಿಕೃಷ್ಟವಾದುದಾಗಿದೆ.' WHERE docid = 5190; 
UPDATE verses_content SET c2text= 'ಅದು ಅವರು ಮೊದಲು ವಿಶ್ವಾಸವಿಟ್ಟು ತರುವಾಯ ನಿಷೇಧಿಸಿದರು ಎಂಬುದರಿಂದಾಗಿದೆ. ಆದ್ದ ರಿಂದ ಅವರ ಹೃದಯಗಳ ಮೇಲೆ ಮುದ್ರೆಯೊತ್ತಲಾಯಿತು. ತನ್ನಿಮಿತ್ತ ಅವರು (ವಿಷಯವನ್ನು) ಗ್ರಹಿಸಲಾರರು.' WHERE docid = 5191; 
UPDATE verses_content SET c2text= 'ತಾವು ಅವರನ್ನು ಕಂಡರೆ ಅವರ ಶರೀರಗಳು ತಮ್ಮನ್ನು ಅಚ್ಚರಿಪಡಿಸುವುದು! ಅವರು ಮಾತನಾಡ ತೊಡಗಿದರೆ ತಾವು ಅವರ ಮಾತನ್ನು ಕೇಳುತ್ತಾ ಕೂರುವಿರಿ.[[ಕಪಟವಿಶ್ವಾಸಿಗಳು ದೈಹಿಕ ಸೌಷ್ಟವವುಳ್ಳವರೂ ಮತ್ತು ಸಮರ್ಥವಾಗಿ ಮಾತನಾಡುವ ಸಾಮರ್ಥ್ಯವುಳ್ಳವರೂ ಆಗಿದ್ದರು....]] ಅವರು ಒರಗಿ ನಿಲ್ಲಿಸಲಾದ ಮರದ ದಿಮ್ಮಿಗಳಂತಿರುವರು. ಪ್ರತಿಯೊಂದು ಸದ್ದೂ ತಮಗೆ ವಿರುದ್ಧವಾಗಿದೆಯೆಂದು ಅವರು ಭಾವಿಸುವರು.[[ಕಪಟವಿಶ್ವಾಸಿಗಳು ಸದಾ ಭಯದಲ್ಲಿರುತ್ತಿದ್ದರು. ತಮ್ಮ ನಿಜಸ್ಥಿತಿಯನ್ನು ಯಾರಾದರೂ ಅರಿಯುತ್ತಿದ್ದಾರೆಯೇ ಎಂದು ಅವರು ಸದಾ ಮೈಯೆಲ್ಲಾ ಕಿವಿಯಾಗಿಸಿ ನಡೆಯುತ್ತಿದ್ದರು. ಆಗ ಅವರು ಆಲಿಸುವ ಕೆಲವು ಸದ್ದುಗಳನ್ನು ತಮಗೆ ವಿರುದ್ಧವಾಗಿದೆಯೆಂದು ಭಾವಿಸಿ ಅವರು ಆತಂಕಗೊಳ್ಳುತ್ತಿದ್ದರು.]] ಅವರೇ ವೈರಿಗಳು! ಆದ್ದರಿಂದ ಅವರ ಬಗ್ಗೆ ಎಚ್ಚರವಹಿಸಿರಿ. ಅಲ್ಲಾಹು ಅವರನ್ನು ನಾಶ ಮಾಡಲಿ. ಅವರನ್ನು ಹೇಗೆ ದಾರಿತಪ್ಪಿಸಲಾಗುತ್ತಿದೆ?' WHERE docid = 5192; 
UPDATE verses_content SET c2text= '‘ಬನ್ನಿರಿ. ಅಲ್ಲಾಹುವಿನ ಸಂದೇಶವಾಹಕರು ನಿಮಗೋಸ್ಕರ ಪಾಪಮುಕ್ತಿಯನ್ನು ಬೇಡುವರು’ ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲಾದರೆ ಅವರು ಅವರ ತಲೆಯನ್ನು ತಿರುಗಿಸುವರು. ಅವರು ಅಹಂಕಾರಪಡುತ್ತಾ ವಿಮುಖ ರಾಗಿ ಹೋಗುವುದನ್ನು ತಾವು ಕಾಣುವಿರಿ.' WHERE docid = 5193; 
UPDATE verses_content SET c2text= 'ತಾವು ಅವರಿಗೋಸ್ಕರ ಪಾಪಮುಕ್ತಿಯನ್ನು ಬೇಡಿದರೂ ಅಥವಾ ಬೇಡದಿದ್ದರೂ ಅವರ ಪಾಲಿಗೆ ಅದು ಸಮಾನವಾಗಿದೆ. ಅಲ್ಲಾಹು ಎಂದಿಗೂ ಅವರನ್ನು ಕ್ಷಮಿಸಲಾರನು. ಖಂಡಿತವಾಗಿಯೂ ದುರ್ಮಾರ್ಗಿಗಳಾದ ಜನರನ್ನು ಅಲ್ಲಾಹು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಸೇರಿಸಲಾರನು.' WHERE docid = 5194; 
UPDATE verses_content SET c2text= '‘ಅಲ್ಲಾಹುವಿನ ಸಂದೇಶವಾಹಕರ ಬಳಿಯಿರುವವ ರಿಗೆ, ಅವರು (ಅಲ್ಲಿಂದ) ಹೊರಟುಹೋಗುವ ತನಕ ನೀವು ಏನನ್ನೂ ವ್ಯಯಿಸದಿರಿ’ ಎಂದು ಹೇಳುವವ ರಾಗಿರುವರು ಅವರು.[[ಕಪಟವಿಶ್ವಾಸಿಗಳು ಅನ್ಸಾರ್‍ಗಳ ಬಳಿ ತೆರಳಿ ಅವರು ಮುಹಾಜಿರ್‍ಗಳಿಗೆ ಒದಗಿಸುತ್ತಿದ್ದ ಸಹಾಯವನ್ನು ನಿಲ್ಲಿಸಲು ಪ್ರಯತ್ನಿಸುತ್ತಿದ್ದರು.]] ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿ ಯಲ್ಲಿರುವ ಖಜಾನೆಗಳು ಅಲ್ಲಾಹುವಿನದ್ದಾಗಿವೆ. ಆದರೆ ಕಪಟವಿಶ್ವಾಸಿಗಳು ವಿಷಯವನ್ನು ಗ್ರಹಿಸುವುದಿಲ್ಲ.' WHERE docid = 5195; 
UPDATE verses_content SET c2text= 'ಅವರು ಹೇಳುವರು: ‘ನಾವು ಮದೀನಕ್ಕೆ ಮರಳಿ ದರೆ ಹೆಚ್ಚು ಪ್ರತಾಪವಿರುವವರು ಕೀಳಾಗಿರುವವರನ್ನು ಖಂಡಿತವಾಗಿಯೂ ಹೊರಗಟ್ಟುವರು’.[[ತಾವು ಪ್ರತಾಪಶಾಲಿಗಳಾಗಿರುವುದರಿಂದ ಬಲಹೀನರಾದ ಮುಸ್ಲಿಮರನ್ನು ಮದೀನದಿಂದ ಹೊರಗಟ್ಟಲು ತಮಗೆ ಸಾಧ್ಯವಿದೆಯೆಂದು ಕಪಟವಿಶ್ವಾಸಿಗಳು ಭಾವಿಸಿದ್ದರು. ಆದ್ದರಿಂದಲೇ ಅವರು ಮುಸ್ಲಿಮರ ಶತ್ರುಗಳೊಂದಿಗೆ ಸಂಚುಗಳಲ್ಲಿ ಭಾಗಿಯಾದರು. ಗುಪ್ತ ಮಾತುಕತೆಗಳಲ್ಲಿ ಅವರು ಈ ವಿಷಯವನ್ನು ಸ್ಪಷ್ಟವಾಗಿ ಹೇಳುತ್ತಿದ್ದರು. ‘ಮುರೈಸಿ''ಅ’ ಯುದ್ಧದಲ್ಲಿ ಅವರ ಮುಖಂಡ ಅಬ್ದುಲ್ಲಾಹ್ ಇಬ್ನ್ ಉಬಯ್ಯ್ ಈ ವಿಷಯವನ್ನು ಬಹಿರಂಗವಾಗಿ ಹೇಳಿದ್ದನು.]] ಪ್ರತಾಪವಿರು ವುದು ಅಲ್ಲಾಹುವಿಗೆ, ಅವನ ಸಂದೇಶವಾಹಕರಿಗೆ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗಾಗಿದೆ. ಆದರೆ ಕಪಟವಿಶ್ವಾಸಿಗಳು (ವಿಷಯವನ್ನು) ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳುವುದಿಲ್ಲ.' WHERE docid = 5196; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನಿಮ್ಮ ಸಂಪತ್ತು ಮತ್ತು ಸಂತತಿಗಳು ಅಲ್ಲಾಹುವಿನ ಸ್ಮರಣೆಯಿಂದ ನಿಮ್ಮನ್ನು ಅಲಕ್ಷ್ಯಗೊಳಿಸದಿರಲಿ. ಯಾರು ಹಾಗೆ ಮಾಡುವರೋ ಅವರೇ ನಷ್ಟ ಹೊಂದಿದವರಾಗಿರುವರು.' WHERE docid = 5197; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಮರಣವು ಆಸನ್ನವಾಗು ವುದಕ್ಕೆ ಮುಂಚಿತವಾಗಿ ನಾವು ನಿಮಗೆ ನೀಡಿರುವುದರಿಂದ ವ್ಯಯಿಸಿರಿ. ಆ ಸಮಯದಲ್ಲಿ ಅವನು ಹೇಳುವನು: ‘ನನ್ನ ಪ್ರಭೂ! ಸಮೀಪದ ಒಂದು ಅವಧಿಯವರೆಗೆ ನೀನೇಕೆ ನನಗೆ ಕಾಲಾವಕಾಶವನ್ನು ನೀಡಲಿಲ್ಲ? ಹಾಗಿದ್ದರೆ ನಾನು ದಾನಧರ್ಮ ಮಾಡುವವನಾಗುತ್ತಿದ್ದೆನು ಮತ್ತು ಸಜ್ಜನರಲ್ಲಿ ಸೇರಿದವನಾಗುತ್ತಿದ್ದೆನು’.' WHERE docid = 5198; 
UPDATE verses_content SET c2text= 'ಯಾವುದೇ ಒಬ್ಬ ವ್ಯಕ್ತಿಯ ಅವಧಿಯು ಬಂದರೆ ತರುವಾಯ ಅಲ್ಲಾಹು ಅವನಿಗೆ ಎಂದಿಗೂ ಕಾಲಾವಕಾಶ ವನ್ನು ನೀಡಲಾರನು. ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿಯುವವನಾಗಿರುವನು.' WHERE docid = 5199; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವವುಗಳು ಅಲ್ಲಾಹುವನ್ನು ಕೊಂಡಾಡುತ್ತಿವೆ. ಆಧಿಪತ್ಯವಿರುವುದು ಅವನಿಗಾಗಿದೆ. ಸ್ತುತಿಯು ಅವನಿಗಾಗಿದೆ. ಅವನು ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ಸಾಮರ್ಥ್ಯವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5200; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಅವನಾಗಿರುವನು. ತರುವಾಯ ನಿಮ್ಮಲ್ಲಿ ಸತ್ಯನಿಷೇಧಿಗಳಿರುವರು ಮತ್ತು ನಿಮ್ಮಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೂ ಇರುವರು. ನೀವು ಮಾಡುತ್ತಿರು ವುದನ್ನು ಅಲ್ಲಾಹು ವೀಕ್ಷಿಸುವವನಾಗಿರುವನು.' WHERE docid = 5201; 
UPDATE verses_content SET c2text= 'ಅವನು ಆಕಾಶಗಳನ್ನು ಮತ್ತು ಭೂಮಿಯನ್ನು ಸತ್ಯದೊಂದಿಗೆ ಸೃಷ್ಟಿಸಿರುವನು. ಅವನು ನಿಮಗೆ ರೂಪ ವನ್ನು ನೀಡಿರುವನು ಮತ್ತು ನಿಮ್ಮ ರೂಪಗಳನ್ನು ಉತ್ತಮ ಗೊಳಿಸಿರುವನು. ಮರಳುವಿಕೆಯು ಅವನೆಡೆಗೇ ಆಗಿದೆ.' WHERE docid = 5202; 
UPDATE verses_content SET c2text= 'ಆಕಾಶಗಳಲ್ಲಿರುವುದನ್ನು ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ರುವುದನ್ನು ಅವನು ಅರಿಯುವನು. ನೀವು ಮರೆಮಾಚು ವುದನ್ನೂ ಬಹಿರಂಗಪಡಿಸುವುದನ್ನೂ ಅವನು ಅರಿಯು ವನು. ಅಲ್ಲಾಹು ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಅರಿಯು ವವನಾಗಿರುವನು.' WHERE docid = 5203; 
UPDATE verses_content SET c2text= 'ಮುಂಚೆ ನಿಷೇಧಿಸಿದವರ ವೃತ್ತಾಂತವು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿಲ್ಲವೇ? ಅವರ ಕರ್ಮದ ಭವಿಷ್ಯವನ್ನು ಅವರು ಆಸ್ವಾದಿಸಿದರು. ಅವರಿಗೆ (ಪರಲೋಕದಲ್ಲಿ) ಯಾತನಾಮಯವಾದ ಶಿಕ್ಷೆಯಿದೆ.' WHERE docid = 5204; 
UPDATE verses_content SET c2text= 'ಅದೇಕೆಂದರೆ ಅವರೆಡೆಗಿರುವ ಸಂದೇಶವಾಹಕರು ಸ್ಪಷ್ಟವಾದ ಪುರಾವೆಗಳೊಂದಿಗೆ ಅವರ ಬಳಿ ಬರುತ್ತಿದ್ದರು ಎಂಬುದರಿಂದಾಗಿದೆ. ಆಗ ಅವರು ಹೇಳಿದರು: ‘ಒಬ್ಬ ಮನುಷ್ಯನು ನಮಗೆ ಮಾರ್ಗದರ್ಶನ ಮಾಡುವುದೇ?’[[ತಮ್ಮಂತಿರುವ ಓರ್ವ ಮನುಷ್ಯನನ್ನು ಮಾರ್ಗದರ್ಶಕನಾಗಿ ಅಂಗೀಕರಿಸಲು ನಮಗೆ ಸಾಧ್ಯವಿಲ್ಲ ಎಂಬುದು ಅವರ ನಿಲುವಾಗಿತ್ತು.]] ತರುವಾಯ ಅವರು ನಿಷೇಧಿಸಿದರು ಮತ್ತು ವಿಮುಖರಾಗಿ ಹೋದರು. ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನಾಗಿರುವನು. ಅಲ್ಲಾಹು ನಿರಾವಲಂಬಿಯೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುವನು.' WHERE docid = 5205; 
UPDATE verses_content SET c2text= 'ತಮ್ಮನ್ನು ಪುನರುತ್ಥಾನಗೊಳಿಸಲಾಗುವುದಿಲ್ಲ ಎಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ವಾದಿಸಿದರು. (ಓ ಪ್ರವಾದಿ ಯವರೇ!) ಹೇಳಿರಿ: ‘ಹೌದು! ನನ್ನ ಪ್ರಭುವಿನ ಮೇಲಾಣೆ! ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮನ್ನು ಪುನರುತ್ಥಾನಗೊಳಿಸ ಲಾಗುವುದು. ತರುವಾಯ ನೀವು ಮಾಡಿರುವುದರ ಬಗ್ಗೆ ನಿಮಗೆ ತಿಳಿಸಿಕೊಡಲಾಗುವುದು. ಅದು ಅಲ್ಲಾಹುವಿನ ಮಟ್ಟಿಗೆ ಅತಿ ಸುಲಭವಾಗಿದೆ’.' WHERE docid = 5206; 
UPDATE verses_content SET c2text= 'ಆದ್ದರಿಂದ ನೀವು ಅಲ್ಲಾಹುವಿನಲ್ಲಿಯೂ, ಅವನ ಸಂದೇಶವಾಹಕರಲ್ಲಿಯೂ, ನಾವು ಅವತೀರ್ಣ ಗೊಳಿಸಿರುವ ಪ್ರಕಾಶದಲ್ಲಿಯೂ ವಿಶ್ವಾಸವಿಡಿರಿ. ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿಯುವವನಾಗಿರುವನು.' WHERE docid = 5207; 
UPDATE verses_content SET c2text= 'ಒಟ್ಟುಗೂಡಿಸಲಾಗುವ ಆ ದಿನಕ್ಕಾಗಿ ಅವನು ನಿಮ್ಮನ್ನು ಒಟ್ಟುಗೂಡಿಸುವ ದಿನ! ಅದು ನಷ್ಟ ಬಹಿರಂಗ ವಾಗುವ ದಿನವಾಗಿದೆ.[[ತಮಗೆ ಉಂಟಾಗಿರುವ ನಷ್ಟದ ಪ್ರಮಾಣವು ಎಷ್ಟು ಅಗಾಧವಾಗಿದೆಯೆಂದು ಅಂತಿಮ ನ್ಯಾಯ ತೀರ್ಮಾನದ ದಿನದಲ್ಲಿ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಸ್ಪಷ್ಟವಾಗಿ ಮನದಟ್ಟಾಗುವುದು.]] ಯಾರು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುವನೋ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗುವನೋ ಅವನ ಪಾಪಗಳನ್ನು ಅವನು (ಅಲ್ಲಾಹು) ಅಳಿಸಿಹಾಕುವನು ಮತ್ತು ತಳಭಾಗದಿಂದ ನದಿಗಳು ಹರಿಯುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಅವನನ್ನು ಪ್ರವೇಶ ಮಾಡಿಸುವನು. ಅದರಲ್ಲಿ (ಸ್ವರ್ಗದಲ್ಲಿ) ಅವರು ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವರು. ಮಹಾ ವಿಜಯವು ಅದೇ ಆಗಿದೆ.' WHERE docid = 5208; 
UPDATE verses_content SET c2text= 'ಅವಿಶ್ವಾಸವಿಟ್ಟವರು ಮತ್ತು ನಮ್ಮ ದೃಷ್ಟಾಂತ ಗಳನ್ನು ನಿಷೇಧಿಸಿದವರಾರೋ ಅವರು ನರಕವಾಸಿಗಳಾ ಗಿರುವರು. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವರು. (ಅವರು) ತಲುಪುವ ಆ ಸ್ಥಳವು ನಿಕೃಷ್ಟವೇ ಆಗಿದೆ.' WHERE docid = 5209; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹುವಿನ ಅಪ್ಪಣೆ ಪ್ರಕಾರವೇ ಹೊರತು ಯಾವುದೇ ವಿಪತ್ತೂ ಬಾಧಿಸಿಲ್ಲ. ಯಾರಾದರೂ ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟರೆ ಅವನ ಹೃದಯವನ್ನು ಅವನು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಮುನ್ನಡೆಸುವನು. ಅಲ್ಲಾಹು ಎಲ್ಲ ವಿಷಯಗಳ ಬಗ್ಗೆಯೂ ಅರಿವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5210; 
UPDATE verses_content SET c2text= 'ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಅನುಸರಿಸಿರಿ ಮತ್ತು ಸಂದೇಶವಾಹಕರನ್ನು ಅನುಸರಿಸಿರಿ. ನೀವು ವಿಮುಖರಾಗು ವುದಾದರೆ ನಮ್ಮ ಸಂದೇಶವಾಹಕರ ಹೊಣೆಯು ಸ್ಪಷ್ಟ ವಾದ ರೀತಿಯಲ್ಲಿ ತಲುಪಿಸಿಕೊಡುವುದು ಮಾತ್ರವಾಗಿದೆ.' WHERE docid = 5211; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು. ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯರಿಲ್ಲ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹುವಿನ ಮೇಲೆ ಭರವಸೆಯಿಡಲಿ.' WHERE docid = 5212; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮ ಪತ್ನಿಯರಲ್ಲಿ ಮತ್ತು ನಿಮ್ಮ ಮಕ್ಕಳಲ್ಲಿ ನಿಮಗೆ ಶತ್ರುಗಳಿರುವರು. ಆದ್ದರಿಂದ ಅವರ ಬಗ್ಗೆ ಎಚ್ಚರವಹಿಸಿರಿ. ನೀವು ಮನ್ನಿಸುವುದಾದರೆ, ವಿನಾಯಿತಿ ತೋರುವುದಾದರೆ ಮತ್ತು ಕ್ಷಮಿಸುವುದಾದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವನು.' WHERE docid = 5213; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಸಂಪತ್ತುಗಳು ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಗಳು ಒಂದು ಪರೀಕ್ಷೆ ಮಾತ್ರವಾಗಿದೆ. ಮಹಾ ಪ್ರತಿಫಲವಿರುವುದು ಅಲ್ಲಾಹುವಿನ ಬಳಿಯಾಗಿದೆ.' WHERE docid = 5214; 
UPDATE verses_content SET c2text= 'ಆದ್ದರಿಂದ ನಿಮಗೆ ಸಾಧ್ಯವಾಗುವಷ್ಟರ ಮಟ್ಟಿಗೆ ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ. ಆಲಿಸಿರಿ, ಅನುಸರಿಸಿರಿ ಮತ್ತು ಸ್ವತಃ ನಿಮಗೇ ಒಳಿತಾಗಿರುವ ವಿಧದಲ್ಲಿ ವ್ಯಯಿಸಿರಿ.[[ಅಲ್ಲಾಹು ಆದೇಶಿಸಿದ ದಾರಿಯಲ್ಲಿ ವ್ಯಯಿಸಿದರೆ ವ್ಯಯಿಸುವ ವ್ಯಕ್ತಿಗೆ ಇಹಲೋಕದಲ್ಲಿ ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ಒಳಿತಾಗಲಿದೆ. ಅಲ್ಲಾಹು ವಿರೋಧಿಸಿದ ದಾರಿಯಲ್ಲಿ ವ್ಯಯಿಸಿದರೆ ಅದು ಅವನಿಗೆ ಎಂದಿಗೂ ಒಳಿತಾಗಿ ಪರಿಣಮಿಸಲಾರದು.]] ಮನಸ್ಸಿನ ಜಿಪುಣತನದಿಂದ ಯಾರನ್ನು ಪಾರು ಮಾಡ ಲಾಗುವುದೋ ಅವರು ಯಶಸ್ವಿಯಾದವರಾಗಿರುವರು.' WHERE docid = 5215; 
UPDATE verses_content SET c2text= 'ನೀವು ಅಲ್ಲಾಹುವಿಗೆ ಉತ್ತಮವಾದ ಸಾಲವನ್ನು ನೀಡುವುದಾದರೆ ಅವನು ಅದನ್ನು ನಿಮಗೆ ಇಮ್ಮಡಿಗೊಳಿಸಿ ಕೊಡುವನು ಮತ್ತು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸುವನು. ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕೃತಜ್ಞನೂ, ಸಹನಶೀಲನೂ ಆಗಿರುವನು.' WHERE docid = 5216; 
UPDATE verses_content SET c2text= 'ಅವನು ಅಗೋಚರವಾಗಿರುವುದನ್ನು ಮತ್ತು ಗೋಚರವಾಗಿರುವುದನ್ನು ಅರಿಯುವವನೂ, ಪ್ರತಾಪ ಶಾಲಿಯೂ, ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5217; 
UPDATE verses_content SET c2text= 'ಓ ಪ್ರವಾದಿಯವರೇ! ನೀವು (ವಿಶ್ವಾಸಿಗಳು) ಸ್ತ್ರೀಯರನ್ನು ವಿಚ್ಛೇದಿಸುವುದಾದರೆ ಅವರನ್ನು ಅವರ ಇದ್ದಃ ಕಾಲಕ್ಕೆ (ಲೆಕ್ಕಹಾಕಿ) ವಿಚ್ಛೇದಿಸಿರಿ ಮತ್ತು ಇದ್ದಃ ಕಾಲವನ್ನು ಎಣಿಸಿಕೊಳ್ಳಿರಿ.[[ಗರ್ಭಿಣಿಯರಲ್ಲದ ವಿಚ್ಛೇದಿತೆಯರ ಇದ್ದಃ ಕಾಲ ಅಥವಾ ಪುನರ್ವಿವಾಹ ಮಾಡದೆ ಅವರು ಕಾಯಬೇಕಾದ ಕಾಲ ಮೂರು ಶುದ್ಧಾವಸ್ಥೆಗಳಾಗಿವೆ. ಲೈಂಗಿಕ ಸಂಬಂಧ ಉಂಟಾಗದ ಒಂದು ಶುದ್ಧಾವಸ್ಥೆಯಲ್ಲಿ ಮಾತ್ರ ಪತಿ ತನ್ನ ಪತ್ನಿಗೆ ವಿಚ್ಛೇದನೆ ನೀಡಬಹುದು. ಋತುಸ್ರಾವದ ಅವಧಿಯಲ್ಲಿ ತಲಾಕ್ ನೀಡಕೂಡದು. ಗರ್ಭಿಣಿಯರ ಇದ್ದಃ ಅವರ ಹೆರಿಗೆಯ ತನಕವಾಗಿದೆ....]] ನಿಮ್ಮ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ. ನೀವು ಅವರನ್ನು ಅವರ ಮನೆಗಳಿಂದ ಹೊರಗೆ ಕಳಿಸದಿರಿ ಮತ್ತು ಸ್ವತಃ ಅವರು ಹೊರಹೋಗದಿರಲಿ.[[ವಿಚ್ಛೇದಿತೆಯು ಇದ್ದಃ ಕಾಲದಲ್ಲಿ ವಿಚ್ಛೇದನ ನೀಡಿದ ಪತಿಯ ಮನೆಯಲ್ಲೇ ತಂಗಿರಬೇಕು. ದಾಂಪತ್ಯವನ್ನು ಪುನಃ ಸ್ಥಾಪಿಸಲು ಮತ್ತು ಅಗಲಿಕೆಯ ನೋವನ್ನು ನಿವಾರಿಸಲು ಇದು ಸಹಾಯಕವಾಗಬಹುದು....]] ಅವರು ಪ್ರತ್ಯಕ್ಷವಾದ ಯಾವು ದಾದರೂ ನೀಚಕೃತ್ಯವನ್ನು ಮಾಡುವ ಹೊರತು. ಇವು ಅಲ್ಲಾಹುವಿನ ನಿಯಮವ್ಯಾಪ್ತಿಗಳಾಗಿವೆ. ಅಲ್ಲಾಹುವಿನ ನಿಯಮವ್ಯಾಪ್ತಿಗಳನ್ನು ಯಾರಾದರೂ ಮೀರುವುದಾದರೆ ಅವನು ಸ್ವತಃ ಅವನ ಮೇಲೆಯೇ ಅನ್ಯಾಯವೆಸಗಿರುವನು. ಇದರ ಬಳಿಕ ಅಲ್ಲಾಹು ಹೊಸದಾಗಿ ಯಾವುದಾದರೂ ವಿಷಯವನ್ನು ತರುವನೋ ಎಂಬುದರ ಬಗ್ಗೆ ತಾವು ಅರಿತಿಲ್ಲ.[[ಜಗಳ ಮತ್ತು ವಿಚ್ಛೇದನೆಯ ನಂತರ ದಂಪತಿಗಳ ಮನಸ್ಸಿನಲ್ಲಿ ವಿಷಾದ ಮತ್ತು ವಿವಾಹ ಸಂಬಂಧವನ್ನು ಪುನಃ ಸ್ಥಾಪಿಸಬೇಕೆಂಬ ಆಸೆಯನ್ನು ಅಲ್ಲಾಹು ಹುಟ್ಟಿಸಬಹುದು. ಅದನ್ನು ಮುಂಚಿತವಾಗಿ ಅರಿತುಕೊಳ್ಳಲು ಮನುಷ್ಯರಿಗೆ ಸಾಧ್ಯವಿಲ್ಲ ಎಂದರ್ಥ.]]' WHERE docid = 5218; 
UPDATE verses_content SET c2text= 'ಅವರು (ವಿಚ್ಛೇದಿತೆಯರು) ತಮ್ಮ ಅವಧಿಯನ್ನು ತಲುಪಿದರೆ ಶಿಷ್ಟಾಚಾರಕ್ಕೆ ಅನುಗುಣವಾಗಿ ಅವರನ್ನು ಬಳಿ ಇರಿಸಿಕೊಳ್ಳಿರಿ ಅಥವಾ ಶಿಷ್ಟಾಚಾರಕ್ಕೆ ಅನುಗುಣವಾಗಿ ಅವರನ್ನು ಕಳುಹಿಸಿಕೊಡಿರಿ. ನಿಮ್ಮಲ್ಲಿನ ಇಬ್ಬರು ನ್ಯಾಯವಂತರನ್ನು ಸಾಕ್ಷಿ ನಿಲ್ಲಿಸಿರಿ. ಅಲ್ಲಾಹುವಿಗೋಸ್ಕರ ಸಾಕ್ಷ್ಯವನ್ನು ಸರಿಯಾದ ವಿಧದಲ್ಲಿ ಸ್ಥಾಪಿಸಿರಿ.[[ಅಗತ್ಯ ಬರುವಾಗ ಸಾಕ್ಷಿಗಳು ನಿಷ್ಪಕ್ಷವಾಗಿ ಸಾಕ್ಷಿ ಹೇಳಬೇಕು ಎಂದರ್ಥ.]] ಅದು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮತ್ತು ಅಂತ್ಯದಿನದಲ್ಲಿ ವಿಶ್ವಾಸವಿಡು ವವರಿಗೆ ನೀಡಲಾಗುವ ಉಪದೇಶವಾಗಿದೆ. ಯಾರಾ ದರೂ ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡುವುದಾದರೆ ಅಲ್ಲಾಹು ಅವನಿಗೆ ಹೊರಹೋಗುವ ಒಂದು ಮಾರ್ಗವನ್ನು ಮಾಡಿಕೊಡುವನು.' WHERE docid = 5219; 
UPDATE verses_content SET c2text= 'ಅವನು ಊಹಿಸಿರದ ವಿಧದಲ್ಲಿ ಅವನಿಗೆ ಅನ್ನಾಧಾರವನ್ನು ಒದಗಿಸುವನು. ಯಾರಾದರೂ ಅಲ್ಲಾಹು ವಿನ ಮೇಲೆ ಭರವಸೆಯಿಡುವುದಾದರೆ ಅವನಿಗೆ ಅಲ್ಲಾಹು ಸಾಕು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ತನ್ನ ಕಾರ್ಯವನ್ನು ಪೂರ್ಣಗೊಳಿಸುವನು. ಪ್ರತಿಯೊಂದು ವಿಷಯಕ್ಕೂ ಅಲ್ಲಾಹು ಒಂದು ಕ್ರಮವನ್ನು ನಿಶ್ಚಯಿಸಿರುವನು.' WHERE docid = 5220; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಸ್ತ್ರೀಯರ ಪೈಕಿ ಋತುಸ್ರಾವದ ಬಗ್ಗೆ ನಿರಾಶರಾಗಿರುವವರ[[ಋತುಸ್ರಾವದ ಪ್ರಾಯವನ್ನು ದಾಟಿದವರು.]] ಬಗ್ಗೆ ಹೇಳುವುದಾದರೆ ಅವರ ಇದ್ದಃದ ವಿಷಯದಲ್ಲಿ ನಿಮಗೆ ಸಂದೇಹವಿದ್ದರೆ, ಅವರ ಇದ್ದಃವು ಮೂರು ತಿಂಗಳುಗಳಾಗಿವೆ. ಋತುಸ್ರಾವವಾ ಗದಿರುವವರ ವಿಷಯವೂ ಇದೇ ರೀತಿಯಾಗಿದೆ. ಗರ್ಭಿಣಿ ಯರ ಬಗ್ಗೆ ಹೇಳುವುದಾದರೆ ಅವರ ಅವಧಿಯು ಅವರು ತಮ್ಮ ಗರ್ಭವನ್ನು ಹೆರುವ ತನಕವಾಗಿದೆ. ಯಾರಾದರೂ ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡುವುದಾದರೆ ಅಲ್ಲಾಹು ಅವನಿಗೆ ಅವನ ವಿಷಯವನ್ನು ಸರಳೀಕರಿಸಿ ಕೊಡುವನು.' WHERE docid = 5221; 
UPDATE verses_content SET c2text= 'ಅದು ಅಲ್ಲಾಹುವಿನ ಆಜ್ಞೆಯಾಗಿದೆ. ಅವನು ಅದನ್ನು ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿರುವನು. ಯಾರಾ ದರೂ ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡುವುದಾದರೆ ಅವನ ಪಾಪಗಳನ್ನು ಅವನು ಅಳಿಸಿಹಾಕುವನು ಮತ್ತು ಅವನಿಗೆ ಪ್ರತಿಫಲವನ್ನು ಹಿಗ್ಗಿಸಿ ಕೊಡುವನು.' WHERE docid = 5222; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಸಾಮರ್ಥ್ಯಕ್ಕೆ ಸೇರಿದ, ನೀವು ವಾಸ ಮಾಡುವ ಸ್ಥಳದಲ್ಲೇ ಅವರನ್ನು ವಾಸಮಾಡಿಸಿರಿ. ಅವರನ್ನು ಇಕ್ಕಟ್ಟುಗೊಳಿಸುವ ಸಲುವಾಗಿ ಅವರಿಗೆ ಕಿರುಕುಳ ಕೊಡದಿರಿ. ಅವರು ಗರ್ಭಿಣಿಯರಾಗಿದ್ದರೆ ಅವರು ಹೆರುವ ತನಕ ಅವರಿಗೆ ಖರ್ಚು ಮಾಡುತ್ತಿರಿ. ಅವರು ನಿಮಗಾಗಿ (ಶಿಶುವಿಗೆ) ಸ್ತನಪಾನ ಮಾಡುವುದಾದರೆ ಅವರಿಗೆ ಅವರ ಪ್ರತಿಫಲವನ್ನು ಕೊಡಿರಿ. ನೀವು ಪರಸ್ಪರ ಶಿಷ್ಟಾಚಾರದೊಂದಿಗೆ ಸಮಾಲೋಚನೆ ಮಾಡಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ಎರಡೂ ಕಡೆಯವರಿಗೂ ಇಕ್ಕಟ್ಟಾಗುವುದಾದರೆ ಅವನಿಗಾಗಿ ಬೇರೊಬ್ಬ ಸ್ತ್ರೀ ಸ್ತನಪಾನ ಮಾಡಲಿ.' WHERE docid = 5223; 
UPDATE verses_content SET c2text= 'ಸಾಮರ್ಥ್ಯವಿರುವವನು ತನ್ನ ಸಾಮರ್ಥ್ಯದಿಂದ ಖರ್ಚಿಗೆ ನೀಡಲಿ. ಯಾರಿಗಾದರೂ ತನ್ನ ಅನ್ನಾಧಾರವು ಇಕ್ಕಟ್ಟಾದರೆ ಅಲ್ಲಾಹು ಅವನಿಗೆ ನೀಡಿರುವುದರಿಂದ ಅವನು ಖರ್ಚಿಗೆ ನೀಡಲಿ. ಅಲ್ಲಾಹು ಅವನಿಗೆ ನೀಡಿರುವುದನ್ನೇ ವಿನಾ (ಕೊಡಬೇಕೆಂದು) ಯಾರನ್ನೂ ಅವನು ಬಲವಂತಪಡಿಸಲಾರನು. ಅಲ್ಲಾಹು ಇಕ್ಕಟ್ಟಿನ ಬಳಿಕ ಅನುಕೂಲತೆಯನ್ನು ಮಾಡಿಕೊಡುವನು.' WHERE docid = 5224; 
UPDATE verses_content SET c2text= 'ಎಷ್ಟೊಂದು ದೇಶದವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರ ಆಜ್ಞೆಯನ್ನು ಧಿಕ್ಕರಿಸಿರುವರು! ಆದ್ದರಿಂದ ನಾವು ಅವರನ್ನು ಕಠೋರವಾಗಿ ವಿಚಾರಣೆ ಮಾಡಿದೆವು ಮತ್ತು ಅವರನ್ನು ಹೀನವಾದ ರೀತಿಯಲ್ಲಿ ಶಿಕ್ಷಿಸಿದೆವು.' WHERE docid = 5225; 
UPDATE verses_content SET c2text= 'ಹೀಗೆ ಅವರು ಅವರ ಕರ್ಮಗಳ ದುಷ್ಫಲವನ್ನು ಆಸ್ವಾದಿಸಿದರು. ಅವರ ಕರ್ಮಗಳ ಪರ್ಯಾವಸಾನವು ನಷ್ಟವೇ ಆಗಿತ್ತು.' WHERE docid = 5226; 
UPDATE verses_content SET c2text= 'ಅಲ್ಲಾಹು ಅವರಿಗೆ ಕಠೋರವಾದ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಪಡಿಸಿರುವನು. ಆದ್ದರಿಂದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ಬುದ್ಧಿವಂತರೇ! ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ನಿಮಗೊಂದು ಸ್ಮರಣೆಯನ್ನು (ಸಂದೇಶ)ವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿರುವನು.' WHERE docid = 5227; 
UPDATE verses_content SET c2text= 'ಅಥವಾ ಅಲ್ಲಾಹುವಿನ ಸ್ಪಷ್ಟವಾದ ದೃಷ್ಟಾಂತ ಗಳನ್ನು ನಿಮಗೆ ಓದಿಕೊಡುವ ಒಬ್ಬ ಸಂದೇಶವಾಹಕರನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ಇಳಿಸಿರುವನು. ವಿಶ್ವಾಸವಿಟ್ಟವರನ್ನು ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿದವರನ್ನು ಅಂಧಕಾರಗಳಿಂದ ಪ್ರಕಾಶದೆಡೆಗೆ ಹೊರತರುವ ಸಲುವಾಗಿ. ಯಾರಾದರೂ ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟು ಸತ್ಕರ್ಮವೆಸಗುವುದಾದರೆ ತಳಭಾಗದಿಂದ ನದಿಗಳು ಹರಿಯುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿಗೆ ಅವನು ಅವನನ್ನು ಪ್ರವೇಶ ಮಾಡಿಸುವನು. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ವಾಸಿಸುವರು. ಅವನಿಗೆ ಅಲ್ಲಾಹು ಅನ್ನಾಧಾರ ವನ್ನು ಉತ್ತಮಗೊಳಿಸಿರುವನು.' WHERE docid = 5228; 
UPDATE verses_content SET c2text= 'ಏಳು ಆಕಾಶಗಳನ್ನು ಮತ್ತು ಭೂಮಿಯಿಂದ ಅದಕ್ಕೆ ಸಮಾನವಾಗಿರುವುದನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಅಲ್ಲಾಹು ವಾಗಿರುವನು.[[ಏಳು ಆಕಾಶಗಳಂತೆ ಏಳು ಭೂಮಿಗಳೂ ಇವೆಯೆಂದು ವ್ಯಾಖ್ಯಾನಕಾರರು ಇದಕ್ಕೆ ಅರ್ಥ ನೀಡಿದ್ದಾರೆ. ಸಹೀಹಾದ ಹದೀಸ್‍ಗಳಲ್ಲೂ ಇದನ್ನು ಬೆಂಬಲಿಸುವ ಪರಾಮರ್ಶೆಗಳನ್ನು ಕಾಣಬಹುದು.]] ಅವುಗಳ ಮಧ್ಯೆ (ಅವನ) ಆಜ್ಞೆಯು ಇಳಿಯುವುದು. ಅಲ್ಲಾಹು ಎಲ್ಲ ವಿಷಯದಲ್ಲೂ ಸಾಮರ್ಥ್ಯ ವುಳ್ಳವನಾಗಿರುವನು ಮತ್ತು ಅವನು ಎಲ್ಲ ವಸ್ತುವನ್ನೂ ಅರಿವಿನೊಂದಿಗೆ ಪೂರ್ಣವಾಗಿ ಆವರಿಸಿರುವನು ಎಂಬುದನ್ನು ನೀವು ಅರಿಯುವ ಸಲುವಾಗಿ.' WHERE docid = 5229; 
UPDATE verses_content SET c2text= 'ಓ ಪ್ರವಾದಿಯವರೇ! ತಾವು ತಮ್ಮ ಪತ್ನಿಯರ ಸಂತೃಪ್ತಿಯನ್ನು ಅರಸುತ್ತಾ, ಅಲ್ಲಾಹು ತಮಗೆ ಧರ್ಮ ಸಮ್ಮತಗೊಳಿಸಿರುವುದನ್ನು ಏಕೆ ನಿಷಿದ್ಧಗೊಳಿಸುತ್ತಿರು ವಿರಿ?[[ಪ್ರವಾದಿಪತ್ನಿಯರು ಉನ್ನತವಾದ ಧಾರ್ಮಿಕ ಮಾನದಂಡಗಳನ್ನು ಪಾಲಿಸುವವರಾಗಿದ್ದರೂ ಸ್ತ್ರೀ ಸಹಜವಾದ ಕೆಲವು ಬಲಹೀನತೆಗಳು ಅವರಲ್ಲಿದ್ದವು. ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯರಿಗೆ ಇತರ ಪತ್ನಿಯರಿಗಿಂತಲೂ ಹೆಚ್ಚು ಪ್ರೀತಿ ತನ್ನೊಂದಿಗೆ ಇರಬೇಕೆಂದು ಅವರ ಪೈಕಿ ಕೆಲವರು ಆಸೆಪಟ್ಟಿದ್ದರು. ಒಮ್ಮೆ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ತಮ್ಮ ಪತ್ನಿಯರಲ್ಲಿ ಒಬ್ಬರಾದ ಝೈನಬ್ ಬಿನ್ತ್ ಜಹ್‍ಶ್‍ರವರ ಮನೆಯಲ್ಲಿ ಸ್ವಲ್ಪ ಜೇನು ಸವಿದರು. ಹೇಗೋ ಈ ಮಾಹಿತಿಯನ್ನು ಪಡೆದ ಆಇಶಾ(رضي الله عنها) ಮತ್ತು ಹಫ್ಸಾ(رضي الله عنها)ರಿಗೆ ಅದು ಅಷ್ಟೊಂದು ಹಿಡಿಸಲಿಲ್ಲ. ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ತಮ್ಮ ಬಳಿಗೆ ಬಂದಾಗ ತಾವು ಮಆಫಿರ್ ಅಂಟನ್ನು ಜಗಿದಿದ್ದೀರಿ, ತಮ್ಮ ಬಾಯಿ ವಾಸನೆ ಬರುತ್ತಿದೆ ಎಂದು ಹೇಳಬೇಕೆಂದು ಅವರಿಬ್ಬರೂ ತೀರ್ಮಾನಿಸಿದರು. ತಾನು ಸ್ವಲ್ಪ ಜೇನನ್ನು ಸವಿದಿರುವೆನೆಂದು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಅವರಿಗೆ ಹೇಳಿದರು. ತರುವಾಯ ತಮ್ಮ ಪತ್ನಿಯರಿಗೆ ಇಷ್ಟವಿಲ್ಲದ ಜೇನನ್ನು ತಾನಿನ್ನು ಸೇವಿಸುವುದಿಲ್ಲವೆಂದು ಅವರು ಶಪಥ ಮಾಡಿದರು. ಅಲ್ಲಾಹು ಧರ್ಮಸಮ್ಮತಗೊಳಿಸಿದ ಜೇನನ್ನು ಪತ್ನಿಯರ ಹಿತಾಸಕ್ತಿಯನ್ನು ಗೌರವಿಸಿ ವರ್ಜಿಸುವುದು ಸರಿಯಲ್ಲವೆಂದು ಅಲ್ಲಾಹು ಈ ಸೂಕ್ತಿಯ ಮೂಲಕ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರನ್ನು ಎಚ್ಚರಿಸಿದ್ದಾನೆ.]] ಅಲ್ಲಾಹು ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಅಪಾರ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವನು.' WHERE docid = 5230; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಶಪಥಗಳಿಗಿರುವ ಪರಿಹಾರವನ್ನು[[ಒಂದು ಸತ್ಕರ್ಮವನ್ನಾಗಲಿ, ಧರ್ಮಸಮ್ಮತವಾದ ಒಂದು ಕರ್ಮವನ್ನಾಗಲಿ ಮಾಡುವುದಿಲ್ಲವೆಂದು ಅಲ್ಲಾಹುವಿನ ಹೆಸರಲ್ಲಿ ಶಪಥ ಮಾಡಕೂಡದು. ಯಾರಾದರೂ ಹಾಗೆ ಮಾಡಿದರೆ ಆ ಶಪಥವನ್ನು ಉಲ್ಲಂಘಿಸಿ ಅದಕ್ಕೆ ಪ್ರಾಯಶ್ಚಿತ್ತ ನೀಡಬೇಕು.]] ಅಲ್ಲಾಹು ನಿಮಗೆ ನಿಯಮವನ್ನಾಗಿ ಮಾಡಿರುವನು. ಅಲ್ಲಾಹು ನಿಮ್ಮ ಒಡೆಯನಾಗಿರುವನು. ಅವನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.' WHERE docid = 5231; 
UPDATE verses_content SET c2text= 'ಪ್ರವಾದಿಯವರು ತನ್ನ ಪತ್ನಿಯರ ಪೈಕಿ ಒಬ್ಬಳೊಂದಿಗೆ ಒಂದು ಮಾತನ್ನು ಗುಟ್ಟಾಗಿ ಹೇಳಿದ ಸಂದರ್ಭ. ತರುವಾಯ ಆಕೆ ಅದನ್ನು (ಇನ್ನೊಬ್ಬಳಿಗೆ) ತಿಳಿಸಿದರು.[[ತಾನು ಇನ್ನು ಮೇಲೆ ಜೇನು ಸೇವಿಸುವುದಿಲ್ಲ ಎಂದು ಶಪಥ ಮಾಡಿದ ವಿಷಯ ಹಾಗೂ ಇನ್ನಿತರ ವಿಷಯಗಳನ್ನು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಪತ್ನಿಯರ ಪೈಕಿ ಓರ್ವರಾದ ಹಫ್ಸಾರಿಗೆ ತಿಳಿಸಿದ್ದರು. ಇದನ್ನು ಇತರ ಯಾರಿಗೂ ತಿಳಿಸಕೂಡದೆಂದು ಅವರು ಹಫ್ಸಾ(رضي الله عنها)ರಿಗೆ ಆದೇಶಿಸಿದ್ದರು. ಆದರೆ ಅಷ್ಟೊಂದು ಗಂಭೀರ ವಿಷಯವಲ್ಲವೆಂಬ ಭಾವನೆಯಿಂದ ಹಫ್ಸಾ(رضي الله عنها) ಇದನ್ನು ಆಇಶಾರಿಗೆ ತಿಳಿಸಿದ್ದರು.]] ಅಲ್ಲಾಹು ಅದನ್ನು ಅವರಿಗೆ (ಪ್ರವಾದಿಗೆ) ಬಹಿರಂಗಪಡಿಸಿದಾಗ ಅವರು ಅದರ ಕೆಲವು ಭಾಗವನ್ನು (ಆ ಪತ್ನಿಗೆ) ತಿಳಿಸಿದರು ಮತ್ತು ಕೆಲವು ಭಾಗವನ್ನು ಬಿಟ್ಟರು. ತರುವಾಯ ಆಕೆಯೊಂದಿಗೆ ಅವರು ಅದನ್ನು ತಿಳಿಸಿದಾಗ ಆಕೆ (ಪತ್ನಿ) ಕೇಳಿದರು: ‘ತಮಗೆ ಇದನ್ನು ತಿಳಿಸಿಕೊಟ್ಟವರು ಯಾರು?’ ಅವರು ಹೇಳಿದರು: ‘ಸರ್ವಜ್ಞನೂ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯೂ ಆಗಿರುವವನು ನನಗೆ ತಿಳಿಸಿಕೊಟ್ಟಿರುವನು’.' WHERE docid = 5232; 
UPDATE verses_content SET c2text= 'ನೀವಿಬ್ಬರೂ[[ಪ್ರವಾದಿಪತ್ನಿಯರಾದ ಆಇಶಾ (رضي الله عنه)ಮತ್ತು ಹಫ್ಸಾ (رضي الله عنه)ರ ಬಗ್ಗೆ ಸೂಚನೆ. ಯಾವುದೇ ಸಣ್ಣ ವಿಷಯದಲ್ಲಾದರೂ ಸರಿ, ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರ ಆದೇಶವನ್ನು ಅವರ ಪತ್ನಿಯರು ಸುತರಾಂ ಪಾಲಿಸಬೇಕಾಗಿದೆ. ಇದರಲ್ಲಿರುವ ಚಿಕ್ಕ ಪ್ರಮಾದವನ್ನೂ ಅಲ್ಲಾಹು ಗಂಭೀರವಾಗಿ ಪರಿಗಣಿಸುತ್ತಾನೆ.]] ಅಲ್ಲಾಹುವಿನೆಡೆಗೆ ಪಶ್ಚಾತ್ತಾಪ ಪಟ್ಟು ಮರಳುವುದಾದರೆ (ಹಾಗೆ ಮಾಡಿರಿ). ಯಾಕೆಂದರೆ ನಿಮ್ಮಿಬ್ಬರ ಹೃದಯಗಳೂ (ಕೆಡುಕಿನೆಡೆಗೆ) ವಾಲಿಕೊಂಡಿವೆ. ಇನ್ನು ನೀವಿಬ್ಬರೂ ಅವರ (ಪ್ರವಾದಿಯ) ವಿರುದ್ಧ ಪರಸ್ಪರ ಸಹಕರಿಸುವುದಾದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ಅವರ ಒಡೆಯನಾಗಿರುವನು. ಜಿಬ್ರೀಲ್ ಮತ್ತು ಸಜ್ಜನರಾದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೂ, ಅದರ ಜೊತೆಗೆ ಮಲಕ್ ಗ ಳೂ ಅವರ ಸಹಾಯಕರಾಗಿರುವರು.' WHERE docid = 5233; 
UPDATE verses_content SET c2text= '(ಓ ಪ್ರವಾದಿಪತ್ನಿಯರೇ!) ಅವರು ನಿಮ್ಮನ್ನು ವಿಚ್ಛೇದಿಸುವುದಾದರೆ ನಿಮಗಿಂತಲೂ ಉತ್ತಮರಾದ ಪತ್ನಿಯರನ್ನು ಅವರ ಪ್ರಭು ಅವರಿಗೆ ಬದಲಿಯಾಗಿ ನೀಡಲೂಬಹುದು. ಮುಸ್ಲಿಮರೂ, ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರೂ, ಭಯಭಕ್ತಿ ಪಾಲಿಸುವವರೂ, ಪಶ್ಚಾತ್ತಾಪಪಡುವವರೂ, ಆರಾಧನಾ ಮಗ್ನರೂ, ಉಪವಾಸ ಆಚರಿಸುವವರೂ, ವಿಧವೆಗಳೂ, ಕನ್ಯೆಯರೂ ಆಗಿರುವವರನ್ನು.' WHERE docid = 5234; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಮನುಷ್ಯರನ್ನು ಮತ್ತು ಕಲ್ಲುಗಳನ್ನು ಇಂಧನವಾಗಿ ಬಳಸುವ ನರಕಾಗ್ನಿಯಿಂದ ಸ್ವತಃ ನಿಮ್ಮನ್ನೂ, ನಿಮ್ಮ ಕುಟುಂಬದವರನ್ನೂ ರಕ್ಷಿಸಿರಿ. ಅದರ ಮೇಲ್ನೋಟ ವಹಿಸಲು ಕಠೋರ ಸ್ವಭಾವದವರೂ, ಅತಿ ಬಲಿಷ್ಠರೂ ಆಗಿರುವ ಮಲಕ್ ಗಳಿರುವರು. ಅಲ್ಲಾಹು ಅವರೊಂದಿಗೆ ಆಜ್ಞಾಪಿಸಿದ ಯಾವುದೇ ವಿಷಯವನ್ನೂ ಅವರು ಧಿಕ್ಕರಿಸಲಾರರು. ಅವರೊಂದಿಗೆ ಆಜ್ಞಾಪಿಸಲಾಗುವ ಯಾವುದೇ ಆಜ್ಞೆಯನ್ನೂ ಅವರು ಪಾಲಿಸುವರು.' WHERE docid = 5235; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯನಿಷೇಧಿಗಳೇ! ಇಂದು ನೀವು ನೆಪಗಳನ್ನು ಹೇಳದಿರಿ. ನಿಮಗೆ ಪ್ರತಿಫಲ ನೀಡಲಾಗುವುದು ನೀವು ಮಾಡಿರುವುದಕ್ಕೆ ಮಾತ್ರವಾಗಿದೆ.[[ನರಕಾಗ್ನಿಗೆ ಪ್ರವೇಶ ಮಾಡಿಸಲಾಗುವ ಸಮಯದಲ್ಲಿ ಸತ್ಯನಿಷೇಧಿಗಳೊಂದಿಗೆ ಹೀಗೆನ್ನಲಾಗುವುದು.]]' WHERE docid = 5236; 
UPDATE verses_content SET c2text= 'ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹುವಿನೆಡೆಗೆ ನಿಷ್ಕಳಂಕವಾದ ಪಶ್ಚಾತ್ತಾಪದೊಂದಿಗೆ ಮರಳಿರಿ. ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಅಳಿಸಿಹಾಕಬಹುದು ಮತ್ತು ತಳಭಾಗದಿಂದ ನದಿಗಳು ಹರಿಯುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನು ಪ್ರವೇಶ ಮಾಡಿಸಬಹುದು. ಅಲ್ಲಾಹು ಪ್ರವಾದಿ ಯನ್ನು ಮತ್ತು ಅವರೊಂದಿಗೆ ವಿಶ್ವಾಸವಿಟ್ಟವರನ್ನು ಅಪಮಾನಗೊಳಿಸದ ದಿನ! ಅವರ ಪ್ರಕಾಶವು ಅವರ ಮುಂಭಾಗದಿಂದಲೂ, ಬಲಭಾಗದಿಂದಲೂ ಚಲಿಸುತ್ತಿರು ವುದು. ಅವರು ಹೇಳುವರು: ‘ನಮ್ಮ ಪ್ರಭೂ! ನಮ್ಮ ಪ್ರಕಾಶವನ್ನು ನಮಗೆ ಪೂರ್ಣಗೊಳಿಸಿ ಕೊಡು ಮತ್ತು ನಮಗೆ ಕ್ಷಮೆ ನೀಡು. ಖಂಡಿತವಾಗಿಯೂ ನೀನು ಎಲ್ಲ ವಿಷಯಗಳಲ್ಲೂ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನಾಗಿರುವೆ’.' WHERE docid = 5237; 
UPDATE verses_content SET c2text= 'ಓ ಪ್ರವಾದಿಯವರೇ! ತಾವು ಸತ್ಯನಿಷೇಧಿಗ ಳೊಂದಿಗೆ ಮತ್ತು ಕಪಟವಿಶ್ವಾಸಿಗಳೊಂದಿಗೆ ಹೋರಾಡಿರಿ. ಅವರೊಂದಿಗೆ ಕಠೋರವಾಗಿ ವರ್ತಿಸಿರಿ. ಅವರ ವಾಸಸ್ಥಳವು ನರಕಾಗ್ನಿಯಾಗಿದೆ. ತಲುಪಲಿರುವ ಆ ಸ್ಥಳ ಎಷ್ಟು ನಿಕೃಷ್ಟವಾದುದು!' WHERE docid = 5238; 
UPDATE verses_content SET c2text= 'ನೂಹ್ ರ ಪತ್ನಿಯನ್ನು ಮತ್ತು ಲೂತ್ ರ ಪತ್ನಿಯನ್ನು ಅಲ್ಲಾಹು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಉದಾಹರಣೆ ಯಾಗಿ ತೋರಿಸಿರುವನು. ಅವರಿಬ್ಬರೂ ನಮ್ಮ ದಾಸರ ಪೈಕಿ ಸಜ್ಜನರಾಗಿದ್ದ ಇಬ್ಬರು ದಾಸರ ಕೆಳಗಿದ್ದರು. ತರುವಾಯ ಅವರಿಬ್ಬರನ್ನೂ ಇವರು (ಪತ್ನಿಯರು) ವಂಚಿಸಿದರು. ಆಗ ಅಲ್ಲಾಹುವಿನ ಶಿಕ್ಷೆಯಿಂದ ಏನನ್ನೂ ಅವರಿಬ್ಬರೂ ಇವರಿಗೆ (ಪತ್ನಿಯರಿಗೆ) ನಿವಾರಿಸಿಕೊಡಲಿಲ್ಲ.[[ಇಬ್ಬರು ಪ್ರವಾದಿಗಳ ಪತ್ನಿಯರಾಗಿದ್ದೂ ಅಲ್ಲಾಹುವಿನ ಶಿಕ್ಷೆಯಿಂದ ಪಾರುಗೊಳಿಸಲು ಆ ದಾಂಪತ್ಯ ಸಂಬಂಧವು ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಲಿಲ್ಲ. ಅದೇ ರೀತಿ ಮುಹಮ್ಮದ್‍  (صلى الله عليه وسلم)ರವರೊಂದಿಗೆ ಕುಟುಂಬ ಸಂಬಂಧವಿದೆ ಎಂಬ ಹೆಸರಿನಲ್ಲಿ ಪರಲೋಕ ಮೋಕ್ಷ ಸಿಗಲಾರದು. ಅವರು ವಿಶ್ವಾಸವಿಟ್ಟು ಸತ್ಕರ್ಮವೆಸಗಿದವರಾಗಿರುವ ಹೊರತು.]] ‘ನರಕಾಗ್ನಿಯನ್ನು ಪ್ರವೇಶಿಸುವ ಜನರೊಂದಿಗೆ ನೀವಿಬ್ಬರೂ ಪ್ರವೇಶಿಸಿರಿ’ ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲಾಯಿತು.' WHERE docid = 5239; 
UPDATE verses_content SET c2text= 'ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಉಪಮೆಯಾಗಿ ಅಲ್ಲಾಹು ಫಿರ್ಔನನ ಪತ್ನಿಯನ್ನು ತೋರಿಸಿರುವನು.[[ಮೂಸಾ (عليه السلام)ರವರ ವಿರೋಧಿಯೂ ನಿರಂಕುಶ ಸರ್ವಾಧಿಕಾರಿಯೂ ಆಗಿದ್ದ ಫಿರ್‍ಔನ್‍ನ ಪತ್ನಿಯು (ವ್ಯಾಖ್ಯಾನಕಾರರ ಪ್ರಕಾರ ಆ ಮಹಿಳೆಯ ಹೆಸರು ಆಸಿಯಾ ಎಂದಾಗಿದೆ) ಮೂಸಾ (عليه السلام)ರವರ ಸಂದೇಶದಲ್ಲಿ ದೃಢವಾಗಿ ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದರು. ಅವರು ವಿಶ್ವಾಸಿನಿಯಾದ ಮಾಹಿತಿ ಪಡೆದ ಫಿರ್‍ಔನ್ ಅವರನ್ನು ಕ್ರೂರ ಹಿಂಸೆಗೊಳಪಡಿಸಿದ್ದನು.]] ಆಕೆ ಹೇಳಿದ ಸಂದರ್ಭ: ‘ನನ್ನ ಪ್ರಭೂ! ನನಗೆ ನಿನ್ನ ಬಳಿ ಸ್ವರ್ಗದಲ್ಲಿ ಒಂದು ಭವನವನ್ನು ನಿರ್ಮಿಸಿಕೊಡು. ಫಿರ್ ಔನ್  ಮತ್ತು ಅವನ ಕೃತ್ಯಗಳಿಂದ ನನ್ನನ್ನು ಕಾಪಾಡು. ಅಕ್ರಮಿಗಳಾದ ಜನರಿಂದಲೂ ನನ್ನನ್ನು ರಕ್ಷಿಸು’.' WHERE docid = 5240; 
UPDATE verses_content SET c2text= 'ತನ್ನ ಗುಪ್ತಾಂಗವನ್ನು ಸಂರಕ್ಷಿಸಿದ ಇಮ್ರಾನ್ ರ ಮಗಳಾದ ಮರ್ಯಮ್ ರನ್ನೂ (ಉಪಮೆಯಾಗಿ ತೋರಿಸಿರುವನು). ಆಗ ನಮ್ಮ ವತಿಯ ಆತ್ಮದಿಂದ[[ಅಲ್ಲಾಹುವಿನ ವತಿಯ ಆತ್ಮ ಎಂದರೆ ಅಲ್ಲಾಹುವಿನ ಸೃಷ್ಟಿಗಳಾದ ಆತ್ಮಗಳ ಪೈಕಿ ಒಂದು ಆತ್ಮ ಎಂದಾಗಿದೆ. ಆತ್ಮಗಳೆಲ್ಲವೂ ಅಲ್ಲಾಹುವಿನ ಸೃಷ್ಟಿಗಳಾಗಿವೆ. ಇಲ್ಲಿ ಆತ್ಮವನ್ನು ಅಲ್ಲಾಹುವಿನೊಂದಿಗೆ ಸೇರಿಸಿ ಹೇಳಲಾಗಿರುವುದು ಅದನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಅಲ್ಲಾಹುವಾಗಿರುವನು ಎಂಬ ಅರ್ಥದಲ್ಲಾಗಿದೆ.]] ನಾವು ಅದರಲ್ಲಿ ಊದಿದೆವು. ತನ್ನ ಪ್ರಭುವಿನ ವಚನಗಳಲ್ಲಿ ಮತ್ತು ಗ್ರಂಥಗಳಲ್ಲಿ ಅವರು (ಮರ್ಯಮ್) ವಿಶ್ವಾಸವಿಟ್ಟಿರುವರು ಮತ್ತು ಅವರು ಭಯಭಕ್ತಿ ಪಾಲಿಸುವವರಲ್ಲಿ ಸೇರಿದವರಾಗಿದ್ದರು.' WHERE docid = 5241; 
UPDATE verses_content SET c2text= 'ಆಧಿಪತ್ಯವು ಯಾರ ಕೈಯ್ಯಲ್ಲಿದೆಯೋ ಅವನು ಅನುಗ್ರಹಪೂರ್ಣನಾಗಿರುವನು. ಅವನು ಎಲ್ಲ ವಿಷಯ ಗಳಲ್ಲೂ ಸಾಮರ್ಥ್ಯವುಳ್ಳವನಾಗಿರುವನು.' WHERE docid = 5242; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮಲ್ಲಿ ಹೆಚ್ಚು ಉತ್ತಮವಾಗಿ ಕರ್ಮವೆಸಗು ವವರು ಯಾರು ಎಂದು ಪರೀಕ್ಷಿಸುವ ಸಲುವಾಗಿ ಅವನು ಮರಣ ಮತ್ತು ಜೀವನವನ್ನು ಸೃಷ್ಟಿಸಿರುವನು. ಅವನು ಪ್ರತಾಪಶಾಲಿಯೂ ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ ಆಗಿರುವನು.' WHERE docid = 5243; 
UPDATE verses_content SET c2text= 'ಅವನು ಏಳು ಆಕಾಶಗಳನ್ನು ಪದರಗಳಾಗಿ ಸೃಷ್ಟಿಸಿದವನಾಗಿರುವನು. ಪರಮ ದಯಾಮಯನ ಸೃಷ್ಟಿಯಲ್ಲಿ ಯಾವುದೇ ನ್ಯೂನತೆಯನ್ನೂ ತಾವು ಕಾಣಲಾರಿರಿ. ಆದರೆ ತಾವು ದೃಷ್ಟಿಯನ್ನು ಮತ್ತೊಮ್ಮೆ ಮರಳಿಸಿರಿ. ಯಾವುದಾದರೂ ಬಿರುಕನ್ನು ತಾವು ಕಾಣುತ್ತಿರುವಿರಾ?[[ಉಪರಿಲೋಕದ -ಮಹಾ ಪ್ರಪಂಚದ- ರಚನೆಯ ಬಗ್ಗೆ ಮನುಷ್ಯನಿಗೆ ಸ್ವಲ್ಪ ಮಾತ್ರ ಅರಿತುಕೊಳ್ಳಲು ಸಾಧ್ಯವಾಗಿದೆ. ನಮಗೆ ಲಭ್ಯವಾಗಿರುವ ಅರಿವಿನ ಪ್ರಕಾರ ಆಕಾಶಕಾಯಗಳ ಸ್ಥಾನ, ಚಲನೆ ಇತ್ಯಾದಿಗಳೆಲ್ಲವೂ ವ್ಯವಸ್ಥಿತವಾಗಿವೆ, ಎಲ್ಲಿಯೂ ಯಾವುದೇ ನ್ಯೂನತೆಯೂ ಗೋಚರವಲ್ಲ ಎಂದು ಸ್ಪಷ್ಟವಾಗಿ ಹೇಳಬಹುದಾಗಿದೆ.]]' WHERE docid = 5244; 
UPDATE verses_content SET c2text= 'ತರುವಾಯ ಎರಡು ಬಾರಿ ತಾವು ದೃಷ್ಟಿಯನ್ನು ಮರಳಿಸಿರಿ. ಆ ಕಣ್ಣು ತಮ್ಮ ಬಳಿಗೆ ಪರಾಭವಗೊಂಡ ಸ್ಥಿತಿಯಲ್ಲಿ ನಿತ್ರಾಣದೊಂದಿಗೆ ಮರಳುವುದು.' WHERE docid = 5245; 
UPDATE verses_content SET c2text= 'ಅತಿ ಸಮೀಪದ ಆಕಾಶವನ್ನು ನಾವು ಕೆಲವು ದೀಪಗಳಿಂದ ಅಲಂಕರಿಸಿರುವೆವು.[[ಒಂದನೇ ಆಕಾಶವೇ ಅಸಂಖ್ಯಾತ ನಕ್ಷತ್ರಗಳನ್ನು ಒಳಗೊಂಡಿದೆಯೆಂದು ಈ ಸೂಕ್ತಿಯು ಸೂಚಿಸುತ್ತದೆ....]] ಸೈತಾನರನ್ನು ಎಸೆದು ಓಡಿಸುವಂತದ್ದಾಗಿ ನಾವು ಅವುಗಳನ್ನು ಮಾಡಿರುವೆವು.[[ಇದರ ತಾತ್ಪರ್ಯ ಉಲ್ಕೆಗಳಾಗಿವೆಯೆಂದು ಕೆಲವು ವ್ಯಾಖ್ಯಾನಕಾರರು ಅಭಿಪ್ರಾಯಪಟ್ಟಿದ್ದಾರೆ. ತಫ್ಸೀರ್ ಗ್ರಂಥಗಳಲ್ಲಿ ಬೇರೆ ವ್ಯಾಖ್ಯಾನಗಳನ್ನೂ ಕಾಣಬಹುದು. ಇಂತಹ ವಿಷಯಗಳಲ್ಲಿ ಅಲ್ಲಾಹು ತಿಳಿಸಿಕೊಟ್ಟಿರುವುದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ಏನನ್ನೂ ಖಚಿತವಾಗಿ ಹೇಳಲು ನಮಗೆ ಸಾಧ್ಯವಿಲ್ಲ.]] ಅವರಿಗೆ ನಾವು ಜ್ವಲಿಸುವ ನರಕ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಗೊಳಿಸಿ ರುವೆವು.' WHERE docid = 5246; 
UPDATE verses_content SET c2text= 'ತಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ಅವಿಶ್ವಾಸವಿಟ್ಟವರಿಗೆ ನರಕ ಶಿಕ್ಷೆಯಿದೆ. ತಲುಪಲಿರುವ ಆ ಸ್ಥಳವು ನಿಕೃಷ್ಟವೇ ಆಗಿದೆ.' WHERE docid = 5247; 
UPDATE verses_content SET c2text= 'ಅವರನ್ನು ಅದಕ್ಕೆ (ನರಕಾಗ್ನಿಗೆ) ಎಸೆಯಲಾದಾಗ ಅವರು ಅದರ ಒಂದು ಗರ್ಜನೆಯನ್ನು ಆಲಿಸುವರು. ಅದು ಕೊತಕೊತನೆ ಕುದಿಯುತ್ತಿರುವುದು.' WHERE docid = 5248; 
UPDATE verses_content SET c2text= 'ಕ್ರೋಧದಿಂದಾಗಿ ಅದು ಸಿಡಿದು ಹೋಗುವಂತಿ ರುವುದು. ಒಂದೊಂದು ಗುಂಪನ್ನು ಅದರೊಳಗೆ (ನರಕದೊಳಗೆ) ಎಸೆಯಲಾಗುವಾಗಲೆಲ್ಲ ಅದರ ಕಾವಲು ಗಾರರು ಅವರೊಂದಿಗೆ ಕೇಳುವರು: ‘ನಿಮ್ಮ ಬಳಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡುವವರು ಬಂದಿರಲಿಲ್ಲವೇ?’' WHERE docid = 5249; 
UPDATE verses_content SET c2text= 'ಅವರು ಹೇಳುವರು: ‘ಹೌದು! ನಮ್ಮ ಬಳಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡುವವರು ಬಂದಿದ್ದರು. ಆಗ ನಾವು ನಿಷೇಧಿಸಿದೆವು ಮತ್ತು ಅಲ್ಲಾಹು ಏನನ್ನೂ ಅವತೀರ್ಣಗೊಳಿಸಿಲ್ಲ, ನೀವು ಮಹಾ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲೇ ಇರುವಿರಿ[[ಇಹಲೋಕದಲ್ಲಿ ಸತ್ಯಧರ್ಮದ ವಕ್ತಾರರೊಂದಿಗೆ ಅವರು ಹೇಳುತ್ತಿದ್ದ ಮಾತುಗಳನ್ನು ನರಕಾಗ್ನಿಯ ಕಾವಲುಗಾರರೊಂದಿಗೆ ಅವರು ಹೇಳುವರು.]] ಎಂದು ನಾವು ಹೇಳಿದೆವು’.' WHERE docid = 5250; 
UPDATE verses_content SET c2text= '‘ನಾವು ಆಲಿಸುವುದಾಗಲಿ, ಚಿಂತಿಸುವುದಾಗಲಿ ಮಾಡಿರುತ್ತಿದ್ದರೆ ನಾವು ಜ್ವಲಿಸುವ ನರಕಾಗ್ನಿಯ ನಿವಾಸಿಗಳಲ್ಲಿ ಸೇರಿದವರಾಗುತ್ತಿರಲಿಲ್ಲ’ ಎಂದು ಅವರು ಹೇಳುವರು.' WHERE docid = 5251; 
UPDATE verses_content SET c2text= 'ಹೀಗೆ ಅವರು ತಮ್ಮ ತಪ್ಪನ್ನು ಒಪ್ಪಿಕೊಳ್ಳುವರು. ಆದ್ದರಿಂದ ಆ ನರಕವಾಸಿಗಳ ಮೇಲೆ ಶಾಪವಿರಲಿ.' WHERE docid = 5252; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ತಮ್ಮ ಪ್ರಭುವನ್ನು ಅಗೋಚರ ಸ್ಥಿತಿಯಲ್ಲಿ ಭಯಪಡುವವರಾರೋ ಅವರಿಗೆ ಪಾಪಮುಕ್ತಿ ಮತ್ತು ಮಹಾ ಪ್ರತಿಫಲವಿದೆ.' WHERE docid = 5253; 
UPDATE verses_content SET c2text= 'ನೀವು ನಿಮ್ಮ ಮಾತನ್ನು ಗುಟ್ಟಾಗಿರಿಸಿರಿ ಅಥವಾ ಬಹಿರಂಗಪಡಿಸಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅವನು (ಅಲ್ಲಾಹು) ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಅರಿಯುವವನಾಗಿರುವನು.' WHERE docid = 5254; 
UPDATE verses_content SET c2text= 'ಸೃಷ್ಟಿಸಿದವನು (ಎಲ್ಲವನ್ನೂ) ಅರಿಯಲಾರನೇ? ಅವನು ನಿಗೂಢ ರಹಸ್ಯಗಳನ್ನು ಅರಿಯುವವನೂ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯೂ ಆಗಿರುವನು.' WHERE docid = 5255; 
UPDATE verses_content SET c2text= 'ನಿಮಗೋಸ್ಕರ ಭೂಮಿಯನ್ನು ವಿಧೇಯಗೊಳಿಸಿ ಕೊಟ್ಟವನು ಅವನಾಗಿರುವನು. ಆದ್ದರಿಂದ ನೀವು ಅದರ ಭುಜಗಳ ಮೇಲೆ ನಡೆಯಿರಿ ಮತ್ತು ಅವನು ಒದಗಿಸಿದ ಅನ್ನಾಧಾರದಿಂದ ತಿನ್ನಿರಿ. ಪುನರುತ್ಥಾನವು ಅವನೆಡೆಗೇ ಆಗಿದೆ.' WHERE docid = 5256; 
UPDATE verses_content SET c2text= 'ಆಕಾಶದಲ್ಲಿರುವವನು ನಿಮ್ಮನ್ನು ಭೂಮಿಯಲ್ಲಿ ಹುದುಗಿಸುವುದರ ಬಗ್ಗೆ ನೀವು ನಿರ್ಭೀತರಾಗಿರುವಿರಾ? ಆಗ ಅದು (ಭೂಮಿ) ಓಲಾಡುತ್ತಿರುವುದು.' WHERE docid = 5257; 
UPDATE verses_content SET c2text= 'ಅಥವಾ, ಆಕಾಶದಲ್ಲಿರುವವನು ನಿಮ್ಮ ಮೇಲೆ ಹರಳು ಕಲ್ಲುಗಳ ಮಳೆಯನ್ನು ಕಳುಹಿಸುವುದರ ಬಗ್ಗೆ ನೀವು ನಿರ್ಭೀತರಾಗಿರುವಿರಾ? ನನ್ನ ಎಚ್ಚರಿಕೆ ಹೇಗಿದೆಯೆಂದು ನೀವು ತರುವಾಯ ಅರಿತುಕೊಳ್ಳುವಿರಿ.' WHERE docid = 5258; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ಅವರಿಗಿಂತ ಮುಂಚಿನ ವರೂ ನಿಷೇಧಿಸಿರುವರು. ಆಗ ನನ್ನ ಪ್ರತಿಭಟನೆ ಹೇಗಿತ್ತು?' WHERE docid = 5259; 
UPDATE verses_content SET c2text= 'ಅವರ ಮೇಲ್ಭಾಗದಲ್ಲಿ ರೆಕ್ಕೆಯನ್ನು ಹರಡುತ್ತ ಲೂ, ಮಡಚುತ್ತಲೂ ಹಾರಾಡುವ ಹಕ್ಕಿಗಳೆಡೆಗೆ ಅವರು ನೋಡಿಲ್ಲವೇ? ಪರಮ ದಯಾಮಯನ ಹೊರತು (ಇನ್ನಾರೂ) ಅವುಗಳನ್ನು ಆಧರಿಸಿ ಹಿಡಿಯಲಾರರು. ಖಂಡಿತವಾಗಿಯೂ ಅವನು ಎಲ್ಲ ವಿಷಯಗಳನ್ನೂ ವೀಕ್ಷಿಸುವವನಾಗಿರುವನು.' WHERE docid = 5260; 
UPDATE verses_content SET c2text= 'ಅಥವಾ, ಪರಮ ದಯಾಮಯನ ಹೊರತು ನಿಮಗೆ ಸಹಾಯ ಮಾಡಲು ಒಂದು ಸೈನ್ಯವಾಗಿರುವವನು ಯಾರಿರುವನು? ಸತ್ಯನಿಷೇಧಿಗಳು ವಂಚನೆಯಲ್ಲಿಯೇ ಒಳಪಟ್ಟಿರುವರು.' WHERE docid = 5261; 
UPDATE verses_content SET c2text= 'ಅಥವಾ, ಅಲ್ಲಾಹು ಅವನ ಅನ್ನಾಧಾರವನ್ನು ನಿಲ್ಲಿಸಿದರೆ ನಿಮಗೆ ಅನ್ನಾಧಾರ ನೀಡುವವನು ಯಾರಿರು ವನು? ಆದರೂ ಅವರು ಧಿಕ್ಕಾರದಲ್ಲಿ ಮತ್ತು ದ್ವೇಷದಲ್ಲಿ ಮಗ್ನರಾಗಿರುವರು.' WHERE docid = 5262; 
UPDATE verses_content SET c2text= 'ಹಾಗಾದರೆ ಸನ್ಮಾರ್ಗ ಪಡೆಯುವವನು ಮುಖವನ್ನು ನೆಲಕ್ಕೆ ಚುಚ್ಚಿ ನಡೆಯುವವನೋ?[[ದುರ್ಮಾರ್ಗದಲ್ಲಿ ಬುಡಮೇಲಾಗಿ ಬೀಳುವವನು.]] ಅಥವಾ ನೇರವಾದ ಮಾರ್ಗದಲ್ಲಿ ಸರಿಯಾಗಿ ನಡೆಯುವವನೋ?' WHERE docid = 5263; 
UPDATE verses_content SET c2text= 'ಹೇಳಿರಿ: ‘ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಮತ್ತು ನಿಮಗೆ ಶ್ರವಣವನ್ನು, ದೃಷ್ಟಿಗಳನ್ನು ಮತ್ತು ಹೃದಯಗಳನ್ನು ಮಾಡಿಕೊಟ್ಟವನು ಅವನಾಗಿರುವನು. ನೀವು ಸ್ವಲ್ಪ ಮಾತ್ರ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸುತ್ತಿರುವಿರಿ’.' WHERE docid = 5264; 
UPDATE verses_content SET c2text= 'ಹೇಳಿರಿ: ‘ನಿಮ್ಮನ್ನು ಭೂಮಿಯಲ್ಲಿ ಸೃಷ್ಟಿಸಿ ಬೆಳೆಸಿದವನು ಅವನಾಗಿರುವನು. ನಿಮ್ಮನ್ನು ಒಟ್ಟುಗೂಡಿಸ ಲಾಗುವುದು ಅವನೆಡೆಗೇ ಆಗಿದೆ’.' WHERE docid = 5265; 
UPDATE verses_content SET c2text= '' WHERE docid = 5266; 
UPDATE verses_content SET c2text= '(25-26) ಅವರು ಹೇಳುವರು: ‘ಈ ವಾಗ್ದಾನ (ಸತ್ಯವಾಗುವುದು) ಯಾವಾಗ? ನೀವು ಸತ್ಯಸಂಧರಾಗಿ ದ್ದರೆ (ಅದನ್ನು ಹೇಳಿಕೊಡಿರಿ)’. ಹೇಳಿರಿ: ‘ಆ ಅರಿವಿರುವುದು ಅಲ್ಲಾಹುವಿನ ಬಳಿ ಮಾತ್ರವಾಗಿದೆ. ನಾನು ಸ್ಪಷ್ಟವಾದ ಮುನ್ನೆಚ್ಚರಿಕೆಗಾರನು ಮಾತ್ರವಾಗಿರುವೆನು’.' WHERE docid = 5267; 
UPDATE verses_content SET c2text= 'ಅವರು ಅದನ್ನು (ಎಚ್ಚರಿಕೆ ನೀಡಲಾದ ವಿಷಯವನ್ನು) ಸಮೀಪದಲ್ಲಿರುವುದಾಗಿ ಕಾಣುವಾಗ ಸತ್ಯನಿಷೇಧಿಗಳ ಮುಖಗಳು ಕಳೆಗುಂದುವುದು. ‘ನೀವು ಯಾವುದರ ಬಗ್ಗೆ ವಾದಿಸುತ್ತಿದ್ದಿರೋ ಅದು ಇದೇ ಆಗಿದೆ’ ಎಂದು (ಅವರೊಂದಿಗೆ) ಹೇಳಲಾಗುವುದು.' WHERE docid = 5268; 
UPDATE verses_content SET c2text= 'ಹೇಳಿರಿ: ‘ನೀವು ಚಿಂತಿಸಿ ನೋಡಿರುವಿರಾ? ನನ್ನನ್ನು ಮತ್ತು ನನ್ನೊಂದಿಗಿರುವವರನ್ನು ಅಲ್ಲಾಹು ನಾಶ ಮಾಡುವುದಾದರೆ ಅಥವಾ ನಮಗೆ ಅವನು ಕರುಣೆ ತೋರಿಸುವುದಾದರೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆಯಿಂದ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಪಾರು ಮಾಡುವವರು ಯಾರಿರುವರು?’[[ಅಲ್ಲಾಹು ಇಹಲೋಕದಲ್ಲಿ ಶಿಕ್ಷಿಸುವುದಾದರೆ ಅದರಲ್ಲಿ ತಾವು ಮಾತ್ರವಲ್ಲ ಬದಲಾಗಿ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಮತ್ತು ಅವರ ಅನುಯಾಯಿಗಳು ಕೂಡ ಭಾಗಿಯಾಗುವರು ಎಂದು ಭಾವಿಸಿ ಸಮಾಧಾನ ಪಟ್ಟಿದ್ದ ಬಹುದೇವಾರಾಧಕರಿಗೆ ಇಲ್ಲಿ ಉತ್ತರ ನೀಡಲಾಗಿದೆ. ಅವಿಶ್ವಾಸಿಗಳ ವಿಧಿನಿಯತಿಯನ್ನು ನಿರ್ಣಯಿಸಲಾಗುವುದು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರನ್ನು ಮತ್ತು ಅವರ ಅನುಯಾಯಿಗಳನ್ನು ಶಿಕ್ಷಿಸಲಾಗುವುದೋ ಅಥವಾ ರಕ್ಷಿಸಲಾಗುವುದೋ ಎಂಬುದರ ಆಧಾರದಲ್ಲಲ್ಲ. ಅವಿಶ್ವಾಸ ಮತ್ತು ಅಧರ್ಮದ ದುಷ್ಫಲದಿಂದ ಹೇಗೆ ರಕ್ಷಣೆಯನ್ನು ಪಡೆಯಬಹುದು ಎಂಬುದನ್ನು ಅವರು ಆಲೋಚಿಸಬೇಕಾಗಿದೆ.]]' WHERE docid = 5269; 
UPDATE verses_content SET c2text= 'ಹೇಳಿರಿ: ‘ಅವನು ಪರಮ ದಯಾಮಯನಾಗಿ ರುವನು. ನಾವು ಅವನ ಮೇಲೆ ವಿಶ್ವಾಸವಿಟ್ಟಿರುವೆವು. ನಾವು ಅವನ ಮೇಲೆ ಭರವಸೆಯನ್ನಿಟ್ಟಿರುವೆವು. ಸ್ಪಷ್ಟವಾದ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲಿರುವವರು ಯಾರು ಎಂದು ತರುವಾಯ ನೀವು ಅರಿತುಕೊಳ್ಳುವಿರಿ’.' WHERE docid = 5270; 
UPDATE verses_content SET c2text= 'ಹೇಳಿರಿ: ‘ನೀವು ಚಿಂತಿಸಿ ನೋಡಿರುವಿರಾ? ನಿಮ್ಮ ನೀರು ಬತ್ತಿ ಹೋದರೆ ಹರಿಯುವ ಒರತೆ ನೀರನ್ನು ನಿಮಗೆ ತಂದು ಕೊಡುವವರು ಯಾರಿರುವರು?’' WHERE docid = 5271; 
UPDATE verses_content SET c2text= 'ನೂನ್. ಲೇಖನಿ ಮತ್ತು ಅವರು ಬರೆಯುವುದರ ಮೇಲಾಣೆ.' WHERE docid = 5272; 
UPDATE verses_content SET c2text= 'ತಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹದಿಂದಾಗಿ ತಾವು ಒಬ್ಬ ಹುಚ್ಚನಲ್ಲ.' WHERE docid = 5273; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ತಮಗೆ ಕಡಿದುಹೋಗದ ಪ್ರತಿಫಲವಿದೆ.' WHERE docid = 5274; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ತಾವು ಸರ್ವಶ್ರೇಷ್ಠ ಸ್ವಭಾವ ದಲ್ಲಿರುವಿರಿ.' WHERE docid = 5275; 
UPDATE verses_content SET c2text= 'ಆದ್ದರಿಂದ ತರುವಾಯ ತಾವು ಕಾಣುವಿರಿ. ಅವರೂ ಕಾಣುವರು.' WHERE docid = 5276; 
UPDATE verses_content SET c2text= 'ನಿಮ್ಮ ಪೈಕಿ ಕ್ಷೋಭೆಯಲ್ಲಿ ಒಳಪಟ್ಟವನು ಯಾರೆಂದು.[[ಮುಹಮ್ಮದ್‍  (صلى الله عليه وسلم)ರವರನ್ನು ಮಾನಸಿಕವಾದ ಯಾವುದೇ ತೊಂದರೆಯಿರುವ ವ್ಯಕ್ತಿಯೆಂದು ದೂಷಿಸುವವರಿಗೆ ಇಲ್ಲಿ ಉತ್ತರ ನೀಡಲಾಗಿದೆ. ಇಪ್ಪತ್ತಮೂರು ವರ್ಷಗಳ ಕಾಲ ಸಂಪೂರ್ಣ ಮನಸ್ಸಾನಿಧ್ಯದೊಂದಿಗೆ ಸಂದೇಶ ಪ್ರಚಾರದ ದೌತ್ಯವನ್ನು ಯಶಸ್ವಿಯಾಗಿ ನಿರ್ವಹಿಸಿದ ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಅನ್ಯೂನವಾದ ಮತ್ತು ವೈಕಲ್ಯರಹಿತ ಮನಸ್ಸಿನ ಒಡೆಯರಾಗಿದ್ದರೆಂಬುದಕ್ಕೆ ಇತಿಹಾಸವು ಸಾಕ್ಷಿಯಾಗಿದೆ.]]' WHERE docid = 5277; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ತಮ್ಮ ಪ್ರಭು ಅವನ ಮಾರ್ಗದಿಂದ ಭ್ರಷ್ಟರಾದವರ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಅರಿತಿರುವನು. ಸನ್ಮಾರ್ಗ ಪಡೆದವರ ಬಗ್ಗೆಯೂ ಅವನು ಚೆನ್ನಾಗಿ ಅರಿತಿರುವನು.' WHERE docid = 5278; 
UPDATE verses_content SET c2text= 'ಆದ್ದರಿಂದ ತಾವು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಅನುಸರಿಸದಿರಿ.' WHERE docid = 5279; 
UPDATE verses_content SET c2text= 'ತಾವು ಮಣಿಯುವುದಾದರೆ ಅವರೂ ಮಣಿಯು ತ್ತಿದ್ದರು ಎಂದು ಅವರು ಆಶಿಸುವರು.[[ಇಸ್ಲಾಮಿನ ವಿಶ್ವಾಸಾಚಾರಗಳನ್ನು ಕಟ್ಟುನಿಟ್ಟಾಗಿ ಪ್ರಚಾರ ಮಾಡುವ ಬದಲು ಜಾಹಿಲಿಯ್ಯತ್ತಿನ ಕೆಲವು ಆಚಾರಗಳೊಂದಿಗೆ ಹೊಂದಾಣಿಕೆ ಮಾಡಲು ಪ್ರವಾದಿ(صلى الله عليه وسلم)ಯವರು ಸನ್ನದ್ಧರಾದರೆ ಅವರೊಂದಿಗೆ ಹೊಂದಾಣಿಕೆ ಮಾಡಲು ಅರಬ್ ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳು ಸಿದ್ಧರಿದ್ದರು.]]' WHERE docid = 5280; 
