UPDATE verses_content SET c2text= '(ಆಗ ಹೀಗೆ ಆಜ್ಞಾಪಿಸಲಾಗುವುದು): ‘ಅವನನ್ನು ಹಿಡಿಯಿರಿ ಮತ್ತು ಬಂಧಿಸಿರಿ.' WHERE docid = 5353; 
