UPDATE verses_content SET c2text= 'ನಾನು ಈ ಪಟ್ಟಣದ (ಮಕ್ಕಾದ) ಮೇಲೆ ಆಣೆಯಿಡುವೆನು.' WHERE docid = 6024; 
UPDATE verses_content SET c2text= 'ತಾವು ಈ ಪಟ್ಟಣದ ನಿವಾಸಿಯಾಗಿರುವಿರಿ. [[‘ಹಿಲ್ಲ್’ಎಂದರೆ ನಿವಾಸಿ, ಅನುಮತಿಯಿರುವವನು, ಸ್ವಾತಂತ್ರ್ಯವಿರುವವನು ಇತ್ಯಾದಿ ಅರ್ಥಗಳಿವೆ.]]' WHERE docid = 6025; 
UPDATE verses_content SET c2text= 'ಜನ್ಮದಾತನ ಮತ್ತು ಅವನು ಜನ್ಮ ನೀಡಿದವುಗಳ ಮೇಲಾಣೆ![[ಒಬ್ಬ ವ್ಯಕ್ತಿ ಜನ್ಮದಾತ ಮತ್ತು ಇನ್ನೊಬ್ಬ ವ್ಯಕ್ತಿ ಜನಿಸಿದವನಾಗುವುದು -ತಂದೆ, ತಾಯಿ ಮತ್ತು ಸಂತತಿಯಾಗುವುದು-ಜನನದೊಂದಿಗೆ ಸಂಬಂಧ ಹೊಂದಿರುವ ಅತಿಸೂಕ್ಷ್ಮ ದೈವಿಕ ನಿಯಮದ ಪ್ರಕಾರವಾಗಿದೆ.]]' WHERE docid = 6026; 
UPDATE verses_content SET c2text= 'ಖಂಡಿತವಾಗಿಯೂ ಮನುಷ್ಯನನ್ನು ನಾವು ಸೃಷ್ಟಿಸಿ ರುವುದು ಕಷ್ಟವನ್ನು ಅನುಭವಿಸುವ ಸ್ಥಿತಿಯಲ್ಲಾಗಿದೆ.[[ಬದುಕಿನ ಎಲ್ಲ ಸಾಧನೆಗಳಿಗೂ ಮಾನದಂಡ ಕಠಿಣ ಪರಿಶ್ರಮವಾಗಿದೆ. ಪರಿಶ್ರಮಿಸಲು ಅಥವಾ ಕ್ಲೇಶ ಸಹಿಸಲು ಸ್ವಲ್ಪವೂ ಸಿದ್ಧನಾಗದವನು ಬದುಕಿನಲ್ಲಿ ಯಶಸ್ವಿಯಾಗಲಾರನು.]]' WHERE docid = 6027; 
UPDATE verses_content SET c2text= 'ಅವನನ್ನು ನಿಯಂತ್ರಿಸಲು ಯಾರಿಗೂ ಸಾಧ್ಯವೇ ಇಲ್ಲವೆಂದು ಅವನು ಭಾವಿಸುತ್ತಿರುವನೇ?' WHERE docid = 6028; 
UPDATE verses_content SET c2text= 'ಅವನು ಹೇಳುವನು: ‘ನಾನು ಸಂಪತ್ತನ್ನು ಯಥೇಷ್ಟವಾಗಿ ಹಾಳು ಮಾಡಿರುವೆನು’.[[ದೇವಭಯವಿಲ್ಲದ ಧನಿಕರು ಧರ್ಮಬಾಹಿರವಾಗಿ ಸಂಪತ್ತನ್ನು ವ್ಯಯಿಸುವವರೂ, ಅದರ ಹೆಸರಲ್ಲಿ ಜಂಬಕೊಚ್ಚುವವರೂ ಆಗಿದ್ದಾರೆ ಮತ್ತು ತಮ್ಮ ಕರ್ಮಗಳನ್ನು ಅಲ್ಲಾಹು ವೀಕ್ಷಿಸುತ್ತಿದ್ದಾನೆಂಬುದರ ಬಗ್ಗೆ ಪ್ರಜ್ಞೆಯಿಲ್ಲದವರಾಗಿದ್ದಾರೆ.]]' WHERE docid = 6029; 
