UPDATE verses_content SET c2text= '(1-2) (ಓ ಪ್ರವಾದಿಯವರೇ!) ಹೇಳಿರಿ: ‘ಓ ಅವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಆರಾಧಿಸುವುದನ್ನು ನಾನು ಆರಾಧಿಸುವುದಿಲ್ಲ.' WHERE docid = 6209; 
